(198910, 'itIT', 'Calcafauce? Sei davvero $gcurioso:curiosa;.', 'Calcafauce? Sei davvero $gcurioso:curiosa;.', 38134),
(198908, 'itIT', '<Ti guarda dall\'alto in basso senza proferire una sola parola di riconoscimento.>', '<Ti guarda dall\'alto in basso senza proferire una sola parola di riconoscimento.>', 38134),
(198907, 'itIT', 'Ci andrei piano, Calcafauce: la Contessa conosce i tuoi desideri pi profondi ed  sempre pronta alla sua prossima raccolta.', 'Ci andrei piano, Calcafauce: la Contessa conosce i tuoi desideri pi profondi ed  sempre pronta alla sua prossima raccolta.', 38134),
(198904, 'itIT', 'Curioso incontrarti qui $PR, la Contessa deve sentirsi caritatevole.', 'Curioso incontrarti qui $PR, la Contessa deve sentirsi caritatevole.', 38134),
(198903, 'itIT', '$C, ora non  il momento di infastidirmi con i tuoi futili alterchi.', '$C, ora non  il momento di infastidirmi con i tuoi futili alterchi.', 38134),
(198902, 'itIT', ' vero quello che dicono sui Calcafauce?', ' vero quello che dicono sui Calcafauce?', 38134),
