(192281, 'itIT', '', 'Pensa in fretta!', 38134),
(192280, 'itIT', '', 'Ehi, prendi!', 38134),
(192279, 'itIT', '', 'Scommetto che questo non riesci a schivarlo!', 38134),
(192278, 'itIT', 'La nostra via  lo Scopo.', 'La nostra via  lo Scopo.', 38134),
(192277, 'itIT', 'S. Sono delle creature frustranti, ma dobbiamo confidare che servano lo Scopo... a modo loro.', 'S. Sono delle creature frustranti, ma dobbiamo confidare che servano lo Scopo... a modo loro.', 38134),
(192276, 'itIT', '', 'Eccomi! Eheh!', 38134),
(192275, 'itIT', 'Ti  stato ordinato di osservare gli Alienatori? Posso soltanto immaginare...', 'Ti  stato ordinato di osservare gli Alienatori? Posso soltanto immaginare...', 38134),
(192274, 'itIT', 'Non posso restare, ma volevo salutarti. C\' un sacco di lavoro da sbrigare.', 'Non posso restare, ma volevo salutarti. C\' un sacco di lavoro da sbrigare.', 38134),
(192272, 'itIT', 'Salve.', 'Salve.', 38134),
(192271, 'itIT', 'Salve.', 'Salve.', 38134),
(192270, 'itIT', '', 'Ehi, ci sai davvero fare!', 38134),
(192269, 'itIT', '', 'Mi hai trovata! Ottimo lavoro!', 38134),
(192268, 'itIT', '', 'Giochiamo agli indovinelli!', 38134),
(192267, 'itIT', '', 'Qual  la vera me?', 38134),
(192266, 'itIT', '', 'Una di noi non  come le altre!', 38134),
(192265, 'itIT', '', 'Grazie per aver giocato con me! Torna ancora!', 38134),
(192264, 'itIT', '', 'Fine del divertimento?', 38134),
(192263, 'itIT', '', 'Non vuoi continuare a giocare?', 38134),
