INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(192062, 'itIT', '', 'L\'Animum...  incontenibile!', 38134),
(192061, 'itIT', '', 'Tremate dinanzi al mio potere!', 38134),
(192060, 'itIT', '', 'Chiunque ci ostacoli verr annientato!', 38134),
(192059, 'itIT', '', 'Dovete essere annientati!', 38134),
(192058, 'itIT', '', 'Avete deluso i Kyrian, cos come loro hanno deluso tutti noi.', 38134),
(192056, 'itIT', '', 'Il Primus  stato campione di molte battaglie campali. Onoralo sconfiggendo i nemici in questo luogo illustre.', 38134),
(192052, 'itIT', '', 'Nessuno pu opporsi all\'Eletta.', 38134),
(192051, 'itIT', '', 'Sei immeritevole della nostra causa!', 38134),
(192050, 'itIT', '', 'La vostra ribellione dev\'essere punita!', 38134),
(192038, 'itIT', '', 'Non fidatevi... del Barone', 38134),
(192037, 'itIT', '', 'La pestilenza vi inghiottir!', 38134),
(192036, 'itIT', '', 'Si diffonde! Non si pu contenere!', 38134),
(192034, 'itIT', '', 'Percepite la sua rabbia? La mia rabbia?!?', 38134),
(192028, 'itIT', '', 'Espierai i crimini della tua gente.', 38134),
(192027, 'itIT', '', 'La mia casata trabocca di morbo!', 38134),
(192023, 'itIT', '', 'Non potete sfuggire al destino!', 38134),
(192021, 'itIT', '', 'Tutto verr distrutto!', 38134),
(192015, 'itIT', '', 'Quanta forza. Un\'aggiunta incredibilmente potente alla piaga.', 38134),
(192008, 'itIT', '', 'Ora proverai la mia sofferenza.', 38134),
(192007, 'itIT', '', 'Cadi nell\'abbraccio mortale della malattia.', 38134),
(192006, 'itIT', '', 'Ammirate il mio ultimo lavoro! Il mio orgoglio e la mia rovina!', 38134),
(192005, 'itIT', '', 'Se desiderate tanto questo potere, allora venite a prenderlo... o sar lui a prendere voi.', 38134),
(192004, 'itIT', '', 'Chi osa entrare nella Casata delle Piaghe? Chi cerca ci che non pu essere controllato?', 38134),
(192003, 'itIT', '', 'La vendetta... sar... mia...', 38134),
(192002, 'itIT', '', 'Presto tutta Maldraxxus si unir a voi.', 38134),
(192001, 'itIT', '', 'Non te lo aspettavi, mh?', 38134),
(192000, 'itIT', '', ' questo il meglio che gli Occhi sanno fare?', 38134),
(191999, 'itIT', '', 'Non mi fermerete!', 38134),
(191980, 'itIT', '', 'Corregger il suo errore. Tutti soffriranno quanto ha sofferto la Casata degli Occhi!', 38134),
(191979, 'itIT', '', 'Se Akarek non fosse stato cos stolto e si fosse impadronito della piaga, la Casata degli Occhi sarebbe ancora in piedi!', 38134),
(191978, 'itIT', '', 'Questo  il vero potere della piaga. Essa persiste e continua a marcire, annientando tutto ci che incontra... persino i vincitori.', 38134),
(191977, 'itIT', '', 'Quindi emergete vittoriosi... ma a che prezzo? Quali vili composti scorrono ora nelle vostre vene?', 38134),
(191975, 'itIT', '', 'Quello  stato un semplice assaggio di ci che pu fare la piaga. Una volta che la controller, tutta Maldraxxus la pagher.', 38134),
(191970, 'itIT', 'Dobbiamo radunare i Mietitori a Oblio del Peccato mentre fai ritorno a Oribos, mortale. C\' bisogno che si sappia cos\' successo qui.', '', 38134),
(191969, 'itIT', '', 'Denathrius raduner sicuramente le sue forze al Castello di Nathria. Se lasciati agire, i suoi seguaci ci travolgeranno presto.', 38134),
(191968, 'itIT', '', 'La Fauce cresce, ricolma d\'Animum. Temo che nulla potr fermare la fuga del Carceriere.', 38134),
(191967, 'itIT', '', 'Sapevo che non ci avresti deluso.', 38134),
(191965, 'itIT', '', '<Il testo  inciso alla base della statua.>\n\nIl destino  determinato da coloro che se ne impadroniscono. I supplicanti devono Inginocchiarsi in segno di omaggio.', 38134),
(191964, 'itIT', '', '<Il testo  inciso alla base della statua.>\n\nGrazie alla virulenza sconfiggiamo i nostri nemici. I meritevoli devono Sanguinare per Maldraxxus.', 38134),
(191963, 'itIT', '<Il testo  inciso alla base della statua.>\n\nLa debolezza dev\'essere soppiantata dalla forza. Fletti i muscoli per darne dimostrazione.', '<Il testo  inciso alla base della statua.>\n\nLa debolezza dev\'essere soppiantata dalla forza. Fletti i muscoli per darne dimostrazione.', 38134),
(191961, 'itIT', '', 'Questa era un\'antica forgia, risalente a prima che Maldraxxus fosse conquistata. Non  stata usata per anni, ma ora sar molto utile, dato che Vyraz ci impedisce l\'accesso alla forgia di Heirmir.', 38134),
(191947, 'itIT', '', 'Ehm, andiamo avanti...', 38134),
(191946, 'itIT', '', 'Sono stata sconfitta. Evviva.', 38134),
(191945, 'itIT', '', 'Ugh... D\'accordo.', 38134),
(191944, 'itIT', '', 'Davvero. E nemmeno Jaina mor.', 38134),
(191943, 'itIT', '', 'Oh, ma dai! Davvero?!?', 38134),
(191942, 'itIT', '', 'Pulvilume, Jaina non uccise i suoi inseguitori.', 38134),
(191941, 'itIT', '', 'Pulvilume, Jaina non uccise il campione.', 38134),
(191940, 'itIT', '', 'Le navi dell\'Orda inseguirono la flotta dell\'Alleanza, ma vennero fermate dall\'Ammiraglio Supremo Jaina Marefiero!', 38134),
(191939, 'itIT', '', 'Cump! Vascelli dell\'Orda all\'orizzonte! Mo\' nun tieni via di scampo, strega del gelo!', 38134),
(191936, 'itIT', '', 'Per porre fine velocemente alla guerra, l\'Alleanza invase Dazar\'alor e uccise il Re Rastakhan!', 38134),
(191935, 'itIT', '', 'Muahahahahah!', 38134),
(191934, 'itIT', '', 'Vi ho attirato in mare aperto perch qui non avete alcuna possibilit di fuga!', 38134),
(191933, 'itIT', '', 'Credi davvero che stessi scappando, Capitano?', 38134),
(191932, 'itIT', '', 'Che la notte ti accolga, Calcafauce. La tua caccia  aperta.', 38134),
(191931, 'itIT', 'Stolti!  stato fin troppo facile seminare caos e discordia tra le vostre litigiose casate.', '', 38134),
(191916, 'itIT', 'Ah, i cadaveri erranti sono tornati.  ora di raccogliere i vostri pezzi!', '', 38134),
(191911, 'itIT', 'I vostri sforzi sono inutili. Non potete fermare il mio rituale!', '', 38134),
(191910, 'itIT', 'Presto, su per la collina!', '', 38134),
(191903, 'itIT', 'Prestate servizio, sapendo che i vostri risultati hanno contribuito a ripristinare la sacralit del nostro ordine.', '', 38134),
(191901, 'itIT', 'Il suo potere ha portato molte anime al Bastione e ora conceder le ali ai nostri aspiranti.', '', 38134),
(191900, 'itIT', 'Il Sigillo del Pentimento rappresenta molte cose: contrizione, rimorso, pulizia di noi stessi dagli errori passati per lasciarceli alle spalle.', '', 38134),
(191893, 'itIT', '', 'Posso portarti dalla Regina.', 38134),
