INSERT INTO `lfg_dungeons_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2348, 'deDE', 'Der Große Entwurf', 'Der Kerkermeister steht kurz vor dem endgültigen Sieg. Wenn er nicht aufgehalten wird, erliegt der gesamte Kosmos seiner Macht.', 45745),
(2347, 'deDE', 'Griff der Herrschaft', 'Zovaals mächtigste Anhänger haben Millionen von Zivilisationen zerstört. Werden sie jetzt das Universum auslöschen?', 45745),
(2346, 'deDE', 'Ephemere Ebenen', 'Die Essenz der Schöpfung wird verschlungen. Wenn dem nicht Einhalt geboten wird, wird die Realität sich auflösen.', 45745),
(2345, 'deDE', 'Grundstein der Schöpfung', 'Lihuvim kämpft vergeblich gegen die Herrschaft an. Wenn er nicht gerettet wird, fällt die Macht des Hüters dem Kerkermeister anheim.', 45745),
(2294, 'deDE', 'Der Große Entwurf', 'Der Kerkermeister steht kurz vor dem endgültigen Sieg. Wenn er nicht aufgehalten wird, erliegt der gesamte Kosmos seiner Macht.', 45745),
(2293, 'deDE', 'Griff der Herrschaft', 'Zovaals mächtigste Anhänger haben Millionen von Zivilisationen zerstört. Werden sie jetzt das Universum auslöschen?', 45745),
(2344, 'deDE', 'Die Abrechnung', 'Auf der Turmspitze erwartet Euch Sylvanas Windläufer. Dort wird der entscheidende Kampf um das Schicksal des Kosmos ausgetragen werden.', 45745),
(2343, 'deDE', 'Fesseln des Schicksals', 'Der Seelenfluss endet an der Spitze des Turms. Vernichtet jene, die von ihm zehren, um die unzähligen Seelen zu befreien, denen das Leben nach dem Tode verwehrt wird.', 45745),
(2342, 'deDE', 'Die Dunkle Bastille', 'Die Schreie der Gequälten treiben die Armee des Kerkermeisters an. Bereitet ihrem Leiden ein Ende und beraubt den Schlund seiner wertvollsten Ressource.', 45745),
(2341, 'deDE', 'Frontkämpfer des Kerkermeisters', 'Die Frontkämpfer des Kerkermeisters stehen bereit, um jeden Angriff abzuwehren. Um in das Sanktum des Kerkermeisters zu gelangen, müssen ihre Linien durchbrochen werden.', 45745),
(2340, 'deDE', 'Eine Audienz mit der Arroganz', 'Denathrius erwartet die Helden ungeduldig in seinem Turm, um in einem letzten Aufeinandertreffen endgültig über das Schicksal von Revendreth zu entscheiden.', 45745),
(2339, 'deDE', 'Blut aus Stein', 'Denathrius\' Generäle der Steinlegion bereiten im Hinterhalt lauernd eine Falle vor, die die Rebellion ein für alle Mal vernichten wird.', 45745),
(2338, 'deDE', 'Reliquiarium der Opulenz', 'Während die Aristokratie im Siegestaumel schwelgt, müssen Helden ihrer Feier ein Ende setzen und ihre Machtbasis vernichten.', 45745),
(2337, 'deDE', 'Die hungrigen Gewölbe', 'Anima fließt aus den Gewölben von Schloss Nathria wie Blut aus einer offenen Wunde. Dieser Aderlass muss gestoppt werden, bevor Revendreth noch völlig ausblutet.', 45745);
INSERT INTO `mount_locale` (`ID`, `locale`, `Name_lang`, `SourceText_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(1679, 'deDE', 'Frostbrutprotowyrm', '|cFFFFD200Promotion:|r Wrath of the Lich King Classic|n', 'Dieselbe Magie des Untodes, die die Frostbrut zu Dienern des Lichkönigs werden ließ, hat diesen urtümlichen Wyrm erfüllt. Seine Loyalität und Kraft stehen jetzt im Dienste derer, die Azeroth verteidigen.', 45745);
