INSERT INTO `lfg_dungeons_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2348, 'frFR', 'Le Grand dessein', 'Le Geôlier est à deux doigts de la victoire absolue. Si personne n\’intervient, le cosmos tout entier succombera à son pouvoir.', 45745),
(2347, 'frFR', 'Emprise de la domination', 'Les plus terribles fidèles de Zovaal ont déjà détruit des millions de civilisations. Vont-ils réussir à annihiler le cosmos ?', 45745),
(2346, 'frFR', 'Plaines Éphémères', 'L\’essence de la création se fait dévorer lentement mais sûrement. Si personne n\’intervient, c\’est la réalité entière qui finira par s\’effondrer.', 45745),
(2345, 'frFR', 'Assise de la Création', 'Lihuvim mène un combat perdu d\’avance contre la domination. À moins qu\’on ne vole à son secours, son pouvoir tombera aux mains du Geôlier.', 45745),
(2294, 'frFR', 'Le Grand Dessein', 'Le Geôlier est à deux doigts de la victoire absolue. Si personne n\’intervient, le cosmos tout entier succombera à son pouvoir.', 45745),
(2293, 'frFR', 'Emprise de la domination', 'Les plus terribles fidèles de Zovaal ont déjà détruit des millions de civilisations. Vont-ils réussir à annihiler le cosmos ?', 45745),
(2344, 'frFR', 'Le châtiment', 'Sylvanas Coursevent attend au sommet de la tour. C\’est là que va se jouer l\’ultime confrontation qui décidera du sort du cosmos.', 45745),
(2343, 'frFR', 'Les entraves du destin', 'La rivière des âmes achève sa course au sommet de la tour. Anéantissez ceux qui s\’en nourrissent afin de libérer les innombrables esprits privés de leur dernier repos.', 45745),
(2342, 'frFR', 'La bastille Noire', 'Les hurlements des tourmentés alimentent l\’armée du Geôlier. Abrégez leurs souffrances et privez l\’Antre de sa plus précieuse ressource.', 45745),
(2341, 'frFR', 'L\’avant-garde du Geôlier', 'L\’avant-garde du Geôlier se tient prête à repousser toute attaque. Il faut percer les lignes ennemies pour pénétrer dans le sanctum du Geôlier.', 45745),
(2340, 'frFR', 'Audience avec l\’arrogance', 'À l\’abri dans sa tour, Denathrius attend avec impatience l\’ultime confrontation qui scellera le sort de Revendreth.', 45745),
(2339, 'frFR', 'Le sang de la pierre', 'À l\’affût, les généraux de la Légion de pierre de Denathrius préparent un piège qui écrasera définitivement la rébellion.', 45745),
(2338, 'frFR', 'Reliquaire de l\’opulence', 'Alors que les aristocrates célèbrent leur victoire, les héros et héroïnes doivent débarquer dans leur réception et démanteler leur base de pouvoir.', 45745),
(2337, 'frFR', 'Caveaux drainants', 'L\’anima s\’écoule des caveaux du château Nathria comme d\’une plaie béante. Il faut stopper l\’hémorragie avant que Revendreth ne soit complètement exsangue.', 45745);
INSERT INTO `mount_locale` (`ID`, `locale`, `Name_lang`, `SourceText_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(1679, 'frFR', 'Proto-wyrm couvegivre', '|cFFFFD200Promotion :|r Wrath of the Lich King Classic|n', 'Ce wyrm primordial est imprégné de la même magie de non-mort qui avait ranimé les couvegivres pour le roi-liche. C\’est avec une loyauté et une férocité sans faille qu\’il sert désormais celles et ceux qui défendent Azeroth.', 45745);
