INSERT INTO `lfg_dungeons_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2348, 'koKR', '위대한 설계', '간수가 궁극적인 승리를 목전에 두고 있습니다. 그를 막지 못하면 온 우주가 조바알에게 복종하게 될 것입니다.', 45745),
(2347, 'koKR', '지배의 손아귀', '조바알의 강대한 추종자들은 수백만 개의 문명을 파괴해왔습니다. 결국 우주마저 그들의 손에 해체되는 운명을 맞이하고 말까요?', 45745),
(2346, 'koKR', '찰나의 터', '창조의 정수가 포식당하고 있습니다. 견제하지 않으면 현실이 붕괴할 것입니다.', 45745),
(2345, 'koKR', '창조의 주춧돌', '리후빔이 지배에 맞서 싸우고 있지만, 패색이 너무나 짙습니다. 그를 돕지 않으면 수호자의 힘은 간수의 것이 되어버릴 것입니다.', 45745),
(2294, 'koKR', '위대한 설계', '간수가 궁극적인 승리를 목전에 두고 있습니다. 그를 막지 못하면 온 우주가 조바알에게 복종하게 될 것입니다.', 45745),
(2293, 'koKR', '지배의 손아귀', '조바알의 강대한 추종자들은 수백만 개의 문명을 파괴해왔습니다. 결국 우주마저 그들의 손에 해체되는 운명을 맞이하고 말까요?', 45745),
(2344, 'koKR', '징벌', '실바나스 윈드러너가 탑의 꼭대기에서 기다립니다. 바로 이곳에서 우주의 명운을 건 마지막 대결이 펼쳐질 것입니다.', 45745),
(2343, 'koKR', '운명의 족쇄', '영혼의 강의 그 끝에는 탑 정상이 있습니다. 강의 힘을 갈취하는 자들을 처단하여 사후의 삶을 빼앗긴 무수히 많은 영혼을 해방해야 합니다.', 45745),
(2342, 'koKR', '암흑의 요새', '고통받는 자들의 비명은 곧 간수의 군대의 원동력과 같습니다. 이들의 괴로움을 끝내주고 나락의 가장 귀중한 자원을 없애버려야 합니다.', 45745),
(2341, 'koKR', '간수의 선봉대', '간수의 선봉대는 그 어떤 공격이든 격퇴할 준비가 되어 있습니다. 그의 성소에 돌입하려면 이들의 전선을 무너뜨려야 합니다.', 45745),
(2340, 'koKR', '교만과의 대면', '데나트리우스가 탑에서 영웅들이 도착하기만을 기다리고 있습니다. 레벤드레스의 운명을 놓고 결착을 지을 순간이 다가왔습니다.', 45745),
(2339, 'koKR', '돌의 선혈', '데나트리우스의 돌 군단 장군이 차분히 기다리며, 반란군을 완전히 궤멸할 덫을 준비하고 있습니다.', 45745),
(2338, 'koKR', '부유의 성물', '귀족들이 승리에 심취해 연회를 즐기는 지금, 영웅들이 난입해 그들을 권력에서 끌어내려야 합니다.', 45745),
(2337, 'koKR', '착취의 금고', '아물지 않은 상처에서 피가 흐르듯, 나스리아 성채의 금고에서 령이 새어 나오고 있습니다. 레벤드레스가 쇠진하기 전에 누출을 막아야 합니다.', 45745);
