INSERT INTO `lfg_dungeons_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2348, 'ptBR', 'O Plano Magistral', 'O Carcereiro está prestes a conquistar a vitória final. A não ser que seja impedido, o cosmos sucumbirá à sua força.', 45745),
(2347, 'ptBR', 'Jugo da Dominação', 'Os maiores seguidores de Zovaal destruíram milhões de civilizações. Agora eles nulificarão o universo?', 45745),
(2346, 'ptBR', 'Planícies Efêmeras', 'A essência da criação está sendo devorada. Sem controle, a realidade entrará em colapso.', 45745),
(2345, 'ptBR', 'Pedra angular da criação', 'Lihuvim luta uma batalha perdida contra a dominação. A menos que seja salvo, o poder do guardião se tornará o do Carcereiro.', 45745),
(2294, 'ptBR', 'O Plano Magistral', 'O Carcereiro está prestes a conquistar a vitória final. A não ser que seja impedido, o cosmos sucumbirá à sua força.', 45745),
(2293, 'ptBR', 'Jugo da Dominação', 'Os maiores seguidores de Zovaal destruíram milhões de civilizações. Agora eles nulificarão o universo?', 45745),
(2344, 'ptBR', 'O Juízo', 'No topo da Torre, Sylvana Correventos aguarda. É aqui que terá lugar o confronto final pelo destino do cosmos.', 45745),
(2343, 'ptBR', 'Grilhões do Destino', 'O rio das almas deságua no pico da torre. Destrua aqueles que se alimentam dele para libertar as incontáveis almas privadas de um pós-vida.', 45745),
(2342, 'ptBR', 'A Bastilha Sombria', 'Os gritos dos atormentados alimentam o exército do Carcereiro. Dê fim ao sofrimento deles e impeça a Gorja de ter acesso ao seu mais precioso recurso.', 45745),
(2341, 'ptBR', 'A Vanguarda do Carcereiro', 'A vanguarda do Carcereiro está pronta para rechaçar qualquer ataque. As fileiras devem ser suplantadas para que seja possível chegar ao santuário do Carcereiro.', 45745),
(2340, 'ptBR', 'Audiência com Arrogância', 'Denathrius aguarda entusiasmado em sua torre pelo confronto final para decidir o destino de Revendreth.', 45745),
(2339, 'ptBR', 'Sangue da Pedra', 'Os Generais da Legião de Pedra de Denathrius estão à espera, preparando uma armadilha que destruirá a rebelião de uma vez por todas.', 45745),
(2338, 'ptBR', 'Relicário da Opulência', 'Enquanto os aristocratas se regozijam com a vitória, os heróis devem entrar de penetra na festa e arruinar a base do poder deles.', 45745),
(2337, 'ptBR', 'As Câmaras Sorvedouras', 'Como uma ferida aberta, a ânima sangra das câmaras do Castelo de Nathria. A hemorragia deve ser contida antes que Revendreth seja exaurido.', 45745);
