INSERT INTO `lfg_dungeons_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2348, 'ruRU', 'Великий замысел', 'Тюремщик в шаге от окончательной победы. Если его не остановить, вселенная окажется в его власти.', 45745),
(2347, 'ruRU', 'Предел Господства', 'Могучие последователи Зоваала истребили миллионы цивилизаций. Удастся ли им уничтожить само мироздание?', 45745),
(2346, 'ruRU', 'Эфемеровые равнины', 'Враги пожирают саму сущность созидания. Если им не помешать, реальность будет уничтожена.', 45745),
(2345, 'ruRU', 'Фундамент Созидания', 'Лихувим противится магии Господства из последних сил. Если не вмешаться, Тюремщик завладеет даром хранителя.', 45745),
(2294, 'ruRU', 'Великий замысел', 'Тюремщик в шаге от окончательной победы. Если его не остановить, вселенная окажется в его власти.', 45745),
(2293, 'ruRU', 'Предел Господства', 'Могучие последователи Зоваала истребили миллионы цивилизаций. Удастся ли им уничтожить само мироздание?', 45745),
(2344, 'ruRU', 'Расплата', 'На вершине башни вас ждет Сильвана Ветрокрылая. Здесь произойдет решающий бой за судьбу мироздания.', 45745),
(2343, 'ruRU', 'Оковы судьбы', 'Река душ обрывается у самой вершины башни. Уничтожьте тех, кто получает из нее силу, и освободите бесчисленные души, лишенные посмертия.', 45745),
(2342, 'ruRU', 'Темный острог', 'Крики истязаемых жертв – источник энергии для армии Тюремщика. Прекратите их страдания и лишите Утробу самого ценного ресурса.', 45745),
(2341, 'ruRU', 'Авангард Тюремщика', 'Авангард Тюремщика готов отразить любое нападение. Мы должны прорвать оборону противника, чтобы проникнуть в святая святых Зоваала.', 45745),
(2340, 'ruRU', 'Встреча с тщеславием', 'Денатрий ждет героев в своей башне, чтобы в последнем сражении решить судьбу Ревендрета.', 45745),
(2339, 'ruRU', 'Кровь из камня', 'Генералы Каменного легиона Денатрия ждут в засаде, готовые уничтожить мятежников и покончить с восстанием Ренатала.', 45745),
(2338, 'ruRU', 'Реликварий роскоши', 'Аристократы вовсю празднуют победу – это идеальный случай, чтобы остановить потеху и покончить с их властью.', 45745),
(2337, 'ruRU', 'Истощающие хранилища', 'Анима течет из хранилищ замка Нафрия, как кровь из открытой раны. Остановите это, пока Ревендрет не погиб.', 45745);
