INSERT INTO `lfg_dungeons_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2348, 'itIT', 'Il Grande Progetto', 'Il Carceriere si trova sull\'orlo della vittoria definitiva. A meno che non venga fermato, l\'intero cosmo soccomberà al suo potere.', 45745),
(2347, 'itIT', 'Stretta del Dominio', 'I più grandi seguaci di Zovaal hanno distrutto milioni di civiltà. Ora distruggeranno anche l\'universo?', 45745),
(2346, 'itIT', 'Pianure Effimere', 'L\'essenza della creazione continua a essere divorata. Se nessuno interverrà, la realtà finirà per collassare.', 45745),
(2345, 'itIT', 'Cardine della Creazione', 'Lihuvim combatte una battaglia persa contro il dominio. A meno che non venga salvato, il potere del custode apparterrà al Carceriere.', 45745),
(2294, 'itIT', 'Grande Progetto', 'Il Carceriere si trova sull\'orlo della vittoria definitiva. A meno che non venga fermato, l\'intero cosmo soccomberà al suo potere.', 45745),
(2293, 'itIT', 'Stretta del Dominio', 'I più grandi seguaci di Zovaal hanno distrutto milioni di civiltà. Ora distruggeranno anche l\'universo?', 45745),
(2344, 'itIT', 'Resa dei Conti', 'In cima alla torre, Sylvanas Ventolesto è in attesa. Qui avrà luogo lo scontro finale per il destino del cosmo.', 45745),
(2343, 'itIT', 'Catene del Destino', 'Il fiume di anime termina in cima alla torre. Distruggi coloro che se ne stanno nutrendo per liberare le innumerevoli anime private dell\'aldilà.', 45745),
(2342, 'itIT', 'Bastiglia Oscura', 'Le urla dei tormentati alimentano l\'esercito del Carceriere. Poni fine alle loro sofferenze e priva la Fauce della sua risorsa più preziosa.', 45745),
(2341, 'itIT', 'L\'avanguardia del Carceriere', 'L\'avanguardia del Carceriere è pronta a respingere qualsiasi attacco. Dobbiamo spezzare le loro linee per far breccia nel suo Sanctum.', 45745),
(2340, 'itIT', 'Un incontro con l\'arroganza', 'Denathrius attende con ansia l\'arrivo degli eroi nella sua torre per il confronto finale che deciderà il destino di Revendreth.', 45745),
(2339, 'itIT', 'Sangue dalla pietra', 'I Generali della Legione di Pietra di Denathrius attendono, mentre preparano una trappola che distruggerà la ribellione una volta per tutte.', 45745),
(2338, 'itIT', 'Reliquiario dell\'Opulenza', 'Con l\'aristocrazia che si gode la vittoria, gli eroi devono rovinarne la festa e distruggere la sua potenza di base.', 45745),
(2337, 'itIT', 'Le cripte prosciuganti', 'L\'Animum trabocca dai depositi del Castello di Nathria come una ferita aperta. L\'emorragia dev\'essere fermata prima che tutta Revendreth venga prosciugata.', 45745);
INSERT INTO `mount_locale` (`ID`, `locale`, `Name_lang`, `SourceText_lang`, `Description_lang`, `VerifiedBuild`) VALUES
