INSERT INTO `lfg_dungeons_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2348, 'zhTW', '宏偉設計', '閻獄之主即將獲得最終勝利。你必須阻止他，否則整個宇宙都會屈服於他的力量之下。', 45745),
(2347, 'zhTW', '統御之握', '佐瓦爾最厲害的部下已經摧毀了數百萬個文明，他們接下來會毀滅整個宇宙嗎？', 45745),
(2346, 'zhTW', '元質平原', '造物的精華正遭到吞噬。如果不採取行動，現實世界將會崩塌。', 45745),
(2345, 'zhTW', '造物基石', '利胡敏力挽狂瀾對抗統御之力。守衛者的力量即將被閻獄之主奪走，除非你能夠出手拯救。', 45745),
(2294, 'zhTW', '宏偉設計', '閻獄之主即將獲得最終勝利。你必須阻止他，否則整個宇宙都會屈服於他的力量之下。', 45745),
(2293, 'zhTW', '統御之握', '佐瓦爾最厲害的部下已經摧毀了數百萬個文明，他們接下來會毀滅整個宇宙嗎？', 45745),
(2344, 'zhTW', '終末交鋒', '希瓦娜斯‧風行者在高塔頂端等著你。在那個地方，雙方將展開足以左右宇宙命運的最終對決。', 45745),
(2343, 'zhTW', '命運鐐銬', '靈魂河流的盡頭就在高塔的頂端。消滅以靈魂河流為能量來源的邪惡敵人，解救來生在此受苦受難的無數靈魂。', 45745),
(2342, 'zhTW', '黑暗牢獄', '受折磨者的慘叫聲會讓閻獄之主的部隊更加強大。終結他們的苦難，奪走淵喉最寶貴的資源吧。', 45745),
(2341, 'zhTW', '獄主先鋒', '閻獄之主的先鋒部隊已經準備好抵禦來犯的敵軍。我們必須突破對方的防線，攻入閻獄之主的大本營。', 45745),
(2340, 'zhTW', '覲見傲慢之王', '戴納瑟斯在他的高塔中渴望英雄們的到來，展開決定瑞文崔斯命運的最終決戰。', 45745),
(2339, 'zhTW', '血濃於石', '戴納瑟斯的石源魔軍團將軍正在準備陷阱並等待目標出現，打算要把反叛軍一網打盡。', 45745),
(2338, 'zhTW', '豐裕聖物盒', '在貴族們陶醉在勝利中的同時，英雄們必須闖入他們的宴會並摧毀他們的權力基礎。', 45745),
(2337, 'zhTW', '吸血寶庫', '靈魄如同敞開的傷口從納撒亞城的寶庫湧洩而出。必須阻止靈魄繼續溢出，否則瑞文崔斯將會乾涸。', 45745);
