DROP TABLE IF EXISTS `gurubashi_givers`;
CREATE TABLE IF NOT EXISTS `gurubashi_givers` (
  `entry` int(11) NOT NULL DEFAULT '0',
  `time` int(11) DEFAULT NULL,
  `refresh` int(11) DEFAULT NULL,
  PRIMARY KEY (`entry`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `gurubashi_spawns_creature`;
CREATE TABLE IF NOT EXISTS `gurubashi_spawns_creature` (
  `theme` int(11) DEFAULT NULL,
  `x` double DEFAULT NULL,
  `y` double DEFAULT NULL,
  `z` double DEFAULT NULL,
  `o` double DEFAULT NULL,
  `entry` int(11) DEFAULT NULL,
  `spawndist` int(11) DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `gurubashi_spawns_gameobject`;
CREATE TABLE IF NOT EXISTS `gurubashi_spawns_gameobject` (
  `theme` int(11) DEFAULT NULL,
  `x` double DEFAULT NULL,
  `y` double DEFAULT NULL,
  `z` double DEFAULT NULL,
  `o` double DEFAULT NULL,
  `entry` int(11) DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `gurubashi_themes`;
CREATE TABLE IF NOT EXISTS `gurubashi_themes` (
  `id` int(11) NOT NULL DEFAULT '0',
  `giver` int(11) DEFAULT NULL,
  `name` text,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

