REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('202','Command: rbac account list','Syntax: .rbac account list [$account]\n\nView permissions of selected player or given account\nNote: Only those that affect current realm');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('203','Command: rbac account grant','Syntax: .rbac account grant [$account] #id [#realmId]\n\nGrant a permission to selected player or given account.\n\n#reamID may be -1 for all realms.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('204','Command: rbac account deny','Syntax: .rbac account deny [$account] #id [#realmId]\n\nDeny a permission to selected player or given account.\n\n#reamID may be -1 for all realms.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('205','Command: rbac account revoke','Syntax: .rbac account revoke [$account] #id\n\nRemove a permission from an account\n\nNote: Removes the permission from granted or denied permissions');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('206','Command: rbac list','Syntax: .rbac list [$id]\n\nView list of all permissions. If $id is given will show only info for that permission.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('217','Command: account','Syntax: .account\r\n\r\nDisplay the access level of your account and the email adress if you possess the necessary permissions.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('218','Command: account addon','Syntax: .account addon #addon\nSet expansion addon level allowed. Addon values: 0 - normal, 1 - tbc, 2 - wotlk.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('219','Command: account create','Syntax: .account create $account $password\r\n\r\nCreate account and set password to it.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('220','Command: account delete','Syntax: .account delete $account\r\n\r\nDelete account with all characters.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('222','Command: account lock country','Syntax: .account lock country [on|off]\nAllow login from account only from current used Country or remove this requirement.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('223','Command: account lock ip','Syntax: .account lock ip [on|off]\nAllow login from account only from current used IP or remove this requirement.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('224','Command: account onlinelist','Syntax: .account onlinelist\r\n\r\nShow list of online accounts.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('225','Command: account password','Syntax: .account password $old_password $new_password $new_password [$email]\r\n\r\nChange your account password. You may need to check the actual security mode to see if email input is necessary.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('226','Command: account set','Syntax: .account set $subcommand\nType .account set to see the list of possible subcommands or .help account set $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('227','Command: account set addon','Syntax: .account set addon [$account] #addon\r\n\r\nSet user (possible targeted) expansion addon level allowed. Addon values: 0 - normal, 1 - tbc, 2 - wotlk.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('228','Command: account set gmlevel','Syntax: .account set gmlevel [$account] #level [#realmid]\r\n\r\nSet the security level for targeted player (can\'t be used at self) or for account $name to a level of #level on the realm #realmID.\r\n\r\n#level may range from 0 to 3.\r\n\r\n#reamID may be -1 for all realms.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('229','Command: account set password','Syntax: .account set password $account $password $password\r\n\r\nSet password for account.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('230','Command: achievement','Syntax: .achievement $subcommand\nType .achievement to see the list of possible subcommands or .help achievement $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('231','Command: achievement add','Syntax: .achievement add $achievement\nAdd an achievement to the targeted player.\n$achievement: can be either achievement id or achievement link');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('233','Command: arena captain','Syntax: .arena captain #TeamID $name\n\nA command to set new captain to the team $name must be in the team');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('234','Command: arena create','Syntax: .arena create $name \"arena name\" #type\n\nA command to create a new Arena-team in game. #type  = [2/3/5]');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('235','Command: arena disband','Syntax: .arena disband #TeamID\n\nA command to disband Arena-team in game.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('236','Command: arena info','Syntax: .arena info #TeamID\n\nA command that show info about arena team');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('237','Command: arena lookup','Syntax: .arena lookup $name\n\nA command that give a list of arenateam with the given $name');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('238','Command: arena rename','Syntax: .arena rename \"oldname\" \"newname\"\n\nA command to rename Arena-team name.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('239','Command: ban','Syntax: .ban $subcommand\nType .ban to see the list of possible subcommands or .help ban $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('240','Command: ban account','Syntax: .ban account $Name $bantime $reason\r\nBan account kick player.\r\n$bantime: negative value leads to permban, otherwise use a timestring like \"4d20h3s\".');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('241','Command: ban character','Syntax: .ban character $Name $bantime $reason\nBan character and kick player.\n$bantime: negative value leads to permban, otherwise use a timestring like \"4d20h3s\".');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('242','Command: ban ip','Syntax: .ban ip $Ip $bantime $reason\r\nBan IP.\r\n$bantime: negative value leads to permban, otherwise use a timestring like \"4d20h3s\".');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('243','Command: ban playeraccount','Syntax: .ban playeraccount $Name $bantime $reason\nBan account and kick player.\n$bantime: negative value leads to permban, otherwise use a timestring like \"4d20h3s\".');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('244','Command: baninfo','Syntax: .baninfo $subcommand\nType .baninfo to see the list of possible subcommands or .help baninfo $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('245','Command: baninfo account','Syntax: .baninfo account $accountid\r\nWatch full information about a specific ban.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('246','Command: baninfo character','Syntax: .baninfo character $charactername \r\nWatch full information about a specific ban.');
