REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('248','Command: banlist','Syntax: .banlist $subcommand\nType .banlist to see the list of possible subcommands or .help banlist $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('249','Command: banlist account','Syntax: .banlist account [$Name]\r\nSearches the banlist for a account name pattern or show full list account bans.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('250','Command: banlist character','Syntax: .banlist character $Name\r\nSearches the banlist for a character name pattern. Pattern required.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('251','Command: banlist ip','Syntax: .banlist ip [$Ip]\r\nSearches the banlist for a IP pattern or show full list of IP bans.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('252','Command: unban','Syntax: .unban $subcommand\nType .unban to see the list of possible subcommands or .help unban $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('253','Command: unban account','Syntax: .unban account $Name\r\nUnban accounts for account name pattern.');
