REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('258','Command: bf start','Syntax: .bf start #battleid');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('259','Command: bf stop','Syntax: .bf stop #battleid');
