REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('314','Command: debug moveflags','Syntax: .debug moveflags [$newMoveFlags [$newMoveFlags2]]\r\nNo params given will output the current moveflags of the target');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('316','Command: debug play cinematics','Syntax: .debug play cinematic #cinematicid\r\n\r\nPlay cinematic #cinematicid for you. You stay at place while your mind fly.\r\n');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('317','Command: debug play movie','Syntax: .debug play movie #movieid\r\n\r\nPlay movie #movieid for you.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('318','Command: debug play sound','Syntax: .debug play sound #soundid\r\n\r\nPlay sound with #soundid.\r\nSound will be play only for you. Other players do not hear this.\r\nWarning: client may have more 5000 sounds...');
