REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('378','Command: go creature','Syntax: .go creature #creature_guid\r\nTeleport your character to creature with guid #creature_guid.\r\n.gocreature #creature_name\r\nTeleport your character to creature with this name.\r\n.gocreature id #creature_id\r\nTeleport your character to a creature that was spawned from the template with this entry.\r\n*If* more than one creature is found, then you are teleported to the first that is found inside the database.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('379','Command: go graveyard','Syntax: .go graveyard #graveyardId\r\n Teleport to graveyard with the graveyardId specified.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('380','Command: go grid','Syntax: .go grid #gridX #gridY [#mapId]\r\n\r\nTeleport the gm to center of grid with provided indexes at map #mapId (or current map if it not provided).');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('381','Command: go object','Syntax: .go object #object_guid\r\nTeleport your character to gameobject with guid #object_guid');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('382','Command: go taxinode','Syntax: .go taxinode #taxinode\r\n\r\nTeleport player to taxinode coordinates. You can look up zone using .lookup taxinode $namepart');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('383','Command: go ticket','Syntax: .go ticket #ticketid\r\nTeleports the user to the location where $ticketid was created.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('384','Command: go trigger','Syntax: .go trigger #trigger_id\r\n\r\nTeleport your character to areatrigger with id #trigger_id. Character will be teleported to trigger target if selected areatrigger is telporting trigger.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('385','Command: go xyz','Syntax: .go xyz #x #y [#z [#mapid [#orientation]]]\r\n\r\nTeleport player to point with (#x,#y,#z) coordinates at map #mapid with orientation #orientation. If z is not provided, ground/water level will be used. If mapid is not provided, the current map will be used. If #orientation is not provided, the current orientation will be used.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('386','Command: go zonexy','Syntax: .go zonexy #x #y [#zone]\r\n\r\nTeleport player to point with (#x,#y) client coordinates at ground(water) level in zone #zoneid or current zone if #zoneid not provided. You can look up zone using .lookup area $namepart');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('387','Command: gobject','Syntax: .gobject $subcommand\nType .gobject to see the list of possible subcommands or .help gobject $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('388','Command: gobject activate','Syntax: .gobject activate #guid\r\n\r\nActivates an object like a door or a button.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('389','Command: gobject add','Syntax: .gobject add #id <spawntimeSecs>\r\n\r\nAdd a game object from game object templates to the world at your current location using the #id.\r\nspawntimesecs sets the spawntime, it is optional.\r\n\r\nNote: this is a copy of .gameobject.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('390','Command: gobject add temp','Syntax: .gobject add temp #id\r\n\r\nAdds a temporary gameobject that is not saved to DB.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('391','Command: gobject delete','Syntax: .gobject delete #go_guid\r\nDelete gameobject with guid #go_guid.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('392','Command: gobject info','Syntax: .gobject info [$object_entry]\r \r Query Gameobject information for selected gameobject or given entry.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('393','Command: gobject move','Syntax: .gobject move #goguid [#x #y #z]\r\n\r\nMove gameobject #goguid to character coordinates (or to (#x,#y,#z) coordinates if its provide).');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('394','Command: gobject near','Syntax: .gobject near  [#distance]\r\n\r\nOutput gameobjects at distance #distance from player. Output gameobject guids and coordinates sorted by distance from character. If #distance not provided use 10 as default value.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('396','Command: gobject set phase','Syntax: .gobject set phase #guid #phasemask\r\n\r\nGameobject with DB guid #guid phasemask changed to #phasemask with related world vision update for players. Gameobject state saved to DB and persistent.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('398','Command: gobject target','Syntax: .gobject target [#go_id|#go_name_part]\r\n\r\nLocate and show position nearest gameobject. If #go_id or #go_name_part provide then locate and show position of nearest gameobject with gameobject template id #go_id or name included #go_name_part as part.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('399','Command: gobject turn','Syntax: .gobject turn #goguid \r\n\r\nSet for gameobject #goguid orientation same as current character orientation.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('400','Command: debug transport','Syntax: .debug transport [start/stop]\r\n\r\n Allows to stop a transport at its nearest wait point and start movement of a stopped one. Not all transports can be started or stopped.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('401','Command: guild','Syntax: .guild $subcommand\nType .guild to see the list of possible subcommands or .help guild $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('402','Command: guild create','Syntax: .guild create [$GuildLeaderName] \"$GuildName\"\r\n\r\nCreate a guild named $GuildName with the player $GuildLeaderName (or selected) as leader.  Guild name must in quotes.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('403','Command: guild delete','Syntax: .guild delete \"$GuildName\"\r\n\r\nDelete guild $GuildName. Guild name must in quotes.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('404','Command: guild invite','Syntax: .guild invite [$CharacterName] \"$GuildName\"\r\n\r\nAdd player $CharacterName (or selected) into a guild $GuildName. Guild name must in quotes.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('405','Command: guild uninvite','Syntax: .guild uninvite [$CharacterName]\r\n\r\nRemove player $CharacterName (or selected) from a guild.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('406','Command: guild rank','Syntax: .guild rank [$CharacterName] #Rank\r\n\r\nSet for player $CharacterName (or selected) rank #Rank in a guild.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('407','Command: guild rename','Syntax: .guild rename \"$GuildName\" \"$NewGuildName\" \n\n Rename a guild named $GuildName with $NewGuildName. Guild name and new guild name must in quotes.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('408','Command: honor','Syntax: .honor $subcommand\nType .honor to see the list of possible subcommands or .help honor $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('409','Command: honor add','Syntax: .honor add $amount\r\n\r\nAdd a certain amount of honor (gained today) to the selected player.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('410','Command: honor add kill','Syntax: .honor add kill\r\n\r\nAdd the targeted unit as one of your pvp kills today (you only get honor if it\'s a racial leader or a player)');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('411','Command: honor update','Syntax: .honor update\r\n\r\nForce the yesterday\'s honor fields to be updated with today\'s data, which will get reset for the selected player.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('412','Command: instance','Syntax: .instance $subcommand\nType .instance to see the list of possible subcommands or .help instance $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('413','Command: instance listbinds','Syntax: .instance listbinds\r\n  Lists the binds of the selected player.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('414','Command: instance unbind','Syntax: .instance unbind <mapid|all> [difficulty]\r\n  Clear all/some of player\'s binds');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('415','Command: instance stats','Syntax: .instance stats\r\n  Shows statistics about instances.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('416','Command: instance savedata','Syntax: .instance savedata\r\n  Save the InstanceData for the current player\'s map to the DB.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('417','Command: learn','Syntax: .learn #spell [all]\r\n\r\nSelected character learn a spell of id #spell. If \'all\' provided then all ranks learned.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('420','Command: learn all my class','Syntax: .learn all my class\r\n\r\nLearn all spells and talents available for his class.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('421','Command: learn all my pettalents','Syntax: .learn all my pettalents\r\n\r\nLearn all talents for your pet available for his creature type (only for hunter pets).');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('422','Command: learn all my spells','Syntax: .learn all my spells\r\n\r\nLearn all spells (except talents and spells with first rank learned as talent) available for his class.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('423','Command: learn all my talents','Syntax: .learn all my talents\r\n\r\nLearn all talents (and spells with first rank learned as talent) available for his class.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('424','Command: learn all gm','Syntax: .learn all gm\r\n\r\nLearn all default spells for Game Masters.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('425','Command: learn all crafts','Syntax: .learn crafts\r\n\r\nLearn all professions and recipes.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('426','Command: learn all default','Syntax: .learn all default [$playername]\r\n\r\nLearn for selected/$playername player all default spells for his race/class and spells rewarded by completed quests.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('427','Command: learn all lang','Syntax: .learn all lang\r\n\r\nLearn all languages');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('428','Command: learn all recipes','Syntax: .learn all recipes [$profession]\r\rLearns all recipes of specified profession and sets skill level to max.\rExample: .learn all recipes enchanting');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('429','Command: unlearn','Syntax: .unlearn #spell [all]\r\n\r\nUnlearn for selected player a spell #spell.  If \'all\' provided then all ranks unlearned.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('431','Command: lfg player','Syntax: .lfg player\n Shows information about player (state, roles, comment, dungeons selected).');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('432','Command: lfg group','Syntax: .lfg group\n Shows information about all players in the group  (state, roles, comment, dungeons selected).');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('433','Command: lfg queue','Syntax: .lfg queue\n Shows info about current lfg queues.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('434','Command: lfg clean','Syntax: .flg clean\n Cleans current queue, only for debugging purposes.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('435','Command: lfg options','Syntax: .lfg options [new value]\n Shows current lfg options. New value is set if extra param is present.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('436','Command: list','Syntax: .list $subcommand\nType .list to see the list of possible subcommands or .help list $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('437','Command: list creature','Syntax: .list creature #creature_id [#max_count]\r\n\r\nOutput creatures with creature id #creature_id found in world. Output creature guids and coordinates sorted by distance from character. Will be output maximum #max_count creatures. If #max_count not provided use 10 as default value.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('438','Command: list item','Syntax: .list item #item_id [#max_count]\r\n\r\nOutput items with item id #item_id found in all character inventories, mails, auctions, and guild banks. Output item guids, item owner guid, owner account and owner name (guild name and guid in case guild bank). Will be output maximum #max_count items. If #max_count not provided use 10 as default value.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('439','Command: list object','Syntax: .list object #gameobject_id [#max_count]\r\n\r\nOutput gameobjects with gameobject id #gameobject_id found in world. Output gameobject guids and coordinates sorted by distance from character. Will be output maximum #max_count gameobject. If #max_count not provided use 10 as default value.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('440','Command: list auras','Syntax: .list auras\nList auras (passive and active) of selected creature or player. If no creature or player is selected, list your own auras.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('441','Command: list mail','Syntax: .list mail $character\nList of mails the character received.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('442','Command: lookup','Syntax: .lookup $subcommand\nType .lookup to see the list of possible subcommands or .help lookup $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('443','Command: lookup area','Syntax: .lookup area $namepart\r\n\r\nLooks up an area by $namepart, and returns all matches with their area ID\'s.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('444','Command: lookup creature','Syntax: .lookup creature $namepart\r\n\r\nLooks up a creature by $namepart, and returns all matches with their creature ID\'s.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('445','Command: lookup event','Syntax: .lookup event $name\r\nAttempts to find the ID of the event with the provided $name.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('446','Command: lookup faction','Syntax: .lookup faction $name\r\nAttempts to find the ID of the faction with the provided $name.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('447','Command: lookup item','Syntax: .lookup item $itemname\r\n\r\nLooks up an item by $itemname, and returns all matches with their Item ID\'s.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('448','Command: lookup itemset','Syntax: .lookup itemset $itemname\r\n\r\nLooks up an item set by $itemname, and returns all matches with their Item set ID\'s.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('449','Command: lookup object','Syntax: .lookup object $objname\r\n\r\nLooks up an gameobject by $objname, and returns all matches with their Gameobject ID\'s.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('450','Command: lookup quest','Syntax: .lookup quest $namepart\r\n\r\nLooks up a quest by $namepart, and returns all matches with their quest ID\'s.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('452','Command: lookup player ip','Syntax: .lookup player ip $ip ($limit) \r\n\r\n Searchs players, which account ast_ip is $ip with optional parametr $limit of results.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('453','Command: lookup player account','Syntax: .lookup player account $account ($limit) \r\n\r\n Searchs players, which account username is $account with optional parametr $limit of results.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('454','Command: lookup player email','Syntax: .lookup player email $email ($limit) \r\n\r\n Searchs players, which account email is $email with optional parametr $limit of results.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('455','Command: lookup skill','Syntax: .lookup skill $$namepart\r\n\r\nLooks up a skill by $namepart, and returns all matches with their skill ID\'s.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('456','Command: lookup spell','Syntax: .lookup spell $namepart\r\n\r\nLooks up a spell by $namepart, and returns all matches with their spell ID\'s.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('457','Command: lookup spell id','Syntax: .lookup spell id #spellid\n\nLooks up a spell by #spellid, and returns the match with its spell name.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('458','Command: lookup taxinode','Syntax: .lookup taxinode $substring\r\n\r\nSearch and output all taxinodes with provide $substring in name.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('459','Command: lookup tele','Syntax: .lookup tele $substring\r\n\r\nSearch and output all .tele command locations with provide $substring in name.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('460','Command: lookup title','Syntax: .lookup title $$namepart\r\n\r\nLooks up a title by $namepart, and returns all matches with their title ID\'s and index\'s.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('461','Command: lookup map','Syntax: .lookup map $namepart\r\n\r\nLooks up a map by $namepart, and returns all matches with their map ID\'s.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('462','Command: announce','Syntax: .announce $MessageToBroadcast\r\n\r\nSend a global message to all players online in chat log.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('465','Command: channel set ownership','Syntax: .channel set ownership $channel [on/off]\n\nGrant ownership to the first person that joins the channel.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('466','Command: gmannounce','Syntax: .gmannounce $announcement\r\nSend an announcement to online Gamemasters.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('467','Command: gmnameannounce','Syntax: .gmnameannounce $announcement.\r\nSend an announcement to all online GM\'s, displaying the name of the sender.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('468','Command: gmnotify','Syntax: .gmnotify $notification\r\nDisplays a notification on the screen of all online GM\'s.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('469','Command: nameannounce','Syntax: .nameannounce $announcement.\nSend an announcement to all online players, displaying the name of the sender.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('470','Command: notify','Syntax: .notify $MessageToBroadcast\r\n\r\nSend a global message to all players online in screen.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('471','Command: whispers','Syntax: .whispers on|off\r\nEnable/disable accepting whispers by GM from players. By default use trinityd.conf setting.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('472','Command: group','Syntax: .group $subcommand\nType .group to see the list of possible subcommands or .help group $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('473','Command: group leader','Syntax: .group leader [$characterName]\n\nSets the given character as his group\'s leader.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('474','Command: group disband','Syntax: .group disband [$characterName]\n\nDisbands the given character\'s group.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('475','Command: group remove','Syntax: .group remove [$characterName]\n\nRemoves the given character from his group.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('476','Command: group join','Syntax: .group join $AnyCharacterNameFromGroup [$CharacterName] \r\nAdds to group of player $AnyCharacterNameFromGroup player $CharacterName (or selected).');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('477','Command: group list','Syntax: .group list [$CharacterName] \r\nLists all the members of the group/party the player is in.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('478','Command: group summon','Syntax: .group summon [$charactername]\r\n\r\nTeleport the given character and his group to you. Teleported only online characters but original selected group member can be offline.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('479','Command: pet','Syntax: .pet $subcommand\nType .pet to see the list of possible subcommands or .help pet $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('480','Command: pet create','Syntax: .pet create\r\n\r\nCreates a pet of the selected creature.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('481','Command: pet learn','Syntax: .pet learn\r\n\r\nLearn #spellid to pet.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('482','Command: pet unlearn','Syntax: .pet unlean\r\n\r\nunLearn #spellid to pet.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('484','Command: send items','Syntax: .send items #playername \"#subject\" \"#text\" itemid1[:count1] itemid2[:count2] ... itemidN[:countN]\r\n\r\nSend a mail to a player. Subject and mail text must be in \"\". If for itemid not provided related count values then expected 1, if count > max items in stack then items will be send in required amount stacks. All stacks amount in mail limited to 12.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('485','Command: send mail','Syntax: .send mail #playername \"#subject\" \"#text\"\r\n\r\nSend a mail to a player. Subject and mail text must be in \"\".');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('486','Command: send message','Syntax: .send message $playername $message\r\n\r\nSend screen message to player from ADMINISTRATOR.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('487','Command: send money','Syntax: .send money #playername \"#subject\" \"#text\" #money\r\n\r\nSend mail with money to a player. Subject and mail text must be in \"\".');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('488','Command: additem','Syntax: .additem #itemid/[#itemname]/#shift-click-item-link #itemcount\r\n\r\nAdds the specified number of items of id #itemid (or exact (!) name $itemname in brackets, or link created by shift-click at item in inventory or recipe) to your or selected character inventory. If #itemcount is omitted, only one item will be added.\r\n.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('489','Command: additemset','Syntax: .additemset #itemsetid\r\n\r\nAdd items from itemset of id #itemsetid to your or selected character inventory. Will add by one example each item from itemset.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('490','Command: appear','Syntax: .appear [$charactername]\r\n\r\nTeleport to the given character. Either specify the character name or click on the character\'s portrait,e.g. when you are in a group. Character can be offline.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('491','Command: aura','Syntax: .aura #spellid\r\n\r\nAdd the aura from spell #spellid to the selected Unit.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('492','Command: bank','Syntax: .bank\r\n\r\nShow your bank inventory.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('493','Command: bindsight','Syntax: .bindsight\r\n\r\nBinds vision to the selected unit indefinitely. Cannot be used while currently possessing a target.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('494','Command: combatstop','Syntax: .combatstop [$playername]\r\nStop combat for selected character. If selected non-player then command applied to self. If $playername provided then attempt applied to online player $playername.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('495','Command: cometome','Syntax: .cometome $parameter\nMake selected creature come to your current location (new position not saved to DB).');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('496','Command: commands','Syntax: .commands\r\n\r\nDisplay a list of available commands for your account level.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('497','Command: cooldown','Syntax: .cooldown [#spell_id]\r\n\r\nRemove all (if spell_id not provided) or #spel_id spell cooldown from selected character or you (if no selection).');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('498','Command: damage','Syntax: .damage $damage_amount [$school [$spellid]]\r\n\r\nApply $damage to target. If not $school and $spellid provided then this flat clean melee damage without any modifiers. If $school provided then damage modified by armor reduction (if school physical), and target absorbing modifiers and result applied as melee damage to target. If spell provided then damage modified and applied as spell damage. $spellid can be shift-link.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('499','Command: dev','Syntax: .dev [on/off]\r\n\r\nEnable or Disable in game Dev tag or show current state if on/off not provided.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('500','Command: die','Syntax: .die\r\n\r\nKill the selected player. If no player is selected, it will kill you.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('501','Command: dismount','Syntax: .dismount\r\n\r\nDismount you, if you are mounted.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('502','Command: distance','Syntax: .distance\r\n\r\nDisplay the distance from your character to the selected creature.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('503','Command: flusharenapoints','Syntax: .flusharenapoints\r\n\r\nUse it to distribute arena points based on arena team ratings, and start a new week.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('504','Command: freeze','Syntax: .freeze (#player)\r\n\"Freezes\" #player and disables his chat. When using this without #name it will freeze your target.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('505','Command: gps','Syntax: .gps [$name|$shift-link]\r\n\r\nDisplay the position information for a selected character or creature (also if player name $name provided then for named player, or if creature/gameobject shift-link provided then pointed creature/gameobject if it loaded). Position information includes X, Y, Z, and orientation, map Id and zone Id');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('506','Command: guid','Syntax: .guid\r\n\r\nDisplay the GUID for the selected character.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('507','Command: help','Syntax: .help [$command]\r\n\r\nDisplay usage instructions for the given $command. If no $command provided show list available commands.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('508','Command: hidearea','Syntax: .hidearea #areaid\r\n\r\nHide the area of #areaid to the selected character. If no character is selected, hide this area to you.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('509','Command: itemmove','Syntax: .itemmove #sourceslotid #destinationslotid\r\n\r\nMove an item from slots #sourceslotid to #destinationslotid in your inventory\r\n\r\nNot yet implemented');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('510','Command: kick','Syntax: .kick [$charactername] [$reason]\r\n\r\nKick the given character name from the world with or without reason. If no character name is provided then the selected player (except for yourself) will be kicked. If no reason is provided, default is \"No Reason\".');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('511','Command: linkgrave','Syntax: .linkgrave #graveyard_id [alliance|horde]\r\n\r\nLink current zone to graveyard for any (or alliance/horde faction ghosts). This let character ghost from zone teleport to graveyard after die if graveyard is nearest from linked to zone and accept ghost of this faction. Add only single graveyard at another map and only if no graveyards linked (or planned linked at same map).');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('512','Command: listfreeze','Syntax: .listfreeze\r\n\r\nSearch and output all frozen players.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('513','Command: maxskill','Syntax: .maxskill\r\nSets all skills of the targeted player to their maximum values for its current level.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('514','Command: movegens','Syntax: .movegens\r\n  Show movement generators stack for selected creature or player.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('515','Command: mute','Syntax: .mute [$playerName] $timeInMinutes [$reason]\r\n\r\nDisible chat messaging for any character from account of character $playerName (or currently selected) at $timeInMinutes minutes. Player can be offline.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('516','Command: neargrave','Syntax: .neargrave [alliance|horde]\r\n\r\nFind nearest graveyard linked to zone (or only nearest from accepts alliance or horde faction ghosts).');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('517','Command: pinfo','Syntax: .pinfo [$player_name/#GUID]\r\n\r\nOutput account information and guild information for selected player or player find by $player_name or #GUID.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('518','Command: playall','Syntax: .playall #soundid\r\n\r\nPlayer a sound to whole server.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('519','Command: possess','Syntax: .possess\r\n\r\nPossesses indefinitely the selected creature.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('520','Command: recall','Syntax: .recall [$playername]\r\n\r\nTeleport $playername or selected player to the place where he has been before last use of a teleportation command. If no $playername is entered and no player is selected, it will teleport you.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('521','Command: repairitems','Syntax: .repairitems\r\n\r\nRepair all selected player\'s items.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('522','Command: respawn','Syntax: .respawn\r\n\r\nRespawn all nearest creatures and GO without waiting respawn time expiration.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('523','Command: revive','Syntax: .revive\r\n\r\nRevive the selected player. If no player is selected, it will revive you.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('524','Command: saveall','Syntax: .saveall\r\n\r\nSave all characters in game.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('525','Command: save','Syntax: .save\r\n\r\nSaves your character.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('526','Command: setskill','Syntax: .setskill #skill #level [#max]\r\n\r\nSet a skill of id #skill with a current skill value of #level and a maximum value of #max (or equal current maximum if not provide) for the selected character. If no character is selected, you learn the skill.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('527','Command: showarea','Syntax: .showarea #areaid\r\n\r\nReveal the area of #areaid to the selected character. If no character is selected, reveal this area to you.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('528','Command: summon','Syntax: .summon [$charactername]\r\n\r\nTeleport the given character to you. Character can be offline.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('529','Command: unaura','Syntax: .unaura #spellid\r\n\r\nRemove aura due to spell #spellid from the selected Unit.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('530','Command: unbindsight','Syntax: .unbindsight\r\n\r\nRemoves bound vision. Cannot be used while currently possessing a target.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('531','Command: unfreeze','Syntax: .unfreeze (#player)\r\n\"Unfreezes\" #player and enables his chat again. When using this without #name it will unfreeze your target.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('532','Command: unmute','Syntax: .unmute [$playerName]\r\n\r\nRestore chat messaging for any character from account of character $playerName (or selected). Character can be ofline.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('533','Command: unpossess','Syntax: .unpossess\r\n\r\nIf you are possessed, unpossesses yourself; otherwise unpossesses current possessed target.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('534','Command: unstuck','Syntax: .unstuck $playername [inn/graveyard/startzone]\n\nTeleports specified player to specified location. Default location is player\'s current hearth location.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('535','Command: wchange','Syntax: .wchange #weathertype #status\r\n\r\nSet current weather to #weathertype with an intensity of #status.\r\n\r\n#weathertype can be 1 for rain, 2 for snow, and 3 for sand. #status can be 0 for disabled, and 1 for enabled.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('536','Command: mmap','Syntax: .mmaps $subcommand Type .mmaps to see the list of possible subcommands or .help mmaps $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('537','Command: mmap loadedtiles','Syntax: .mmap loadedtiles to show which tiles are currently loaded');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('538','Command: mmap loc','Syntax: .mmap loc to print on which tile one is');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('539','Command: mmap path','Syntax: .mmap path to calculate and show a path to current select unit');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('540','Command: mmap stats','Syntax: .mmap stats to show information about current state of mmaps');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('541','Command: mmap testarea','Syntax: .mmap testarea to calculate paths for all nearby npcs to player');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('542','Command: morph','Syntax: .morph #displayid\r\n\r\nChange your current model id to #displayid.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('543','Command: demorph','Syntax: .demorph\r\n\r\nDemorph the selected player.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('544','Command: modify','Syntax: .modify $subcommand\nType .modify to see the list of possible subcommands or .help modify $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('545','Command: modify arenapoints','Syntax: .modify arenapoints #value\r\nAdd $amount arena points to the selected player.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('546','Command: modify bit','Syntax: .modify bit #field #bit\r\n\r\nToggle the #bit bit of the #field field for the selected player. If no player is selected, modify your character.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('547','Command: modify drunk','Syntax: .modify drunk #value\r\n Set drunk level to #value (0..100). Value 0 remove drunk state, 100 is max drunked state.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('548','Command: modify energy','Syntax: .modify energy #energy\r\n\r\nModify the energy of the selected player. If no player is selected, modify your energy.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('549','Command: modify faction','Syntax: .modify faction #factionid #flagid #npcflagid #dynamicflagid\r\n\r\nModify the faction and flags of the selected creature. Without arguments, display the faction and flags of the selected creature.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('550','Command: modify gender','Syntax: .modify gender male/female\r\n\r\nChange gender of selected player.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('551','Command: modify honor','Syntax: .modify honor $amount\r\n\r\nAdd $amount honor points to the selected player.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('552','Command: modify hp','Syntax: .modify hp #newhp\r\n\r\nModify the hp of the selected player. If no player is selected, modify your hp.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('553','Command: modify mana','Syntax: .modify mana #newmana\r\n\r\nModify the mana of the selected player. If no player is selected, modify your mana.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('554','Command: modify money','Syntax: .modify money #money\r\n.money #money\r\n\r\nAdd or remove money to the selected player. If no player is selected, modify your money.\r\n\r\n #gold can be negative to remove money.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('555','Command: modify mount','Syntax: .modify mount #id #speed\r\nDisplay selected player as mounted at #id creature and set speed to #speed value.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('556','Command: modify phase','Syntax: .modify phase #phasemask\r\n\r\nSelected character phasemask changed to #phasemask with related world vision update. Change active until in game phase changed, or GM-mode enable/disable, or re-login. Character pts pasemask update to same value.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('557','Command: modify rage','Syntax: .modify rage #newrage\r\n\r\nModify the rage of the selected player. If no player is selected, modify your rage.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('558','Command: modify reputation','Syntax: .modify reputation #repId (#repvalue | $rankname [#delta])\nSets the selected players reputation with faction #repId to #repvalue or to $reprank.\nIf the reputation rank name is provided, the resulting reputation will be the lowest reputation for that rank plus the delta amount, if specified.\nYou can use \'.pinfo rep\' to list all known reputation ids, or use \'.lookup faction $name\' to locate a specific faction id.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('559','Command: modify runicpower','Syntax: .modify runicpower #newrunicpower\r\n\r\nModify the runic power of the selected player. If no player is selected, modify your runic power.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('560','Command: modify scale','Syntax: .modify scale #scale\r\n\nModify size of the selected player or creature to \"normal scale\"*rate. If no player or creature is selected, modify your size.\n#rate may range from 0.1 to 10.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('561','Command: modify speed','Syntax: .modify speed $speedtype #rate\r\n\r\nModify the running speed of the selected player to \"normal base run speed\"= 1. If no player is selected, modify your speed.\r\n\r\n$speedtypes may be fly, all, walk, backwalk, or swim.\r\n\r\n #rate may range from 0.1 to 50.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('562','Command: modify speed all','Syntax: .modify aspeed #rate\r\n\r\nModify all speeds -run,swim,run back,swim back- of the selected player to \"normalbase speed for this move type\"*rate. If no player is selected, modify your speed.\r\n\r\n #rate may range from 0.1 to 50.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('563','Command: modify speed backwalk','Syntax: .modify speed backwalk #rate\r\n\r\nModify the speed of the selected player while running backwards to \"normal walk back speed\"*rate. If no player is selected, modify your speed.\r\n\r\n #rate may range from 0.1 to 50.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('564','Command: modify speed fly','Syntax: .modify speed fly #rate\r\n\nModify the flying speed of the selected player to \"normal flying speed\"*rate. If no player is selected, modify your speed.\n #rate may range from 0.1 to 50.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('565','Command: modify speed walk','Syntax: .modify speed bwalk #rate\r\n\r\nModify the speed of the selected player while running to \"normal walk speed\"*rate. If no player is selected, modify your speed.\r\n\r\n #rate may range from 0.1 to 50.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('566','Command: modify speed swim','Syntax: .modify speed swim #rate\r\n\r\nModify the swim speed of the selected player to \"normal swim speed\"*rate. If no player is selected, modify your speed.\r\n\r\n #rate may range from 0.1 to 50.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('567','Command: modify spell','TODO');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('568','Command: modify standstate','Syntax: .modify standstate #emoteid\r\n\r\nChange the emote of your character while standing to #emoteid.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('569','Command: modify talentpoints','Syntax: .modify talentpoints #amount\r\n\r\nSet free talent points for selected character or character\'s pet. It will be reset to default expected at next levelup/login/quest reward.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('570','Command: npc','Syntax: .npc $subcommand\nType .npc to see the list of possible subcommands or .help npc $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('571','Command: npc add','Syntax: .npc add #creatureid\r\n\r\nSpawn a creature by the given template id of #creatureid.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('572','Command: npc add formation','Syntax: .npc add formation $leader\nAdd selected creature to a leader\'s formation.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('573','Command: npc add item','Syntax: .npc add item #itemId <#maxcount><#incrtime><#extendedcost>r\r\n\r\nAdd item #itemid to item list of selected vendor. Also optionally set max count item in vendor item list and time to item count restoring and items ExtendedCost.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('574','Command: npc add move','Syntax: .npc add move #creature_guid [#waittime]\r\n\r\nAdd your current location as a waypoint for creature with guid #creature_guid. And optional add wait time.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('575','Command: npc add temp','Syntax: .npc add temp\r\n\r\nAdds temporary NPC, not saved to database.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('576','Command: npc add delete','Syntax: .npc delete [#guid]\r\n\r\nDelete creature with guid #guid (or the selected if no guid is provided)');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('577','Command: npc add delete item','Syntax: .npc delete item #itemId\r\n\r\nRemove item #itemid from item list of selected vendor.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('578','Command: npc add follow','Syntax: .npc follow start\r\n\r\nSelected creature start follow you until death/fight/etc.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('579','Command: npc add follow stop','Syntax: .npc follow stop\r\n\r\nSelected creature (non pet) stop follow you.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('581','Command: npc set allowmove','Syntax: .npc set allowmove\r\n\r\nEnable or disable movement creatures in world. Not implemented.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('582','Command: npc set entry','Syntax: .npc set entry $entry\nSwitch selected creature with another entry from creature_template. - New creature.id value not saved to DB.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('583','Command: npc set factionid','Syntax: .npc set factionid #factionid\r\n\r\nSet the faction of the selected creature to #factionid.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('584','Command: npc set flag','Syntax: .npc set flag #npcflag\r\n\r\nSet the NPC flags of creature template of the selected creature and selected creature to #npcflag. NPC flags will applied to all creatures of selected creature template after server restart or grid unload/load.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('585','Command: npc set level','Syntax: .npc set level #level\r\n\r\nChange the level of the selected creature to #level.\r\n\r\n#level may range from 1 to (CONFIG_MAX_PLAYER_LEVEL) + 3.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('586','Command: npc set link','Syntax: .npc set link $creatureGUID\r\n\r\nLinks respawn of selected creature to the condition that $creatureGUID defined is alive.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('587','Command: npc set model','Syntax: .npc set model #displayid\r\n\r\nChange the model id of the selected creature to #displayid.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('588','Command: npc set movetype','Syntax: .npc set movetype [#creature_guid] stay/random/way [NODEL]\r\n\r\nSet for creature pointed by #creature_guid (or selected if #creature_guid not provided) movement type and move it to respawn position (if creature alive). Any existing waypoints for creature will be removed from the database if you do not use NODEL. If the creature is dead then movement type will applied at creature respawn.\r\nMake sure you use NODEL, if you want to keep the waypoints.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('589','Command: npc set phase','Syntax: .npc set phase #phasemask\r\n\r\nSelected unit or pet phasemask changed to #phasemask with related world vision update for players. In creature case state saved to DB and persistent. In pet case change active until in game phase changed for owner, owner re-login, or GM-mode enable/disable..');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('590','Command: npc set spawndist','Syntax: .npc set spawndist #dist\r\n\r\nAdjust spawndistance of selected creature to dist.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('591','Command: npc set spawntime','Syntax: .npc set spawntime #time \r\n\r\nAdjust spawntime of selected creature to time.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('592','Command: npc set data','Syntax: .npc set data $field $data\nSets data for the selected creature. Used for testing Scripting');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('593','Command: npc info','Syntax: .npc info\r\n\r\nDisplay a list of details for the selected creature.\r\n\r\nThe list includes:\r\n- GUID, Faction, NPC flags, Entry ID, Model ID,\r\n- Level,\r\n- Health (current/maximum),\r\n\r\n- Field flags, dynamic flags, faction template, \r\n- Position information,\r\n- and the creature type, e.g. if the creature is a vendor.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('595','Command: npc move','Syntax: .npc move [#creature_guid]\r\n\r\nMove the targeted creature spawn point to your coordinates.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('596','Command: npc playemote','Syntax: .npc playemote #emoteid\r\n\r\nMake the selected creature emote with an emote of id #emoteid.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('597','Command: npc say','Syntax: .npc say $message\nMake selected creature say specified message.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('598','Command: npc textemote','Syntax: .npc textemote #emoteid\r\n\r\nMake the selected creature to do textemote with an emote of id #emoteid.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('599','Command: npc whisper','Syntax: .npc whisper #playerguid #text\r\nMake the selected npc whisper #text to  #playerguid.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('600','Command: npc yell','Syntax: .npc yell $message\nMake selected creature yell specified message.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('602','Command: quest','Syntax: .quest $subcommand\nType .quest to see the list of possible subcommands or .help quest $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('603','Command: quest add','Syntax: .quest add #quest_id\r\n\r\nAdd to character quest log quest #quest_id. Quest started from item can\'t be added by this command but correct .additem call provided in command output.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('604','Command: quest complete','Syntax: .quest complete #questid\r\nMark all quest objectives as completed for target character active quest. After this target character can go and get quest reward.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('605','Command: quest remove','Syntax: .quest remove #quest_id\r\n\r\nSet quest #quest_id state to not completed and not active (and remove from active quest list) for selected player.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('606','Command: quest reward','Syntax: .quest reward #questId\n\nGrants quest reward to selected player and removes quest from his log (quest must be in completed state).');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('607','Command: reload','Syntax: .reload $subcommand\nType .reload to see the list of possible subcommands or .help reload $subcommand to see info on subcommands');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('608','Command: reload access_requirement','Syntax: .reload access_requirement\nReload access_requirement table.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('609','Command: reload achievement_criteria_data','Syntax: .reload achievement_criteria_data\nReload achievement_criteria_data table.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('610','Command: reload achievement_reward','Syntax: .reload achievement_reward\nReload achievement_reward table.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('611','Command: reload all','Syntax: .reload all\r\n\r\nReload all tables with reload support added and that can be _safe_ reloaded.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('612','Command: reload all achievement','Syntax: .reload all achievement\r\n\r\nReload achievement_reward, achievement_criteria_data tables.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('613','Command: reload all area','Syntax: .reload all area\r\n\r\nReload areatrigger_teleport, areatrigger_tavern, game_graveyard_zone tables.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('615','Command: reload all gossips','Syntax: .reload all gossips\nReload gossip_menu, gossip_menu_option, gossip_scripts, points_of_interest tables.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('616','Command: reload all item','Syntax: .reload all item\nReload page_text, item_enchantment_table tables.');
REPLACE INTO `rbac_permissions` (`id`, `name`, `help`) VALUES('617','Command: reload all locales','Syntax: .reload all locales\r\n\r\nReload all `locales_*` tables with reload support added and that can be _safe_ reloaded.');
