UPDATE `broadcast_text` SET `MaleText`='Thank you for taking the time to help us test the Cataclysm content for World of Warcraft. \n\nWhere would you like to go?', `FemaleText`='Thank you for taking the time to help us test the Cataclysm content for World of Warcraft. \n\nWhere would you like to go?', `VerifiedBuild`=19831 WHERE `ID`=40940; -- 40940
