UPDATE `broadcast_text` SET `FemaleText`='Took the fish to market. See you at Halfhill!\n\n- Fish', `VerifiedBuild`=19831 WHERE `ID`=57544; -- 57544
UPDATE `broadcast_text` SET `FemaleText`='If you find this note, then I am either dead or at Halfhill Market.$B$BHopefully I have temporarily left my post to enjoy a hot mug of ale in town. Perhaps you should go check, just in case.$B$BIf indeed I have failed my mission, please send my love and effects to my family in Zhu''s Watch.\n\n- Sho', `VerifiedBuild`=19831 WHERE `ID`=57545; -- 57545
UPDATE `broadcast_text` SET `MaleText`='M A r c K e T.\n\n- Chee Chee', `VerifiedBuild`=19831 WHERE `ID`=57546; -- 57546
