UPDATE `broadcast_text` SET `FemaleText`='If this artifact that Harrison is seeking is as powerful as he seems to think, I hope it remains undiscovered.\n\nRegardless of intentions, the wielding of great power often comes with drastic consequences.', `VerifiedBuild`=19831 WHERE `ID`=90340; -- 90340
UPDATE `broadcast_text` SET `MaleText`='The cost to do battle in the Coliseum is 100 Broken Bones. Do you wish to proceed?', `VerifiedBuild`=19831 WHERE `ID`=90361; -- 90361
UPDATE `broadcast_text` SET `MaleText`='Many adventurers have come to Ashran of late. Some seek their own personal fortune, while others come to aid the Alliance in their plight against the Horde.\n\nWhat adventure do you seek here, friend?', `VerifiedBuild`=19831 WHERE `ID`=90386; -- 90386
UPDATE `broadcast_text` SET `SoundId`=49764, `Type`=0, `VerifiedBuild`=19831 WHERE `ID`=90417; -- 90417
