INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(187442, 'itIT', '', 'I tuoi peccati sono molti, ma niente è così crudele come aizzare le tue belve sugli innocenti.', 37474),
(38124, 'itIT', 'Abbraccia il buio... il buio eterno.', 'Abbraccia il buio... il buio eterno.', 37474),
(180366, 'itIT', '', 'È un piacere darti il benvenuto al Bastione! Anche se... Non sei come le anime che ho visto finora, ma non importa.', 37474),
(187280, 'itIT', 'Con la Casata degli Occhi fuori dai giochi, la nostra segretezza è stata assicurata. Non rimane nessuno a intralciarci.', '', 37474),
(185904, 'itIT', 'Gelosia? Ti prego. Ero superiore a lui in tutto!', '', 37474),
(182733, 'itIT', 'Il Marchio della Conoscenza, una luce contro l\'ignoranza e la paura.', '', 37474),
(180367, 'itIT', '', 'Farai parte dei Kyrian! Il nostro sacro compito è portare le anime mortali dinanzi all\'Arbiter perché siano giudicate.', 37474),
(194383, 'itIT', '', 'Sarà entusiasmante.', 37474),
(182626, 'itIT', '', 'Avvicinati alla fiamma quando vuoi e segui le istruzioni del Guardiano.', 37474),
(202313, 'itIT', '', 'Lascia che li infilzi! Che li tagli! Che li fenda! Li vivisezioni! Li faccia a brandelli...', 37474),
(202311, 'itIT', 'Mmm, ora che ci penso... potremmo scorticare queste petulanti, stupide creature e usare le loro ossa per spianarci la strada verso la nostra ascesa. Che fare, che fare...', '', 37474),
(202310, 'itIT', '', 'Ho un\'idea migliore, Maestro! Squartiamoli invece!', 37474),
(202312, 'itIT', '', 'Ti preeego, Maestro! È da tanto che non taglio carne viva!', 37474),
(202315, 'itIT', 'Assisterò i nostri ospiti nella loro espiazione. Quando si saranno pentiti, ti permetterò di lacerarne i corpi e spedire le loro anime urlanti nella Fauce!', '', 37474),
(205464, 'itIT', '', 'Ora capisco perché le creature del tuo mondo sono considerate così formidabili. Non c\'è da meravigliarsi che tu sia $griuscito:riuscita; a respingere la Legione Infuocata. Sono grata per la tua forza, a nome di Selvarden.', 37474),
(191962, 'itIT', '<C\'è un testo inciso alla base della statua.>\n\nIl rispetto si guadagna con una prova di forza. Rendiamo un omaggio Militare a chi si erge sulle masse.', '', 37474),
(191966, 'itIT', '<Il testo è inciso alla base della statua.>\n\nNell\'oscurità si celano molte verità nascoste. Chi è veramente abile può Defilarsi senza che i nemici se ne accorgano.', '', 37474),
(180775, 'itIT', 'Devo avvisare... il Droman...', 'Devo avvisare... il Droman...', 37474),
(193577, 'itIT', '', 'Tienimelo un momento.', 37474),
(186290, 'itIT', 'Elegante e potente. Una corona per difendere un regno.', '', 37474),
(193598, 'itIT', '', 'Dove dovrei metterti?', 37474),
(193582, 'itIT', '', 'Ti dispiace fargli fare una prova?', 37474),
(203409, 'itIT', 'Un\'arma... forgiata nel fuoco di Torgast. Una lama greve è nata.', '', 37474),
(196707, 'itIT', 'Spara quando vuoi!', '', 37474),
(199409, 'itIT', 'Vuoi il mio Animum? Perché non può farlo l\'antica Tirnenn?', '', 37474),
(199444, 'itIT', 'Ecco. Facciamola finita!', '', 37474),
(198956, 'itIT', 'Non ho tempo per questo, $C, la Contessa sta aspettando.', 'Non ho tempo per questo, $C, la Contessa sta aspettando.', 37474),
(206412, 'itIT', '', 'Cosa ti porta a Maldraxxus, $p?\n\nAltri funghi per i banchetti della tua corte o altri soldati di Maldraxxus per difendere le mura?\n\n<La Baronessa Vashj sorride.>\n\nDevo ammetterlo, mi sto godendo la Corte delle Braci più di quanto mi aspettassi.\n\nIl Principe Renathal potrà essere stato l\'organizzatore principale, ma io ho messo più spesso i miei occhi su di te.\n\nSei $gcolui:colei; che agisce dietro le quinte, proprio come ho fatto io durante i miei giorni alla corte della Regina.\n\n<La Baronessa Vashj guarda lontano per un istante.>\n\nNon è facile, ciò che fai.', 37474),
(202361, 'itIT', 'Kyrian! Ti do il benvenuto negli Archivi degli Eoni, dove un afflusso d\'Animum ci permette di rinforzare le tue capacità.\n\nIl mio potenziamento ti fornisce il seguente effetto:\n\n|TInterface\\ICONS\\Achievement_Raid_Thunder_King.blp:20|t|cFFFF0000|Hspell:335576|h[Stella Polare dell\'Ascendente]|h|r: quando si ha più del 90% della salute massima, gli incantesimi e le abilità d\'attacco hanno una probabilità di lanciare Marchiato per la Raccolta sul bersaglio, infliggendo danni periodici. Sconfiggere un nemico che ne è affetto permette di raccoglierne l\'Animum, fornendo uno scudo che protegge per qualche istante.', 'Kyrian! Ti do il benvenuto negli Archivi degli Eoni, dove un afflusso d\'Animum ci permette di rinforzare le tue capacità.\n\nIl mio potenziamento ti fornisce il seguente effetto:\n\n|TInterface\\ICONS\\Achievement_Raid_Thunder_King.blp:20|t|cFFFF0000|Hspell:335576|h[Stella Polare dell\'Ascendente]|h|r: quando si ha più del 90% della salute massima, gli incantesimi e le abilità d\'attacco hanno una probabilità di lanciare Marchiato per la Raccolta sul bersaglio, infliggendo danni periodici. Sconfiggere un nemico che ne è affetto permette di raccoglierne l\'Animum, fornendo uno scudo che protegge per qualche istante.', 37474),
(199451, 'itIT', '', 'Ah... be\', una volta con Plasmasogni...', 37474),
(183994, 'itIT', '', 'Un po\' a sinistra!', 37474),
(183995, 'itIT', '', 'Sinistra! Destra! L\'altra tua destra!', 37474),
(183993, 'itIT', 'Riesci a raggiungere la cima?', '', 37474),
(204708, 'itIT', 'La Defunta Blanchy solleva gli zoccoli obbedientemente, felice di avere curate le sue zampe ferite. È senza fiato e soffia a fatica e sembra assetata.', '', 37474),
(204686, 'itIT', 'La Defunta Blanchy trema e ti si strofina addosso mentre la Spazzola per Strigliatura toglie le sostanze irritanti dalla sua pelle. Man mano che cambia dove tiene il peso e scalpita, vedi che gli zoccoli sono scheggiati malamente.', '', 37474),
(204663, 'itIT', 'La Defunta Blanchy accetta l\'avena con entusiasmo. Anche se ha chiazze di peli, la sua pelle è piena di irritazioni da acari urticanti, spine appuntite e schifezza puzzolente.', '', 37474),
(204710, 'itIT', 'La Defunta Blanchy resta immobile mentre il sottosella viene messo su di lei. Mentre il suo tremore rallenta e poi smette, Blanchy inizia a cercare nelle tue sacche un dolcetto.', '', 37474),
(204668, 'itIT', 'La Defunta Blanchy si ferma improvvisamente per evitare di travolgerti.', '', 37474),
(204903, 'itIT', 'Gli occhi della Defunta Blanchy sono frenetici. La sua forma è malata e smunta. È chiaramente affamata e ha bisogno di un buon pasto.', '', 37474),
(121956, 'itIT', 'Io, Maestro Mathias Shaw, autorizzo la Lama Rossa a fermare l\'agente ribelle Amber Kearnen.', '', 37474),
(204709, 'itIT', 'La Defunta Blanchy bene felicemente l\'intero secchio prima di mettere la testa su di te. Ha placato la sua sete, ma sta tremando incontrollabile.', '', 37474),
(54130, 'itIT', '', 'Rell è... è gravemente ferito.$b$b<L\'Agente Kearnen sbatte i pugni a terra.>$b$bSta morendo, $n! E non possiamo farci nulla. Maledizione!', 37474),
(204749, 'itIT', 'Le narici della Defunta Blanchy si infiammano mentre odora le mele. Le sgranocchia volentieri. Blanchy sospira contenta e mette la testa sulla tua spalla.', '', 37474),
(121283, 'itIT', 'Amber Kearnen era una dei nostri... Non riesco a credere che abbiamo dovuto ucciderla.', '', 37474),
(197546, 'itIT', '', 'Basta! Le vostre spastiche giravolte mi stanno irritando.', 37474),
(207303, 'itIT', 'È un bene che ci aiuti a difendere la foresta, $gCacciatore:Cacciatrice;.', 'È un bene che ci aiuti a difendere la foresta, $gCacciatore:Cacciatrice;.', 37474),
(189107, 'itIT', 'Nonostante la nostra continua richiesta di abili incantatori, non abbiamo intenzione di ridurre i requisiti per entrare.', '', 37474);
INSERT INTO `faction_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2463, 'itIT', 'Marasmio', 'Un antico abitante di Selvarden con le sembianze di un fungo, Marasmio è parte integrante dell\'ecosistema del regno. La tua disponibilità ad aiutarlo ha addolcito qualsiasi parte in lui si possa chiamare cuore, e c\'è sempre molto lavoro da fare.', 37474),
(2410, 'itIT', 'L\'Armata Imperitura', 'Molto tempo fa, il Primus ha fondato le cinque Casate di Maldraxxus al fine di costruire l\'esercito di combattimento definitivo. Con il potere, l\'intelligenza e l\'astuzia, quest\'armata inflessibile ha il compito a difendere le Terretetre contro ogni minaccia.', 37474),
(2465, 'itIT', 'La Caccia Selvaggia', 'Richiamata a servire la Regina dell\'Inverno in tempi di estrema necessità, la Caccia Selvaggia riunisce i migliori Cacciatori e le migliori Cacciatrici di tutta Selvarden. Di fronte alla siccità e ai pericoli, la Caccia Selvaggia è pronta a difendere la foresta.', 37474),
(2462, 'itIT', 'Maestri delle Suture', 'I Maestri delle Suture sono rinomati per la loro capacità di fondere carne, ossa e Animum in costrutti che li rendono dei potenti alleati da avere a Maldraxxus, e si sono stabiliti nell\'Isolato dei Macellai dopo essersi separati dai traditori della Casata dei Costrutti.', 37474),
(2464, 'itIT', 'Corte della Notte', 'L\'attenzione della Regina dell\'Inverno e della Caccia Selvaggia è concentrata sulla siccità d\'Animum e sull\'invasione dei Drust, lasciando al Droman della Corte della Notte il compito di proteggere i Semebrado e difendere la foresta.', 37474);
