INSERT INTO `mail_template_locale` (`ID`, `locale`, `Body_lang`, `VerifiedBuild`) VALUES
(568, 'itIT', 'A $p,\n\nnon contesterò l\'accuratezza del conteggio delle tue uccisioni, come riportato nella tua lettera precedente.\n\nAnche se l\'ho trovato sospettosamente alto...\n\nIn ogni caso, mi piacciono le nostre gare e cercherò comunque di stare al tuo passo.\n\nHo registrato centottantatre colpi letali da quando ho ricevuto la tua ultima lettera, e...\n\n<La scrittura dal Capitano della Caccia Korayn esce dalla pagina.>\n\nFanno centocinquantadue uccisioni! Alcuni in quell\'ultima ondata erano acari, ma non posso fare lo schizzinoso, quando tu sei così palesemente in testa!\n\nAdesso tocca a te,\nCapitano della Caccia Korayn', 37474);
INSERT INTO `chr_classes_locale` (`ID`, `locale`, `Name_lang`, `NameMale_lang`, `NameFemale_lang`, `Description_lang`, `RoleInfoString_lang`, `DisabledString_lang`, `HyphenatedNameMale_lang`, `HyphenatedNameFemale_lang`, `VerifiedBuild`) VALUES
(3, 'itIT', 'Cacciatore', 'Cacciatore', 'Cacciatrice', 'I Cacciatori sono a loro agio nelle terre selvagge e hanno un\'affinità particolare con le bestie. Infliggono danni utilizzando le armi e i propri famigli.', 'Ruoli: |cffffffffAssaltatore a distanza o assaltatore in mischia|r', 'Devi scegliere una razza diversa\nper giocare questa classe.', 'Cacciatore', 'Cacciatrice', 37474),
(1, 'itIT', 'Guerriero', 'Guerriero', 'Guerriera', 'I Guerrieri sono combattenti protetti da armature a piastre che aspirano alla perfezione nell\'arte bellica. Quando un Guerriero infligge danni genera rabbia, risorsa che gli permette di eseguire attacchi speciali.', 'Ruoli: |cffffffffDifensore o assaltatore in mischia|r', 'Devi scegliere una razza diversa\nper giocare questa classe.', 'Guerriero', 'Guerriera', 37474),
(11, 'itIT', 'Druido', 'Druido', 'Druida', 'I Druidi sono mutaforma con una grande affinità per il regno animale e quello vegetale. Possono specializzarsi in cure, nel lancio di incantesimi a distanza o nella trasformazione in orsi o felini per il combattimento in mischia.', 'Ruoli: |cffffffffDifensore, guaritore, assaltatore a distanza o assaltatore in mischia|r', 'Devi scegliere una razza diversa\nper giocare questa classe.', 'Druido', 'Druida', 37474),
(5, 'itIT', 'Sacerdote', 'Sacerdote', 'Sacerdotessa', 'I Sacerdoti sono guaritori sacri molto versatili, capaci però di abbandonare questo ruolo e dedicarsi alla magia dell\'ombra per infliggere danni ai nemici.', 'Ruoli: |cffffffffGuaritore o assaltatore a distanza|r', 'Devi scegliere una razza diversa\nper giocare questa classe.', 'Sacerdote', 'Sacerdo|ntessa', 37474),
(8, 'itIT', 'Mago', 'Mago', 'Maga', 'I Maghi sono i classici fruitori della magia di Azeroth e raggiungono la perfezione nella loro arte grazie a un intenso percorso di studio e ricerca. Compensano la loro armatura leggera con potenti incantesimi offensivi e difensivi.', 'Ruolo: |cffffffffAssaltatore a distanza|r', 'Devi scegliere una razza diversa\nper giocare questa classe.', 'Mago', 'Maga', 37474),
(7, 'itIT', 'Sciamano', 'Sciamano', 'Sciamana', 'Gli Sciamani utilizzano il potere degli elementi per potenziare i danni delle loro armi e i loro incantesimi. Possono evocare dei totem, piccoli oggetti che inabilitano i nemici, infliggono danni o conferiscono benefici e cure.', 'Ruoli: |cffffffffGuaritore, assaltatore a distanza o assaltatore in mischia|r', 'Devi scegliere una razza diversa\nper giocare questa classe.', 'Sciamano', 'Sciamana', 37474),
(12, 'itIT', 'Cacciatore di Demoni', 'Cacciatore di Demoni', 'Cacciatrice di Demoni', 'I Cacciatori di Demoni sono una classe eroica, perciò iniziano da un livello più alto. Sono esiliati oscuri che fanno uso della proibita magia demoniaca per cacciare e uccidere i propri nemici.', 'Ruoli: |cffffffffDifensore o assaltatore in mischia|r', 'Richiede Legion e un personaggio di livello 70.', 'Cacciatore di Demoni', 'Cacciatrice di Demoni', 37474),
(9, 'itIT', 'Stregone', 'Stregone', 'Strega', 'Gli Stregoni infliggono danni utilizzando la magia del fuoco e dell\'ombra, possono maledire i nemici, risucchiare le anime delle loro vittime ed evocare servitori demoniaci.', 'Ruolo: |cffffffffAssaltatore a distanza|r', 'Devi scegliere una razza diversa\nper giocare questa classe.', 'Stregone', 'Strega', 37474),
(4, 'itIT', 'Ladro', 'Ladro', 'Ladra', 'I Ladri sono spesso impiegati come assassini o esploratori, anche se in genere sono tipi piuttosto solitari. Possono aggirare silenziosamente i propri nemici o attaccarli alle spalle, cercando di ucciderli rapidamente.', 'Ruolo: |cffffffffAssaltatore in mischia|r', 'Devi scegliere una razza diversa\nper giocare questa classe.', 'Ladro', 'Ladra', 37474);
