INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(187442, 'ptBR', '', 'Seus pecados são muitos. Mas nenhum é tão cruel quanto soltar feras em gente inocente.', 37474),
(38124, 'ptBR', 'Entreguem-se à escuridão... à eterna escuridão.', 'Entreguem-se à escuridão... à eterna escuridão.', 37474),
(180366, 'ptBR', '', 'É um prazer receber você no Bastião! Que coisa... você não se parece com nenhuma alma que eu já vi. Enfim.', 37474),
(187280, 'ptBR', 'Com a Casa dos Olhos eliminada, nosso segredo está bem guardado. Não restou ninguém para impedir nosso trabalho.', '', 37474),
(185904, 'ptBR', 'Ciúmes? Por favor. Eu era mil vezes superior a ele!', '', 37474),
(182733, 'ptBR', 'A Marca do Conhecimento. Um farol contra ignorância e medo.', '', 37474),
(180367, 'ptBR', '', 'Você fará parte dos kyrianos! Nosso dever sagrado é levar as almas mortais diante da Juíza para serem julgadas.', 37474),
(194383, 'ptBR', '', 'Isso vai ser interessante.', 37474),
(182626, 'ptBR', '', 'Quando estiver tudo pronto, aproxime-se da chama e siga as instruções do Vigia.', 37474),
(202313, 'ptBR', '', 'Me deixe talhar, fatiar e picar todos! Dissecar vivos! Cortar em tirinhas a carne...', 37474),
(202311, 'ptBR', 'Hum, é uma ideia a se pensar... Podemos esfolar essas criaturas petulantes de mente miúda e pavimentar com seus ossos a estrada da nossa ascensão. Ó, dúvida crueeel...', '', 37474),
(202310, 'ptBR', '', 'Eu tenho uma ideia melhor, Mestre! Deixe que eu talho eles!', 37474),
(202312, 'ptBR', '', 'Por favooor, Meeestre! Faz tanto tempo que eu não corto a carne viva!', 37474),
(202315, 'ptBR', 'Eu auxiliarei as visitas na sua penitência. Uma vez que tiverem se arrependido, eu permitirei que você rasgue os corpos e mande as almas aos berros Gorja adentro!', '', 37474),
(205464, 'ptBR', '', 'Agora entendo por que criaturas do seu mundo são consideradas tão formidáveis. Não é de surpreender que você tenha conseguido repelir a Legião Ardente. Agradeço pela sua força, em nome de toda Ardena.', 37474),
(191962, 'ptBR', '<Na base da estátua foi gravado um texto.>\n\nO respeito se conquista com provas de poder. Prestamos Continência àqueles que se sobressaem às massas.', '', 37474),
(191966, 'ptBR', '<Na base da estátua foi gravado um texto.>\n\nVerdades se ocultam na escuridão. Os hábeis passam de Fininho entre os inimigos.', '', 37474),
(180775, 'ptBR', 'Preciso alertar... o droman...', 'Preciso alertar... o droman...', 37474),
(193577, 'ptBR', '', 'Segura aqui um momento.', 37474),
(186290, 'ptBR', 'Elegante e poderosa. A coroa que defenderá um reino.', '', 37474),
(193598, 'ptBR', '', 'Hum... Onde eu vou botar você?', 37474),
(193582, 'ptBR', '', 'Quer testar esse aqui?', 37474),
(203409, 'ptBR', 'Uma arma... forjada nas chamas da Thanator. Nasce uma lâmina do lamento.', '', 37474),
(196707, 'ptBR', 'Dispare quando puder!', '', 37474),
(199409, 'ptBR', 'Você quer minha ânima? Por que os tirnenns não cuidam disso?', '', 37474),
(199444, 'ptBR', 'Ali. Vamos acabar com isso!', '', 37474),
(198956, 'ptBR', 'Não tenho tempo para isso, $c, a Condessa está esperando.', 'Não tenho tempo para isso, $c, a Condessa está esperando.', 37474),
(206412, 'ptBR', '', 'O que $Uo:a; traz a Maldraxxus, $p?\n\nMais cogumelos para os festins da corte, ou mais soldados de Maldraxxus para defender suas muralhas?\n\n<A Baronesa Vashj sorri.>\n\nEu confesso que estou gostando da Corte das Brasas bem mais do que esperava.\n\nO Príncipe Renathal assume o papel de anfitrião no palco principal, mas eu venho observando você.\n\nÉ você quem faz as coisas acontecerem por trás dos bastidores, igualzinho a mim nos tempos da corte da minha Rainha.\n\n<A Baronesa Vashj fita o horizonte por um momento.>\n\nNão é fácil, o que você faz.', 37474),
(202361, 'ptBR', 'Os kyrianos são bem-vindos no Arquivo Eônico, onde o influxo de ânima multiplica nossas capacidades.\n\nEu ofereço o seguinte benefício:\n\n|TInterface\\ICONS\\Achievement_Raid_Thunder_King.blp:20|t|cFFFF0000|Hspell:335576|h[Estrela Guia do Ascendente]|h|r: acima de 90% dos pontos de vida, seus feitiços e habilidades que causam dano têm chance de lançar Marcado para Coleta no alvo, causando dano ao longo do tempo. Ao derrotar um inimigo durante esse intervalo, a ânima dele será recolhida, protegendo você por um curto intervalo.', 'Os kyrianos são bem-vindos no Arquivo Eônico, onde o influxo de ânima multiplica nossas capacidades.\n\nEu ofereço o seguinte benefício:\n\n|TInterface\\ICONS\\Achievement_Raid_Thunder_King.blp:20|t|cFFFF0000|Hspell:335576|h[Estrela Guia do Ascendente]|h|r: acima de 90% dos pontos de vida, seus feitiços e habilidades que causam dano têm chance de lançar Marcado para Coleta no alvo, causando dano ao longo do tempo. Ao derrotar um inimigo durante esse intervalo, a ânima dele será recolhida, protegendo você por um curto intervalo.', 37474),
(199451, 'ptBR', '', 'Ha... Bom, uma vez com a Tece-sonhos...', 37474),
(183994, 'ptBR', '', 'Pra esquerda!', 37474),
(183995, 'ptBR', '', 'Esquerda! Direita! A outra direita!', 37474),
(183993, 'ptBR', 'Consegue chegar no topo?', '', 37474),
(204708, 'ptBR', 'Obediente, Brancurinha Defunta levanta a pata. Parece feliz em deixar que você trate seus cascos feridos. Sua respiração está entrecortada, e ela parece estar com sede.', '', 37474),
(204686, 'ptBR', 'Com a escova, você limpa a pelagem de Brancurinha Defunta. Ela estremece e dá uma cabeçadinha em você. Quando ela pisoteia, você nota que seus cascos estão partidos.', '', 37474),
(204663, 'ptBR', 'Brancurinha Defunta aceita a aveia com avidez. Embora ainda restem alguns pelos, sua pele está cheia de espinhos, lodo e picadas de ácaro, e muito irritada.', '', 37474),
(204710, 'ptBR', 'Brancurinha Defunta fica imóvel enquanto você a cobre com a manta de sela. O tremor vai diminuindo até parar. Bem melhor, Brancurinha mete o focinho na sua bolsa em busca de um petisco.', '', 37474),
(204668, 'ptBR', 'Brancurinha Defunta estaca de repente para não atropelar você.', '', 37474),
(204903, 'ptBR', 'Brancurinha Defunta traz um olhar alucinado e um aspecto abatido. Ela está faminta, precisando de uma refeição farta.', '', 37474),
(121956, 'ptBR', 'Eu, mestre Mathias Shaw, por meio deste, autorizo a Lâmina Vermelha a caçar a agente dupla Amber Kearnen.', '', 37474),
(204709, 'ptBR', 'Brancurinha Defunta sorve a água inteira com avidez e agradece com uma cabeçadinha. Apesar de ter matado a sede, você nota que ela está tiritando incontrolavelmente.', '', 37474),
(54130, 'ptBR', '', 'Rell está... ele está gravemente ferido.$b$b<A agente Marta Hari bate com o punho no chão.>$b$bEle está morrendo, $n! E não há nada que possamos fazer!', 37474),
(204749, 'ptBR', 'Brancurinha Defunta infla as narinas ao sentir o cheiro das maçãs. Ela as come com vontade, depois suspira, contente, e põe a cabeça no seu ombro.', '', 37474),
(121283, 'ptBR', 'Amber Kearnen era uma das nossas... ainda não acredito que tivemos que matá-la.', '', 37474),
(197546, 'ptBR', '', 'Chega! Essas piruetas bisonhas me dão nojo.', 37474),
(207303, 'ptBR', 'Ainda bem que você nos ajuda a defender a floresta, guarda-caça.', 'Ainda bem que você nos ajuda a defender a floresta, guarda-caça.', 37474),
(189107, 'ptBR', 'Apesar de precisarmos de lançadores competentes, não vamos baixar os requisitos para a admissão.', '', 37474);
INSERT INTO `faction_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2463, 'ptBR', 'Marasmius', 'Um habitante fúngico impossivelmente antigo de Ardena, Marasmius é parte integral do ecossistema do reino. Sua disposição em ajudar amoleceu qualquer que seja a parte dele que serve de coração, e há sempre muito trabalho a ser feito.', 37474),
(2410, 'ptBR', 'O Exército Imorredouro', 'Há muito tempo, o Prócer estabeleceu as cinco casas de Maldraxxus para criar as forças armadas supremas. Por meio do poder, inteligência e astúcia, essa força implacável jurou defender as Terras Sombrias contra todas as ameaças.', 37474),
(2465, 'ptBR', 'A Caçada Selvagem', 'Convocada para servir à Rainha Invernal em momentos difíceis, a Caçada Selvagem reúne os melhores caçadores e caçadoras de toda Ardena. Diante da seca e invasões, a Caçada Selvagem está pronta para defender a floresta.', 37474),
(2462, 'ptBR', 'Mestres-das-suturas', 'Os Mestres-das-suturas são conhecidos pela habilidade de fundir carne, osso e ânima em constructos, sendo assim poderosos aliados em Maldraxxus. Eles se estabeleceram no Bloco dos Açougueiros depois de abandonar a traiçoeira Casa dos Constructos.', 37474),
(2464, 'ptBR', 'Corte da Noite', 'A seca de ânima e a invasão drusta consomem a atenção da Rainha Invernal e da Caçada Selvagem, deixando aos Dromans da Corte da Noite a tarefa de proteger as sementes selvagens e defender os bosques.', 37474);
