INSERT INTO `mail_template_locale` (`ID`, `locale`, `Body_lang`, `VerifiedBuild`) VALUES
(568, 'ptBR', 'Para $p:\n\nNão vou contestar a precisão da contagem de abates que você relatou na carta anterior.\n\nPorém, admito, acho que ela está suspeitosamente alta. Bem, ser Caminhante da Gorja tem dessas coisas...\n\nAinda assim, gosto das suas competições e vou tentar acompanhar.\n\nRegistrei 138 golpes finais desde que recebi sua última carta, além de...\n\n<A escrita da Capitã de Caça Korayn começa a sair da página.>\n\nCom isso, são 152 abates! Alguns da última leva foram de ácaros, mas eu não posso ficar escolhendo demais com você tão na frente!\n\nSua vez.\nCapitã de Caça Korayn', 37474);
INSERT INTO `chr_classes_locale` (`ID`, `locale`, `Name_lang`, `NameMale_lang`, `NameFemale_lang`, `Description_lang`, `RoleInfoString_lang`, `DisabledString_lang`, `HyphenatedNameMale_lang`, `HyphenatedNameFemale_lang`, `VerifiedBuild`) VALUES
(3, 'ptBR', 'Caçador', 'Caçador', 'Caçadora', 'Caçadores estão em casa na natureza e têm uma afinidade especial com as feras. Eles contam principalmente com suas armas e seus ajudantes para causar dano.', 'Funções: |cffffffffdano de longo alcance ou dano corpo a corpo|r', 'É preciso escolher outra raça se optar por esta classe.', 'Caçador', 'Caçadora', 37474),
(1, 'ptBR', 'Guerreiro', 'Guerreiro', 'Guerreira', 'Guerreiros são lutadores de armadura que aspiram à perfeição no combate armado. À medida que lutam, os guerreiros geram raiva, que é usada em ataques especiais.', 'Funções: |cfffffffftanque ou dano corpo a corpo|r', 'É preciso escolher outra raça se optar por esta classe.', 'Guerreiro', 'Guerreira', 37474),
(11, 'ptBR', 'Druida', 'Druida', 'Druidesa', 'Druidas são metamorfos com uma afinidade pela fauna e flora. Eles podem se especializar em curar, lançar feitiços à distância ou assumir a forma de um felino ou urso em lutas corpo a corpo.', 'Funções: |cfffffffftanque, curador, dano de longo alcance ou dano corpo a corpo|r', 'É preciso escolher outra raça se optar por esta classe.', 'Druida', 'Druidesa', 37474),
(5, 'ptBR', 'Sacerdote', 'Sacerdote', 'Sacerdotisa', 'Sacerdotes são curandeiros completos com uma variedade de ferramentas. Contudo, eles também podem se sacrificar para causar dano com magia sombria.', 'Funções: |cffffffffcurador ou dano de longo alcance|r', 'É preciso escolher outra raça se optar por esta classe.', 'Sacerdote', 'Sacerdotisa', 37474),
(8, 'ptBR', 'Mago', 'Mago', 'Maga', 'Magos são os emblemáticos praticantes de magia de Azeroth, que aprendem sua arte por meio de pesquisa e estudo intensivos. Eles compensam a armadura leve com uma poderosa seleção de feitiços de ataque e defesa.', 'Função: |cffffffffdano de longo alcance|r', 'É preciso escolher outra raça se optar por esta classe.', 'Mago', 'Maga', 37474),
(7, 'ptBR', 'Xamã', 'Xamã', 'Xamã', 'Xamãs usam o poder dos elementos para melhorar o dano da arma e seus feitiços. Eles evocam totens em combate, pequenos objetos que incapacitam inimigos, curam ou causam dano aos inimigos.', 'Funções: |cffffffffcurador, dano de longo alcance ou dano corpo a corpo|r', 'É preciso escolher outra raça se optar por esta classe.', 'Xamã', 'Xamã', 37474),
(12, 'ptBR', 'Caçador de Demônios', 'Caçador de Demônios', 'Caçadora de Demônios', 'Caçadores de Demônios são uma classe heroica, o que significa que começam em um nível alto. Eles são párias que usam magia demoníaca proibida para caçar e matar seus inimigos.', 'Funções: |cfffffffftanque ou dano corpo a corpo|r', 'Requer a expansão Legion e um personagem no nível 70.', 'Caçador de Demônios', 'Caçadora de Demônios', 37474),
(9, 'ptBR', 'Bruxo', 'Bruxo', 'Bruxa', 'Bruxos lançam magias de fogo e das sombras para causar dano, drenar ou amaldiçoar seus inimigos. Eles evocam demônios como servos.', 'Função: |cffffffffdano de longo alcance|r', 'É preciso escolher outra raça se optar por esta classe.', 'Bruxo', 'Bruxa', 37474),
(4, 'ptBR', 'Ladino', 'Ladino', 'Ladina', 'Ladinos geralmente atuam como assassinos ou batedores, apesar de muitos agirem sozinhos. Eles espreitam inimigos ou atacam um adversário por trás para tentar eliminá-lo rapidamente.', 'Função: |cffffffffdano corpo a corpo|r', 'É preciso escolher outra raça se optar por esta classe.', 'Ladino', 'Ladina', 37474);
