INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(204303, 'deDE', 'Ihr habt es geschafft! Ich habe natrlich nie an Euch gezweifelt.', '', 38134),
(204302, 'deDE', 'Ihr msst vielleicht Kpfchen beweisen, um ihn zu erreichen.', '', 38134),
(204301, 'deDE', 'Klettert auf diese Regale, um zum Schalter zu gelangen.', '', 38134),
(204300, 'deDE', 'Der Schalter ist am Ende des Korridors.', '', 38134),
(204299, 'deDE', 'Eine Hand? Ja... ja! Beim Primus, das ist genial!', '', 38134),
(204298, 'deDE', 'Eine Fackel? Auch wenn viele Missetter auf diese Art Schalter verbergen, bezweifle ich das in einer Zelle doch sehr.', '', 38134),
(204297, 'deDE', 'Wollt Ihr mit jemandem sprechen, der auf Eurem Niveau ist? Ansonsten hilft uns ein Schdel nicht weiter.', '', 38134),
(204296, 'deDE', 'Meine Magie kann leider keine Ketten manipulieren. Seht, was Ihr sonst noch auftreiben knnt.', '', 38134),
(204295, 'deDE', 'Wachen! Werft diesen Wicht in eine Zelle! Und bringt mir den verrterischen Lich!', '', 38134),
(204294, 'deDE', 'Also steckt Ihr dahinter! Und was haben wir hier... Balmedars Oculus!', '', 38134),
(204293, 'deDE', 'Unmglich! Wartet... was ist das? Verrat!', '', 38134),
(204292, 'deDE', 'Was... was geschieht hier? Neeein!', '', 38134),
(204291, 'deDE', 'Das Blut des Trgers wird den Weg ffnen!', '', 38134),
(204290, 'deDE', 'Die Barriere wird schwcher! Lngst versiegelte Pfade ffnen sich!', '', 38134),
(204289, 'deDE', 'Ja! Ich sehe den Weg! Die Finsternis des Schlunds ruft nach mir!', '', 38134),
(204288, 'deDE', 'Das Ritual beginnt! ffnet den Animazufluss!', '', 38134),
(204287, 'deDE', 'Hoffen wir um Euretwillen, dass sie nicht tdlich sind.', '', 38134),
(204286, 'deDE', 'Auch wenn das Ritual gewiss fehlschlgt, knnte es einige... Kollateralschden geben.', '', 38134),
(204285, 'deDE', 'Es war eigentlich nicht vorgesehen, dass Ihr am Ritual teilnehmt. Aber wir werden improvisieren.', '', 38134),
(204284, 'deDE', 'Wie Ihr befehlt, Meister. Es wird sofort geschehen!', '', 38134),
(204283, 'deDE', 'Dann haben wir einen Verrter unter uns. Wir mssen unsere Plne beschleunigen.', '', 38134),
(204282, 'deDE', 'Es war nirgendwo zu sehen, Meister.', '', 38134),
(204281, 'deDE', 'Verschwendet meine Zeit nicht mit Lakaien! Was ist mit dem Reagenz?', '', 38134),
(204280, 'deDE', '...die Karawane wurde vollstndig geplndert, die Mittlereskorte brutal gettet.', '', 38134),
(204279, 'deDE', 'Kommt mit den brigen Reagenzien zu mir. Ich habe einen Plan.', '', 38134),
(204278, 'deDE', 'Eine mysterise Lieferung der Mittler? Sehr faszinierend. Ich muss davon wissen!', '', 38134),
(204277, 'deDE', 'Was der Baron auch anfordert, bringt es erst zu mir. Ich mchte sehen, was er benutzt.', '', 38134),
(204276, 'deDE', 'Vielleicht helfen uns einige dieser Reagenzien, auf den Typ des Rituals zu schlieen.', '', 38134),
(204275, 'deDE', 'Haltet an Eurer List fest. Mal sehen, was wir sonst noch aufdecken.', '', 38134),
(204274, 'deDE', 'Trotz unserer Mhen haben wir nichts ber die Plne des Barons herausgefunden.', '', 38134),
(204273, 'deDE', 'Was knnte Kel\'Thuzad damit nur vorhaben?', '', 38134),
(204272, 'deDE', '\"Die Bindung des Verbannten\"? Das ist nun wirklich kaum mehr als ein Geschichtsbuch.', '', 38134),
(204270, 'deDE', 'Was fr eine Barriere mchte der Baron wohl errichten... oder umgehen? Seltsam.', '', 38134),
(204268, 'deDE', '\"Grere Schutze und Barrieren, Band IV: Gegenzauber\". Keine leichte Lektre.', '', 38134),
(204266, 'deDE', 'Hat sein Ritual etwas mit den Pfaden zu tun, die die Reiche des Todes verbinden?', '', 38134),
(204265, 'deDE', '\"Pfade der Ersten\"? Eine seltsame Wahl voller Spekulationen und Halbwahrheiten.', '', 38134),
(204264, 'deDE', 'Was erwartet er in einem solchen Standardwerk zu finden?', '', 38134),
(204263, 'deDE', '\"Geflgeltes Arkanum\". Eine logische Wahl fr Forschung zu kyrianischer Mystik.', '', 38134),
(204261, 'deDE', 'Ich verstehe, was den Verschmhten entgeht.\n\nPerfektion ist fr den Erfolg unerlsslich! Ihr msst Eure Werkzeuge und Ressourcen pflegen.', '', 38134),
(204259, 'deDE', 'Allerdings kaum ein Beweis fr Verrat. Werfen wir einen Blick in die Folianten, die er sucht.', '', 38134),
(204258, 'deDE', 'Der Baron erbittet also Hilfe von den Venthyr? Nicht unverzeihlich, wenn er wenigstens seine Beute teilt.', '', 38134),
(204257, 'deDE', 'Genug der Grbelei. Es wird Zeit, das Ritual vorzubereiten.', '', 38134),
(204256, 'deDE', 'Und dank der Anima, die unsere Verbndeten in Revendreth sichergestellt haben, werde ich den Weg zum Sieg ffnen.', '', 38134),
(204255, 'deDE', 'Dieser Kyrianer trgt den Schlssel zu mchtigen Orten in sich.', '', 38134),
(204254, 'deDE', 'Dafr bezahlt Ihr, Lich! Ich bin niemandes Belohnung!', '', 38134),
(204253, 'deDE', 'Darunter ist auch meine Belohnung, ein Kyrianer, der bei ihnen \"Trger\" genannt wird.', '', 38134),
(204252, 'deDE', 'Die Konstrukte sind mit der brigen Beute unseres berfalls auf die Bastion zurck.', '', 38134),
(204251, 'deDE', 'Aber daran wrdet Ihr nicht einmal denken... nicht wahr?', '', 38134),
(204250, 'deDE', 'Falls Ihr vorhaben solltet, mich zu hintergehen, werde ich dafr sorgen, dass die Markgrfin die Identitt des Mrders erfhrt.', '', 38134),
(204249, 'deDE', 'Eine kleine Anmerkung: Der Akolyth, den Ihr so mutwillig erschlagen habt, war auch der Lieblingsgefhrte von Markgrfin Sin\'dane.', '', 38134),
(204248, 'deDE', 'Zufriedenstellend. Kommt in die Halle der Klingen, dort berprfe ich die Qualitt Eurer Verkleidung.', '', 38134),
(204247, 'deDE', 'Die nchste Aufgabe wird schwerer. Ihr mgt doch Herausforderungen?', '', 38134),
(204246, 'deDE', 'Gut, Ihr habt mein kleines \"Hilfspaket\" gefunden. Dann seid Ihr nicht vllig nutzlos.', '', 38134),
(204245, 'deDE', 'Erzhlt niemandem von dieser Absprache.', '', 38134),
(204244, 'deDE', 'Der Turm steht nahe dem Skapliagrat. Dort findet Ihr alles Ntige, um weiterzukommen.', '', 38134),
(204242, 'deDE', 'Ich frage mich, fr wen wir spielen werden?', '', 38134),
(204241, 'deDE', '', 'Mit diesen Instrumenten fhle ich mich wieder wie ich selbst...', 38134),
(204240, 'deDE', 'Endlich!', '', 38134),
(204239, 'deDE', 'Genug der Besinnung. Die Proben knnen beginnen!', '', 38134),
(204238, 'deDE', 'Gute Neuigkeiten, Leute! Ich habe unser nchstes Konzert gebucht... und das wird GROSS!', '', 38134),
(204237, 'deDE', '', 'In meinem sterblichen Leben konnte ich nie lange an einem Ort bleiben.\n\nJeden Tag wartete auf mich ein neues Abenteuer, das ich erleben wollte.\n\nDie gleiche Reiselust spre ich bei Euch.\n\nAlso sagt mir ehrlich... Glaubt Ihr an die Theorie von Verwalter Tedo? Kann Musik die Seele retten?\n\nIch bin mir da noch nicht sicher.', 38134),
(204236, 'deDE', 'Ansel macht sich zu viele Sorgen. Es wird alles gut.\n\nWie meine Asha zu sagen pflegte: \"Hinter jeder berwundenen Dnenkuppe bietet sich die Mglichkeit auf Wasser\".\n\nDie Musik wird uns retten. Das war schon immer so.', '', 38134),
(204235, 'deDE', 'Habt Ihr schon mal davon gehrt, dass Musik Seelennahrung sein soll?\n\nDas ist natrlich eine Metapher...\n\n<Verwalter Tedo schaut Euch fest an.>\n\nAber was, wenn es mehr als das wre?\n\nWas, wenn es fr einige, die sich in ihrem sterblichen Leben zur Musik hingezogen fhlten, eine Erlsung sein knnte?\n\nWas, wenn Musik wirklich die Seele heilen kann?\n\nIch glaube fest daran, dass das wahr ist.', '', 38134),
(204233, 'deDE', '', 'Jetzt, wo das Konservatorium etwas weiter wiederhergestellt wurde, habt Ihr Zugriff auf weitere Wildsamen und die Katalysatorfelder.\n\nMithilfe der Katalysatorfelder knnt Ihr die Wildsamen noch weiter strken, bevor Ihr darin einen Geist platziert.\n\nWenn Ihr Katalysatoren pflanzt, seht Ihr, mit welchen Wildsamen sie verbunden sind.', 38134),
(204232, 'deDE', 'Wie sollen wir unsere Betreuer mit Musik beeindrucken, wenn wir immer unsere Instrumente verlieren?\n\n<Glckspilz Ansel schaut auf die Sndensteine drauen.>\n\nWir kommen hier nie wieder raus.', '', 38134),
(204231, 'deDE', '', 'Zurzeit gibt es nur zwei Wildsamen, die wiederhergestellt wurden. Alles, was ihr momentan also tun knnt, ist, sie zu verwenden, um die Geister wieder mit Strke zu versehen.\n\nJe weiter Ihr dabei vorankommt, diesem Garten zu einstiger Pracht zu verhelfen, desto mehr Potential wird aktiviert.\n\nFragt mich noch einmal, wenn Ihr das Konservatorium weiter wiederhergestellt habt.', 38134),
(204229, 'deDE', '', 'Willkommen, $n. Wie kann ich Euch heute behilflich sein?', 38134),
(204228, 'deDE', '', 'Willkommen, $n. Wenn Ihr es wiederhergestellt habt, knnt Ihr das Konservatorium der Knigin durch dieses Pilzportal betreten.\n\nSprecht mit Zayhad, um alle Dinge zu sehen, die wir fr die Wiederherstellung bentigen.', 38134),
(204227, 'deDE', 'Weimhne wird Euch von dem finsteren Schicksal unserer Verbndeten erzhlen.', '', 38134),
(204226, 'deDE', 'Nazgrim wird Euch von dem finsteren Schicksal unserer Verbndeten erzhlen.', '', 38134),
(204225, 'deDE', 'Hochlord Bolvar Fordragon hat sich von seinem Kampf mit Sylvanas Windlufer erholt. Er und seine Verbndeten erwarten uns.', '', 38134),
(204224, 'deDE', 'Wir haben angeordnet, dass Acherus vor die Eiskronenzitadelle verlegt wird. Lasst mich ein Schwarzes Tor fr uns ffnen.', '', 38134),
(204223, 'deDE', 'Die Vorbereitungen wurden getroffen.', '', 38134),
(204222, 'deDE', 'Die Schwarze Klinge steht bereit. Ich erwarte Euch in Orgrimmar. Kommt, bevor die eisige Hand des Todes ganz Azeroth umklammert.', '', 38134),
(204221, 'deDE', 'Der Schleier zwischen unserer Welt und den Schattenlanden wurde zerrissen.', '', 38134),
(204220, 'deDE', 'Die Schwarze Klinge steht bereit. Ich erwarte Euch in Sturmwind. Kommt, bevor die eisige Hand des Todes ganz Azeroth umklammert.', '', 38134),
(204219, 'deDE', 'Der Schleier zwischen unserer Welt und den Schattenlanden wurde zerrissen.', '', 38134),
(204217, 'deDE', 'Schwcht ihn, damit ich sie mit dem Stab befreien kann!', '', 38134),
(204216, 'deDE', 'Kel\'Thuzad entzieht Markgrfin Sin\'dane ihre Macht!', '', 38134),
(204215, 'deDE', 'Diener! Vernichtet die Eindringlinge!', '', 38134),
(204211, 'deDE', '<Die Truhe schwingt knarrend auf und enthllt verschiedene Ausstattungen und Accessoires.>', '', 38134),
(204210, 'deDE', '<Nherin Rogana inspiziert Euch eingehend.>\n\nWas fr... abenteuerliche Festkleidung.\n\nIch bin mir sicher, dass Ihr etwas Passenderes in meiner Truhe findet.', '<Nherin Rogana inspiziert Euch eingehend.>\n\nWas fr... abenteuerliche Festkleidung.\n\nIch bin mir sicher, dass Ihr etwas Passenderes in meiner Truhe findet.', 38134),
(204200, 'deDE', 'Fr $geinen Sterblichen:eine Sterbliche; seid Ihr sehr weise, $p.', 'Fr $geinen Sterblichen:eine Sterbliche; seid Ihr sehr weise, $p.', 38134),
(204199, 'deDE', 'Verkneift es Euch!', 'Verkneift es Euch!', 38134),
(204198, 'deDE', 'Manchmal glaube ich, Harlowe redet nur, um seine eigene Stimme zu hren!', 'Manchmal glaube ich, Harlowe redet nur, um seine eigene Stimme zu hren!', 38134),
(204197, 'deDE', 'Da habt Ihr wohl recht. Fredrik kann sein unglckliches Aussehen nicht leugnen.', 'Da habt Ihr wohl recht. Fredrik kann sein unglckliches Aussehen nicht leugnen.', 38134),
(204196, 'deDE', 'Fredrik ist nur neidisch, dass ich besser in dem Anzug aussehe als er. Das Leben nach dem Tod tut seiner Bauchgegend nicht gut.', 'Fredrik ist nur neidisch, dass ich besser in dem Anzug aussehe als er. Das Leben nach dem Tod tut seiner Bauchgegend nicht gut.', 38134),
(204194, 'deDE', 'Habt Ihr gesehen, was Harlowe trgt? Wie unoriginell. Ich sollte Ihm diese Klamotten glatt runterreien!', 'Habt Ihr gesehen, was Harlowe trgt? Wie unoriginell. Ich sollte Ihm diese Klamotten glatt runterreien!', 38134),
(204192, 'deDE', 'N\'Abend. Der neue Boss sagt mir, ich soll herkommen und rausfinden, was wir mit neu ankommender Anima machen knnen.\n\nKriegen wir genug davon, knnen wir die meisten Mauern, die von der Seite von Revendreth dauernd wegbrechen, wieder aufbauen.', '', 38134),
(204187, 'deDE', 'Was Ihr auch tut, Ihr zgert das Unvermeidliche nur hinaus.', '', 38134),
(204186, 'deDE', 'Ihr haltet Euch fr besonders talentiert. Euch werde ich noch Demut beibringen.', '', 38134),
(204185, 'deDE', 'Vyraz hat mich im Schlund in die Falle gelockt. Und zweifellos war es seine Hand, durch die unser Markgraf gefallen ist.', '', 38134),
(204182, 'deDE', 'So erschpfend vorhersehbar. Andererseits drfte Scheitern fr Euresgleichen zweite Natur sein.', '', 38134),
(204176, 'deDE', 'Ihr wollt fliegen? Ja?', 'Ihr wollt fliegen? Ja?', 38134),
(204175, 'deDE', 'Einigermaen beeindruckend. Dennoch, ich freue ich mich darauf, Euch auszulschen.', '', 38134),
(204174, 'deDE', '', 'Es wre Wahnsinn, ihnen jetzt zu folgen. Konzentriert Euch. Fr Theatralik ist spter Zeit.', 38134),
(204169, 'deDE', 'Ah ja, Ihr habt die Steinziegel. Was soll ich machen?', '', 38134),
(204164, 'deDE', 'Das Zerschmettern des Helms hat Bolvar Fordragon von der Brde, Lichknig zu sein, befreit. Jetzt geniet er dieselbe Freiheit wie wir.', '', 38134),
(204160, 'deDE', 'Ich glaube, unsere vorderen Tore brauchen eine neue Statue.\n\nWas meint Ihr, was fr eine?', '', 38134),
(204155, 'deDE', 'Bitte! Benutzt die Flte in der Nhe des Gormlingbaus, dann kommen sie aus ihrem Versteck!', '', 38134),
(204138, 'deDE', 'Mgen die Geister meiner Ahnen sicher in Frieden ruhen.', '', 38134),
(204137, 'deDE', 'Ich kenne nur die dunkelsten Tiefen der Schattenlande. Gut zu wissen, dass es hier auch hoffnungsvolle Orte gibt.', '', 38134),
(204133, 'deDE', '', 'Thrall, wisst Ihr, wohin sie Anduin gebracht haben?', 38134),
(204132, 'deDE', 'Meine Freunde!', '', 38134),
(204125, 'deDE', 'Man sagt, ein Boss is\' ein Boss wie jeder andere Boss, aber kein Boss is\' ein Boss, der weniger auf Peitschenhiebe aus ist als der letzte Boss.\n\nDas gilt aber nicht fr den Prinzen. Der is\' schon irgendwie in Ordnung, auch wenn er ein Blutsauger ist und so. Steht mit beiden Beinen auf\'m Boden.', '', 38134),
(204124, 'deDE', 'Ich habe im Laufe der onen auf vielen Schlachtfeldern in vielen Reichen gestanden.\n\nIn all diesen Konflikten hat sich nichts ehrenhafter angefhlt, als an der Seite von Prinz Renathal, General Draven und Euch zu dienen.\n\nWir kmpfen fr die Integritt unserer Realitt gegen unvorstellbare Krfte. Dafr wurde ich wahrhaftig geschaffen.', '', 38134),
(204123, 'deDE', 'Wenn es Prinz Renathal ernst damit ist, so viel verlorenes Territorium zurckzugewinnen, dann brauchen wir mehr Steingeborene, die sich fr seine Sache einsetzen.\n\nEs wre so viel einfacher gewesen, wenn er einfach den Steinmetz berzeugt htte, sich uns anzuschlieen. So, wie es jetzt aussieht, mssen wir nur bestimmte Steingeborene animieren, damit wir nicht noch mehr Krfte wecken, die unsere Feinde gegen uns einsetzen knnen.\n\nGlcklicherweise habt Ihr mich, um Euch zu sagen, welche von ihnen uns gegenber loyal sind.', 'Wenn es Prinz Renathal ernst damit ist, so viel verlorenes Territorium zurckzugewinnen, dann brauchen wir mehr Steingeborene, die sich fr seine Sache einsetzen.\n\nEs wre so viel einfacher gewesen, wenn er einfach den Steinmetz berzeugt htte, sich uns anzuschlieen. So, wie es jetzt aussieht, mssen wir nur bestimmte Steingeborene animieren, damit wir nicht noch mehr Krfte wecken, die unsere Feinde gegen uns einsetzen knnen.\n\nGlcklicherweise habt Ihr mich, um Euch zu sagen, welche von ihnen uns gegenber loyal sind.', 38134),
(204122, 'deDE', 'Demzufolge werde ich alle potenziellen Vergeltungsplne aus dem Protokoll streichen.', 'Demzufolge werde ich alle potenziellen Vergeltungsplne aus dem Protokoll streichen.', 38134),
(204114, 'deDE', 'Meinem Protokoll zufolge habt Ihr alle gestohlenen Andenken geborgen.', 'Meinem Protokoll zufolge habt Ihr alle gestohlenen Andenken geborgen.', 38134),
(204110, 'deDE', '<Das war kein Unfall!\n\nOberst Morts Verletzungen legen nahe, dass sein Tod schnell und schmerzhaft kam.\n\nKeine Anzeichen eines Kampfs, und keiner der Zeugen hat einen Attentter gesehen.\n\nWer auch immer hinter diesem grausamen Mord steckt, befindet sich noch unter den Gsten.>', '', 38134),
(204108, 'deDE', 'Markgraf Gharmal wird heute sterben.', '', 38134),
(204107, 'deDE', 'Viele Aspiranten, wenig Saft! Kommt spter wieder, ja?', 'Viele Aspiranten, wenig Saft! Kommt spter wieder, ja?', 38134),
(204104, 'deDE', 'Huh! Getrnk fr Euch! Nehmt! Nehmt!', 'Huh! Getrnk fr Euch! Nehmt! Nehmt!', 38134),
(204103, 'deDE', '<Der Provost bietet Euch sein Tablett mit einem sanften \"Huu\" an.>', '<Die Provostin bietet Euch ihr Tablet mit einem sanften \"Huu\" an.>', 38134),
(204100, 'deDE', 'Aspirant durstig? Provost zur Stelle!', 'Aspirant durstig? Provost zur Stelle!', 38134),
(204099, 'deDE', 'Trinken! Trinken! Trinken?', 'Trinken! Trinken! Trinken?', 38134),
(204097, 'deDE', 'Einen Schluck? Sehr lecker!', 'Einen Schluck? Sehr lecker!', 38134),
(204081, 'deDE', '', 'Guten Tag, $gmein Freund:meine Liebe;!\n\nEs gibt noch so viel zu tun.\n\nWenn Ihr etwas Zeit habt, wre Eure Untersttzung sehr willkommen.', 38134),
(204080, 'deDE', 'Wenn wir damit zu Markgrfin Sin\'dane zurckkehren, halten wir Kel\'Thuzad vielleicht noch auf.', '', 38134),
(204079, 'deDE', 'Moment... Der Stab des Primus! Er ist noch intakt!', '', 38134),
(204078, 'deDE', '', 'Geht zum Ring der bertragung und ffnet ein Portal zum Ardenwald. Mge die Vorsehung Euch leiten, sterbliche Seele.', 38134),
(204077, 'deDE', '', 'Die Haine des Ardenwalds bentigen Anima, um die schlummernden Naturgeister zu nhren. Ich hoffe, dass die Vorrte der Knigin die Drre berstanden haben.', 38134),
(204076, 'deDE', '', 'Die Winterknigin war eine enge Verbndete des Primus. Sie wird diese Warnung sicher beherzigen.', 38134),
(204075, 'deDE', 'Gemeinsam knnen wir tief ins Reich des Kerkermeisters blicken. Doch Vorsicht. Verliert Euch nicht im Dunkel.', '', 38134),
(204074, 'deDE', 'Im zerbrochenen Helm wohnt noch immer die Macht des Lichknigs. Die Macht... des Schlunds.', '', 38134),
(204073, 'deDE', '', 'Graf Denathrius herrscht ber Revendreth. Ich bete, dass er die Animadrre gut berstanden hat. Wir brauchen seine Hilfe dringend.', 38134),
(204072, 'deDE', '', 'Es ist das Ziel hochmtiger, arroganter Seelen, in denen die Richterin einen Funken Potenzial sieht. Die Venthyr fhren diese Seelen auf den Pfad zur Erlsung.', 38134),
(204071, 'deDE', '', 'Revendreth ist ein unheimliches Reich voll dsterer Trme und kaltem Stein. Aber Land und Bewohner dienen einem edlen Zweck.', 38134),
(204070, 'deDE', '', 'Ohne die Macht von Maldraxxus besteht fr uns leider keine groe Hoffnung, den Verbannten aufzuhalten.', 38134),
(204069, 'deDE', '', 'Ihr msst ihre Grnde fr den Angriff auf die Kyrianer erfahren. Verhindert um jeden Preis, dass dieser Konflikt weiter eskaliert.', 38134),
(204068, 'deDE', 'Sie hat nicht berlebt...', '', 38134),
(204067, 'deDE', '', 'Gweyir! Nein...', 38134),
(204066, 'deDE', '', 'Bleibt bei uns, Gweyir! Der Fluch ist fast gebrochen!', 38134),
(204065, 'deDE', '', 'Weiter so! Die Umklammerung der Drust lsst nach!', 38134),
(204064, 'deDE', '', 'Zerstrt diese Dinger, schnell!', 38134),
(204063, 'deDE', '', 'Macht Euch bereit. Gorak Zhars Magie lsst sich nicht einfach bereinigen.', 38134),
(204062, 'deDE', '', 'Ich brauche ein kleines, schnelles und leises Konstrukt.\n\nMeine Feinde sehen mich selten, aber sie frchten stets meine Prsenz.', 38134),
(204060, 'deDE', 'Ich wusste, Ihr wrdet meine Hilfe brauchen.', '', 38134),
(204059, 'deDE', 'Ich werde ein starkes Konstrukt mit Eisenhaut bentigen.\n\nDamit kann ich jedes Problem lsen, das Euch strt.', '', 38134),
(204058, 'deDE', 'Lord Garridan hat nicht bertrieben. Ihr veranstaltet einen wrdigen Hof!', 'Lord Garridan hat nicht bertrieben. Ihr veranstaltet einen wrdigen Hof!', 38134),
(204057, 'deDE', 'Wo ist Stefan der Whlerische? Er hat mir eine Privatverkostung versprochen.', 'Wo ist Stefan der Whlerische? Er hat mir eine Privatverkostung versprochen.', 38134),
(204056, 'deDE', 'Ich bin froh, dass Theotar aus dem Exil zurckgekehrt ist. Er ist so geistreich!', 'Ich bin froh, dass Theotar aus dem Exil zurckgekehrt ist. Er ist so geistreich!', 38134),
(204055, 'deDE', 'Habt Ihr irgendwo ein extragroes Getrnk herumstehen sehen?', 'Habt Ihr irgendwo ein extragroes Getrnk herumstehen sehen?', 38134),
(204054, 'deDE', 'Ich hoffe dieser Hof endet niemals!', 'Ich hoffe dieser Hof endet niemals!', 38134),
(204053, 'deDE', 'Ich hab mich an Eure Berhmtheit da drben herangemacht... Gern geschehen!', 'Ich hab mich an Eure Berhmtheit da drben herangemacht... Gern geschehen!', 38134),
(204052, 'deDE', 'Ich amsiere mich prchtig!', 'Ich amsiere mich prchtig!', 38134),
(204051, 'deDE', 'Ich muss zugeben, Ihr veranstaltet hier einen faszinierenden Hof.', 'Ich muss zugeben, Ihr veranstaltet hier einen faszinierenden Hof.', 38134),
(204014, 'deDE', 'Ich kann es kaum erwarten, herauszufinden, ob die Gerchte ber den Gluthof wahr sind!', 'Ich kann es kaum erwarten, herauszufinden, ob die Gerchte ber den Gluthof wahr sind!', 38134),
(204013, 'deDE', 'In Dunkelhafen werden ja alle so neidisch sein!', 'In Dunkelhafen werden ja alle so neidisch sein!', 38134),
(204012, 'deDE', 'Wie viele berhmte Leute ich heute wohl treffen werde?', 'Wie viele berhmte Leute ich heute wohl treffen werde?', 38134),
(204011, 'deDE', 'Ganz unter uns... Ich kann es kaum glauben, dass ich eine Einladung ergattern konnte... Ich bin ja so aufgeregt!', 'Ganz unter uns... Ich kann es kaum glauben, dass ich eine Einladung ergattern konnte... Ich bin ja so aufgeregt!', 38134),
(204010, 'deDE', 'Von diesem Hof wird die ganze Stadt sprechen!', 'Von diesem Hof wird die ganze Stadt sprechen!', 38134),
(204009, 'deDE', 'Was haltet Ihr von meinen Stiefeln? Ich habe fr diesen Anlass extra lange Dornen anbringen lassen!', 'Was haltet Ihr von meinem Kleid? Ich habe es mir extra fr diesen Anlass aufgehoben.', 38134),
(204008, 'deDE', 'Ich kann kaum erwarten, dass das Tor sich ffnet!', 'Ich kann kaum erwarten, dass das Tor sich ffnet!', 38134),
(204007, 'deDE', 'Ich bitte Euch um einen kleinen Tipp, $gSchlundgnger:Schlundgngerin;! Welche neuen, spannenden Dinge erwarten uns heute beim Hof?', 'Ich bitte Euch um einen kleinen Tipp, $gSchlundgnger:Schlundgngerin;! Welche neuen, spannenden Dinge erwarten uns heute beim Hof?', 38134),
(204005, 'deDE', 'Ara\'lon... ich werde sofort einen Suchtrupp losschicken. Wir berlassen ihn nicht den Drust.', '', 38134),
(204003, 'deDE', '', 'Ara\'lon ist noch nicht mit dem Beutel zurck. Ich frchte, er ist in Gefahr, oder Schlimmeres.', 38134),
(204002, 'deDE', '', 'Trefft mich am Herz des Waldes, um das Ritual abzuschlieen. Gute Jagd, Euch beiden.', 38134),
(204000, 'deDE', '', 'Er bevorzugt frmliche Hfe, also solltet Ihr Eure gesamte Aufmerksamkeit auf Frmlichkeiten richten.', 38134),
(203999, 'deDE', 'Wir haben Grufthter Kassirs Rckmeldung erhalten, und er wird teilnehmen.', '', 38134),
(203998, 'deDE', 'Ohne ihn komme ich nicht zurck. Ihr habt mein Wort!', '', 38134),
(203997, 'deDE', '', '$p, Ihr sammelt den Nachtwurz. Ara\'lon, wir brauchen einen Hexenbeutel. Den Drust einen stehlen wird gefhrlich sein.', 38134),
(203996, 'deDE', 'Sprecht mit dem Personal bezglich der Vertrge und Rckmeldungen und ich kmmere mich um den Rest.', '', 38134),
(203995, 'deDE', 'Ich kenne unseren ersten Gast sehr, sehr gut, also wei ich genau, welche Art Hof ihm gefallen wrde!', '', 38134),
(203993, 'deDE', 'Wir werden mit seinen Grbern sprechen mssen. Sie scheinen immer einen weiteren Kessel auf dem Feuer zu haben.', '', 38134),
(203992, 'deDE', 'Ich frchte, dass Theotar whrend der Proben unseren gesamten Teevorrat ausgetrunken hat.', '', 38134),
(203990, 'deDE', 'Gresit macht das fr uns klar. Ich markier seine Position auf Eurer Karte.', '', 38134),
(203989, 'deDE', 'Wenn Ihr nicht wisst, wo Ihr anfangen sollt, kann Euch meine Unterhaltungsliste verraten, wo Ihr hingehen msst.', '', 38134),
(203988, 'deDE', 'Ihr helft der Anklgerin und ihren Ritualisten mit allem, was sie brauchen, und die helfen dann uns. Alles klar?', '', 38134),
(203987, 'deDE', 'Niemand hlt bessere Rituale ab als die Eingeschworenen in den Hallen der Shne.', '', 38134),
(203984, 'deDE', 'Ein klarer Kopf gewhrleistet, dass nur reife Seelen nach Oribos kommen. Gut gemacht.', '', 38134),
(203983, 'deDE', 'Ein Beobachter muss entscheiden, ob eine Seele fr die Schattenlande bereit ist. Schrft Eure Fhigkeiten mit dieser Prfung.', '', 38134),
(203981, 'deDE', '', 'Gerade noch rechtzeitig! Fast wrt Ihr beiden zu spt gewesen...', 38134),
(203980, 'deDE', 'Wenn Ihr nach Kivarr sucht, die haben sie gerade weggeschleppt!', '', 38134),
(203979, 'deDE', '', 'Mein Kfig hat drei separate Schlsser mit je einem Schlssel. Ich glaube, die Drust frchten mich.', 38134),
(203974, 'deDE', 'Sucht den Hain ab. Die Drust knnen nicht weit mit ihr gekommen sein.', '', 38134),
(203973, 'deDE', 'Kivarr ist nicht hier... aber Spuren eines Kampfes. Ihr muss etwas zugestoen sein!', '', 38134),
(203970, 'deDE', 'Ich habe nicht vor, viel zu kmpfen, aber mit leerem Magen kann man keinen Krieg fhren.\n\nIch will ein Konstrukt mit einem guten Kopf, starken Hnden und vielen Mndern, um meine Kochknste zu testen.', '', 38134),
(203969, 'deDE', '', 'Ich will zwei riesige Klingen als Hnde. Dann rutscht dem Gegner sofort das Herz in die Hose!\n\nDas wird fantastisch... stimmt\'s?', 38134),
(203968, 'deDE', '', 'Ich bin es leid, ein Fleischschild zu sein und Kopf voran in zwanzig Gegner auf einmal zu rammen!\n\nIch wollte schon immer Zauber schleudern und mit Magie durch Gegner pflgen.\n\nBaut mir ein Konstrukt, das das kann!', 38134),
(203966, 'deDE', 'Auserwhlte! Der Feind nhert sich! Eine Schlacht steht uns bevor, und der Sieg wird unser sein!', '', 38134),
(203965, 'deDE', 'Einverstanden. Prft sie. Dann trefft mich in der Nekropole. Es gilt, einen Krieg zu fhren.', '', 38134),
(203964, 'deDE', 'Seid gegrt, $uReisender:Reisende;.\n\nIch erforsche uralte Relikte hier in den Schattenlanden.\n\nMchtet Ihr Euch mir anschlieen?', 'Seid gegrt, $uReisender:Reisende;.\n\nIch erforsche uralte Relikte hier in den Schattenlanden.\n\nMchtet Ihr Euch mir anschlieen?', 38134),
(203961, 'deDE', 'Archologie? Was ist denn das?\n\n<Er lauscht Eurer Erklrung zu Ablufen und Sinn der Archologie.>\n\nFaszinierend, aber ich denke nicht, dass wir hier so etwas haben.\n\nEuch Sterblichen mag die Zeit Geheimnisse entreien, doch hier funktioniert das etwas anders.\n\nSollte sich daran jedoch etwas ndern, sage ich Euch Bescheid.', 'Archologie? Was ist denn das?\n\n<Er lauscht Eurer Erklrung zu Ablufen und Sinn der Archologie.>\n\nFaszinierend, aber ich denke nicht, dass wir hier so etwas haben.\n\nEuch Sterblichen mag die Zeit Geheimnisse entreien, doch hier funktioniert das etwas anders.\n\nSollte sich daran jedoch etwas ndern, sage ich Euch Bescheid.', 38134),
(203960, 'deDE', '', 'Wir haben beide viel von ihm gelernt. Und gemeinsam werden wir ihn rchen.', 38134),
(203959, 'deDE', 'Ich bin erfreut, dass Ihr Krexus so gut dient. Selbst als Ihr noch Akarek gedient habt, hat er Euren Mut bewundert.', '', 38134),
(203958, 'deDE', 'Er hat meine Kraft in unmgliche Siege verwandelt. Fr diesen Verrat reie ich Vyraz sein schwarzes Herz heraus.', '', 38134),
(203957, 'deDE', '', 'Krexus hat viel Lob fr Euch brig, Aschenbringer. Und das ist selten.', 38134),
(203956, 'deDE', '', 'berleben ist die Tugend der Schwachen. Entweder Ihr siegt... oder sterbt.', 38134),
(203955, 'deDE', '', 'Der Markgraf hat mir eine neue Aufgabe anvertraut, Vashj. Wir berleben nur, wenn wir die Vergangenheit ruhen lassen.', 38134),
(203948, 'deDE', '', 'Habt Ihr Euch deshalb hinter dem Banner der Auserwhlten versteckt als die anderen Augen niedergemetzelt wurden?', 38134),
(203947, 'deDE', '', 'Ich habe Markgraf Akarek nicht verraten. Ich wurde auf eine Mission geschickt, bevor unser Haus fiel. So wie Ihr.', 38134),
(203944, 'deDE', '', 'Na, was haben wir denn hier? Eins von Kel\'Thuzads Haustierchen...', 38134),
(203943, 'deDE', 'Nein? Schade. Doch erlaubt mir, Euch ein... Abschiedsgeschenk zu geben.', '', 38134),
(203942, 'deDE', 'Ihr seid also entkommen? Wollt Ihr meinen Sieg feiern?', '', 38134),
(203941, 'deDE', 'Mag sein. Doch das ist eben der Preis der Macht.', '', 38134),
(203940, 'deDE', '', 'Das werdet Ihr ben!', 38134),
(203939, 'deDE', '', 'Eure Schwche ist des Hauses der Rituale unwrdig, Sin\'dane. Es wird Zeit, einen neuen Markgrafen zu salben.', 38134),
(203938, 'deDE', '', 'Baronin?! Ihr wagt es?!', 38134),
(203937, 'deDE', 'Sieht aus wie \'ne neue Jagd fr Vol\'jin... sobald ich ausgeruht bin.', '', 38134),
(203936, 'deDE', 'Allein? Nein.', '', 38134),
(203935, 'deDE', '', 'Was soll dieser Verrat?! Denkt Ihr etwa, Ihr wrt der Macht einer Markgrfin gewachsen?', 38134),
(203934, 'deDE', '', 'Ich habe von Eurer eitlen und herzlosen Knigin gehrt. Ihr zwei scheint Euch sehr hnlich zu sein... in Gestalt und Wesen.', 38134),
(203933, 'deDE', '', 'Knigin Azsharas Plne brachten uns ungeahnte Macht. Die Zerschlagung war ein geringer Preis fr unseren Aufstieg.', 38134),
(203931, 'deDE', '', 'Ihr wisst viel ber gefallene Welten, Vashj. Und ber Pakte mit Dmonenabschaum.', 38134),
(203930, 'deDE', '', 'Ich habe Eure Welt mit eigenen Augen gesehen, Draka. Oder zumindest ihre zerschmetterten berreste.', 38134),
(203929, 'deDE', '', 'Sie haben Widerstandkraft und Strke bewiesen. Genau die brauchen wir jetzt.', 38134),
(203928, 'deDE', 'Eine waghalsige Flucht!', '', 38134),
(203927, 'deDE', 'Man erntet, was man st.', '', 38134),
(203926, 'deDE', 'Lasst Euch davon nicht aufhalten.', '', 38134),
(203924, 'deDE', 'Da steckt jemand fest!', '', 38134),
(203922, 'deDE', 'Gut, dass Ihr die sterbliche Seele herausgeholt habt. Unsere Feinde drfen sie nicht haben.', '', 38134),
(203921, 'deDE', 'Erliegt niemals dem bermut. Bleibt stets konzentriert!', '', 38134),
(203920, 'deDE', 'An der Seite der Arena geht es tief nach unten!', '', 38134),
(203919, 'deDE', 'Der Fluch der Drust wird Gweyirs Willen bald brechen. Es ist zu riskant, wenn sie hier bleibt!', '', 38134),
(203918, 'deDE', '', 'Ich werde Baronin Vashj berichten. Es ist mir eine Ehre, mit Euch zu dienen.', 38134),
(203914, 'deDE', 'Gweyir! Sie atmet noch! Aber... was haben sie ihr angetan?', '', 38134),
(203911, 'deDE', 'Nera war die wildeste von uns. Sie htte sich den Drust nie ergeben.', '', 38134),
(203910, 'deDE', 'Taeras... Nacht umarme Euch, alter Freund.', '', 38134),
(203909, 'deDE', '', 'Das erste Portal von vielen. Ein wrdiger Sieg.', 38134),
(203908, 'deDE', 'Azelor... das habe ich befrchtet. Er hatte Besseres verdient...', '', 38134),
(203903, 'deDE', 'Der ganze Hain ist besessen! Seht zu, dass Ihr nicht auffliegt.', '', 38134),
(203902, 'deDE', 'Hier drben! Ich brauche Hilfe...', '', 38134),
(203901, 'deDE', 'Zzz... Zurckkehren... Treshala... Darnassus... Zzzz...', '', 38134),
(203899, 'deDE', 'Wir mssen alle noch viel lernen, Uther. Reden wir in meinem Tempel weiter.', '', 38134),
(203896, 'deDE', 'Wir mssen verstehen, was ihn so schmerzt. Vermutlich wissen wir vieles noch nicht.', '', 38134),
(203895, 'deDE', 'Ich bitte Euch, unterstellt Uther meiner Obhut.', '', 38134),
(203894, 'deDE', '', 'Jetzt, da wir die Nekropole kontrollieren, knnen wir ein Portal zum Sitz des Primus ffnen.', 38134),
(203893, 'deDE', 'Wir brauchen ihn auf dem Schlachtfeld. Ihr msst ihm helfen, das zu berwinden!', '', 38134),
(203892, 'deDE', 'Pelagos wollte Euch die Sammler als Untersttzung organisieren, aber die bevorstehende Schlacht hat ihn zweifeln lassen.', '', 38134),
(203891, 'deDE', '', 'In diesem Zustand hilft sie uns nicht. Stellt die Vesperglocken wieder her und stabilisiert sie!', 38134),
(203889, 'deDE', 'Ich verleihe Euch nun diese Macht. Verteidiger von Maldraxxus!', '', 38134),
(203885, 'deDE', '', 'Ihre Pein ist einfach kstlich!', 38134),
(203884, 'deDE', '', 'Aalen wir uns in ihrer Qual!', 38134),
(203883, 'deDE', 'Meine Herrschaft whrt ewig!', '', 38134),
(203882, 'deDE', 'Ihr werdet alle zu meinen Fen sterben!', '', 38134),
(203881, 'deDE', 'Niemand wird berleben!', '', 38134),
(203865, 'deDE', '', 'Jetzt!', 38134),
(203864, 'deDE', '', 'Fhlt unseren Stich! Bleibt nur noch eine Aufgabe.', 38134),
(203863, 'deDE', '', 'Bleibt wachsam! Spione sind unter uns!', 38134),
(203858, 'deDE', 'Unser Feind hlt an seiner berzeugung fest. Wenn wir ihn mit Worten nicht erreichen, mssen wir Taten sprechen lassen. Diese unglckliche Aufgabe berlasse ich Euch.', '', 38134),
(203857, 'deDE', 'Mit der Zeit werden sie ihren Irrtum einsehen. Ich hoffe, sie haben heute eine wertvolle Lektion gelernt.', '', 38134),
(203852, 'deDE', '', 'Unsere Taten machen uns zu Vorbildern fr die gesamten Schattenlande. Gut gemacht.', 38134),
(203851, 'deDE', '', 'Wir rufen Euch an. Ihr msst lediglich einen Dienst erfllen. Werdet Ihr antworten?', 38134),
(203848, 'deDE', '', 'Die Dankbarkeit des einen ist die Motivation des anderen. Eure Taten machen die Bastion strker.', 38134),
(203847, 'deDE', '', 'Die Rivalitt zwischen Nemea und Pelodis dauert schon onenlang an. Sie finden fr Euch sicher Verwendung... in aller Freundschaft, versteht sich.', 38134),
(203846, 'deDE', '', 'Ihr habt Euch gegen diese fremden Bestien behauptet. Vielleicht schlagen wir sie doch noch zurck. Danke.', 38134),
(203845, 'deDE', '', 'Eure Tapferkeit ist gefragt, sterbliche Seele. Diese Monster verschlingen alles auf ihrem Weg. Setzt dieser Bedrohung ein Ende!', 38134),
(203844, 'deDE', '', 'Ihr wart fr den wahren Pfad immer zu schwach. Devos htte Euch auf diesem Feld verrotten lassen sollen!', 38134),
(203843, 'deDE', 'Wir wollten unser Volk retten, nicht an solche Finsternis binden!', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(203841, 'deDE', 'Lysonia... warum...', '', 38134),
(203840, 'deDE', '', 'Nun wird alles klar. Ihr wart der Schlssel zu unserem Schicksal! Zu meinem Schicksal!', 38134),
(203839, 'deDE', 'Meine Seele ist von diesem Ort gezeichnet?', '', 38134),
(203838, 'deDE', 'Ich sehe nur Finsternis...', '', 38134),
(203837, 'deDE', '', 'Hinterfragt mich nicht. Tut es!', 38134),
(203836, 'deDE', 'Lysonia, ich...', '', 38134),
(203835, 'deDE', '', 'Mehr Energie! Ttet ihn, falls ntig!', 38134),
(203834, 'deDE', 'Zu Befehl.', '', 38134),
(203833, 'deDE', '', 'Er bricht nicht! Strengt Euch mehr an.', 38134),
(203832, 'deDE', '', 'Ich habe die Zahl der Patrouillen erhht, Polemarch. Die Aktivitten der Verschmhten haben zugenommen... drastisch.', 38134),
(203831, 'deDE', '', 'Geht, meine Verschmhten. Bereitet alles vor. Der neue Morgen muss bald anbrechen.', 38134),
(203830, 'deDE', 'Der originalgetreue Nachbau des Emblems des Aufstiegs durch die Archon, untersttzt durch die anderen Pakte.', '', 38134),
(203829, 'deDE', '', 'Polemarch. Schlundgnger. Ich habe etwas, das Ihr sehen msst.', 38134),
(203828, 'deDE', '', 'Und auch Uther, wie ich befrchte.', 38134),
(203827, 'deDE', '', 'Wir brauchen sie vielleicht. Laut meinen Beobachtern hatte Thenios recht. Lysonia fhrt die Verschmhten an.', 38134),
(203826, 'deDE', '', 'Dieses neue Emblem macht die Aufgestiegenen strker als zuvor!', 38134),
(203819, 'deDE', 'Ohne Devos wird Lysonia die Fhrung der Verschmhten bernehmen. Ich bete, dass wir Frieden finden, bevor sie erneut in den Krieg ziehen.', '', 38134),
(203813, 'deDE', 'Ihre Hand, Lysonia, hat sich mit Uther schnell angeschlossen. Whrend unsere Anima abnahm, wurden sie zahlreicher.', '', 38134),
(203812, 'deDE', 'Als Verkrperung der Loyalitt vom Pfad abzukommen, hat Devos auf einen Irrweg voller Zweifel und Gewalt gebracht.', '', 38134),
(203805, 'deDE', 'Devos wollte... ein vermeintliches Unrecht tilgen... Doch Uther war nicht fr die Prfungen des Aufstiegs bereit.', '', 38134),
(203799, 'deDE', 'Es wurde Zeit, dass Ihr seht, was nur wenige gesehen haben: Uthers Ankunft in der Bastion.', '', 38134),
(203798, 'deDE', 'Der... der enthlt...', '', 38134),
(203797, 'deDE', 'Die Archon hat mir meine Gerechtigkeit verwehrt. Dank Devos leidet dieses Monster nun dort, wo es hingehrt!', '', 38134),
(203796, 'deDE', '', 'Wir werden ihre glorreiche Vision verwirklichen: Kyrianer, und zwar alle, frei von den Fesseln Eurer Rituale!', 38134),
(203795, 'deDE', '', 'Mit diesen Scherben kann ich neue Spiegel fr den nchsten Gluthof zusammensetzen.', 38134),
(203791, 'deDE', 'Ihr hattet Eure Gelegenheit, aufzugeben. Jetzt rchen wir Krexus mit Eurem Blut!', '', 38134),
(203789, 'deDE', '', 'Euer Verrat hat Euer Schicksal besiegelt. Zeit fr Gerechtigkeit!', 38134),
(203788, 'deDE', 'Schluss mit diesem Irrsinn! Der Primus wrde das nicht dulden!', '', 38134),
(203787, 'deDE', 'Seid gegrt.\n\nIch erschaffe Musik aus den Snden der Seelen, die von den Taten ihres Untergangs in den gefrchteten Tiefen dieser heiligsten aller Hallen singen.\n\nVielleicht werdet Ihr, wenn Eure Zeit gekommen ist, meinem Chor beitreten...', 'Seid gegrt.\n\nIch erschaffe Musik aus den Snden der Seelen, die von den Taten ihres Untergangs in den gefrchteten Tiefen dieser heiligsten aller Hallen singen.\n\nVielleicht werdet Ihr, wenn Eure Zeit gekommen ist, meinem Chor beitreten...', 38134),
(203785, 'deDE', 'Seht nur, wie sie laufen. Bald haben wir die Drust aus unserem Wald vertrieben!', '', 38134),
(203784, 'deDE', 'Die Drust bedrngen unsere Verteidigung am Hain des Erwachens. Zeigt ihnen die Macht der Wilden Jagd!', '', 38134),
(203781, 'deDE', 'Treibt den Sturmangriff voran! Schaltet ihren Anfhrer aus.', '', 38134),
(203772, 'deDE', '', 'Alles begann, als die Horde Teldrassil zerstrte, die Heimat der Kaldorei. \n\nDie Allianz konnte keine Soldaten entbehren, um zurckzuholen, was von Teldrassil brig blieb, und so hatte meine Minn\'do keine Wahl.\n\nSie flehte Elune an, ihr die Macht der Nachtkriegerin zu verleihen, eine Form von Elunes Zorn.\n\nTyrande trgt jetzt den Zorn der Elune in sich, aber solche Macht ttet jeden, der sie zu lange trgt.', 38134),
(203771, 'deDE', '', 'Wisst Ihr es nicht? Ich dachte, dass sich die Kunde des Zorns meiner Minn\'do in der Horde verbreitet htte.\n\nEure Leute haben Teldrassil zerstrt, die Heimat der Kaldorei. \n\nDie Allianz konnte keine Soldaten entbehren, um zurckzuholen, was von Teldrassil brig blieb, und so hatte meine Minn\'do keine Wahl.\n\nSie flehte Elune an, ihr die Macht der Nachtkriegerin zu verleihen, eine Form von Elunes Zorn.\n\nTyrande trgt jetzt den Zorn der Elune in sich, aber solche Macht ttet jeden, der sie zu lange trgt.', 38134),
(203770, 'deDE', '', 'Wir teleportieren uns mit der verbleibenden Anima zur Gieerei. Trefft uns dort.', 38134),
(203765, 'deDE', '', 'Lord Garridans Gsteliste msste ein paar grundlegende Informationen darber enthalten, wo man die potenziellen Teilnehmer finden kann.\n\nWenn Ihr mit Lord Garridan gesprochen habt, erhaltet Ihr eine Kopie der Gsteliste, die Ihr dann jederzeit einsehen knnt.\n\nWir markieren zudem die Standorte aller Gste, fr die es noch keine Rckmeldung gibt, auf der Karte.\n\nUm die Rckmeldung eines Gastes zu erhalten, msst Ihr einfach nur zum jeweiligen Standort auf der Karte reisen und tun, was von Euch verlangt wird.\n\nWenn Ihr noch keinen potenziellen Gast getroffen habt, gibt es auch keine Rckmeldung, bis Ihr sie antrefft, normalerweise auf Abenteuerreisen in ihren Reichen.\n\nNoch einmal: Lord Garridans Gsteliste enthlt alle Details ber die jeweiligen Aufgaben, die erledigt werden mssen, um eine Rckmeldung zu erhalten.', 38134),
(203764, 'deDE', '', 'Solltet Ihr weitere Informationen bentigen, stelle ich sie Euch gerne bereit. Ihr msst nur fragen.', 38134),
(203763, 'deDE', '', 'Laut meinen Aufzeichnungen trifft man ihn meistens in der Nhe der Gruften bei den Hallen der Shne an.', 38134),
(203762, 'deDE', 'Wrdet Ihr ihn liebenswrdigerweise besuchen und das Problem lsen, das seine Teilnahme verhindert?', '', 38134),
(203761, 'deDE', 'Wir haben von Grufthter Kassir noch keine Rckmeldung erhalten.', '', 38134),
(203758, 'deDE', '', 'Sehr gut! Vielleicht habt Ihr die Ehre, den Mantel an seinen rechtmigen Ort zu bringen.', 38134),
(203753, 'deDE', 'Wartet, das knnte etwas sein. Was denkt Ihr, Schlundgnger?', '', 38134),
(203752, 'deDE', 'Gharmal gierte unaufhrlich nach Macht. Er htte damit lieber Maldraxxus als seine eigenen Interessen verteidigen sollen.', '', 38134),
(203751, 'deDE', 'Hmm. Nichts, das fr uns von Wert wre.', '', 38134),
(203750, 'deDE', 'Kommt. Durchsuchen wir die Vorrte des gefallenen Markgrafen.', '', 38134),
(203749, 'deDE', '', 'Richtig. Der Schlundgnger und ich bereiten unsere Reise dorthin vor. Seht in der Zwischenzeit nach, ob Gharmal sonst etwas von Wert fr uns besessen hat.', 38134),
(203748, 'deDE', 'Gharmal hat sich mit Sin\'dane verschworen. Wenn Revendreth hinter ihnen steht, ist das schlecht fr uns. Forschen wir nach.', '', 38134),
(203747, 'deDE', '', 'Sie wirkt nicht wie die aus Maldraxxus geplnderte Anima. Diese war ein Geschenk an einen Verbndeten.', 38134),
(203746, 'deDE', 'Anima... aber nicht aus Maldraxxus. Das riecht nach Venthyrhexerei.', '', 38134),
(203745, 'deDE', '', 'Ich hielt es fr weise, den Turm nach Beweisen fr Gharmals Verrat zu durchsuchen. Das hier hatte ich nicht erwartet.', 38134),
(203744, 'deDE', 'Geht voran, Baronin.', '', 38134),
(203743, 'deDE', '', 'Mograine! Schlundgnger! In Gharmals Hochburg gab es etwas Besorgniserregendes. Das msst Ihr sehen.', 38134),
(203741, 'deDE', '', 'Da ich so viele Namen von meiner Liste streichen konnte, habe ich wohl etwas Zeit, um Euren Hof zu verteidigen.', 38134),
(203740, 'deDE', 'Stellt Euch, Draka! Oder bettelt Ihr uns um Aufnahme an, wie auch bei den Auserwhlten?', 'Stellt Euch, Draka! Oder bettelt Ihr uns um Aufnahme an, wie auch bei den Auserwhlten?', 38134),
