INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(203290, 'deDE', '', 'Ich habe Berichte ber Wildtiere erhalten, die in der Nhe Chaos stiften. Die Animadrre muss sie wahnsinnig gemacht haben. Bitte stellt wieder Ruhe her.', 38134),
