INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(194957, 'deDE', 'Ich glaube, ich kann es schaffen!', 'Ich glaube, ich kann es schaffen!', 38134),
(194956, 'deDE', 'Ich habe Xandria zum Haupttempel fliegen sehen. Sie braucht vielleicht Eure Hilfe.', 'Ich habe Xandria zum Haupttempel fliegen sehen. Sie braucht vielleicht Eure Hilfe.', 38134),
(194955, 'deDE', 'Der Archon sei Dank, Ihr seid gekommen, whrend ich noch atme.', 'Der Archon sei Dank, Ihr seid gekommen, whrend ich noch atme.', 38134),
(194954, 'deDE', 'Hier drben! Schnell!', 'Hier drben! Schnell!', 38134),
(194953, 'deDE', 'Rettet... die anderen...', 'Rettet... die anderen...', 38134),
(194942, 'deDE', 'Wir haben die Leichen, die wir brauchen, Fleischnaht. Angriff auf den nchsten Tempel vorbereiten!', '', 38134),
(194941, 'deDE', 'Nur noch einen Moment. Die Teile fr meine neueste Kreation sind endlich eingetroffen...', '', 38134),
(194940, 'deDE', '', 'Ich fhre die Streitkrfte hier und meine Jnger bringen Euch zur Nekropole. Beenden wir diese Gruel.', 38134),
(194939, 'deDE', '', 'Ihr seid rechtzeitig hier, aber die Schlacht tobt weiter. Wir knnen erst ruhen, wenn der Himmel leer von Eindringlingen ist.', 38134),
(194938, 'deDE', 'Nrrisches Paragon. Ihr knnt uns nicht aufhalten!', '', 38134),
(194937, 'deDE', '', 'Ihr bekommt Eure gerechte Strafe!', 38134),
(194936, 'deDE', '', 'Widerliches Monster!', 38134),
(194935, 'deDE', 'Euer Tempel liegt in Trmmern, Eure Brder am Boden. Ihr habt bereits verloren!', '', 38134),
(194921, 'deDE', 'Ihr wollt das Schicksal Eures Schergen teilen? Euer vielgerhmter \"Mut\" wird Euer Untergang sein!', '', 38134),
(194916, 'deDE', 'Fleischnaht wird nicht gefallen, dass Ihr seine Schpfung zerstrt habt. Aber Eure Gliedmaen nimmt er sicher als Entschdigung an.', '', 38134),
(194915, 'deDE', 'Das knnte fr uns mehr sein als ein Flickenhof, Er knnte unser neues Zuhause sein.', '', 38134),
(194913, 'deDE', 'Es ist beleidigend, dass wir uns mit diesen Stachelebern abgeben mssen. Sie werden schon bald die ganze Macht der Horde zu spren bekommen.', '', 38134),
(194912, 'deDE', 'ZERSCHMETTERN WIR DEN GROSSEN, HAHA!', '', 38134),
(194910, 'deDE', '', 'Ich war eine Adlige, bevor ich hierherkam. Ihr drft Euch verbeugen.', 38134),
(194908, 'deDE', '', 'Das Licht scheint... selbst hier!', 38134),
(194907, 'deDE', 'Sie werden uns gehren!', 'Sie werden uns gehren!', 38134),
(194906, 'deDE', 'Die Ringe werden ihr Geheimnis preisgeben!', 'Die Ringe werden ihr Geheimnis preisgeben!', 38134),
(194905, 'deDE', 'Suchen wir Glitzerzeug!', '', 38134),
(194902, 'deDE', 'Seid im Schlund auf der Hut, sonst wird Eure Seele auf ewig verloren sein.', '', 38134),
(194901, 'deDE', 'Die Schattenlande waren nie fr sterbliche Augen bestimmt.\n\nTraut Euren Sinnen nicht und seid auf der Hut.\n\nSeid stets auf der Hut.', '', 38134),
(194897, 'deDE', 'Macht Chordy, hm... schnell wie einen Pftzenspringerkranich!\n\nSechs... sieben... nein, zwei Arme, und starke Daumen, um Sachen zu tragen.', '', 38134),
(194895, 'deDE', 'Chordy kann es kaum erwarten, wieder einen Krper zu haben!', '', 38134),
(194883, 'deDE', 'Die Geiel. Wir mssen ins Rathaus.', 'Die Geiel. Wir mssen ins Rathaus.', 38134),
(194880, 'deDE', 'Sagt einfach Bescheid, Ben.', 'Sagt einfach Bescheid, Ben.', 38134),
(194875, 'deDE', 'Ich habe das Flstern ber die Geiel gehrt... Ist es so weit?', 'Ich habe das Flstern ber die Geiel gehrt... Ist es so weit?', 38134),
(194874, 'deDE', 'Das ist das Ende der Welt!', 'Das ist das Ende der Welt!', 38134),
(194873, 'deDE', 'Was mache ich jetzt nur? Wo kann ich mich verstecken?', 'Was mache ich jetzt nur? Wo kann ich mich verstecken?', 38134),
(194872, 'deDE', 'Ben! Was geht hier vor?', 'Ben! Was geht hier vor?', 38134),
(194856, 'deDE', 'Das Rathaus ist unsere beste Chance... Ich werde die Vorbereitungen treffen.', '', 38134),
(194845, 'deDE', 'Die Anklgerin wird wissen, was zu tun ist... Das tut sie immer...', '', 38134),
(194844, 'deDE', '', 'Mir geht die Geduld aus!', 38134),
(194837, 'deDE', '', 'Ich muss Euch danken, Z\'rali. Ihr seid ein erstklassiges Versuchsobjekt.', 38134),
(194836, 'deDE', '', 'Aha, Ihr sprt also Schmerz. Hochinteressant.', 38134),
(194835, 'deDE', 'Der Eindringling betritt endlich meine Bhne. Lasst uns beginnen!', '', 38134),
(194833, 'deDE', '', 'Dem Licht sei Dank, Ihr habt uns gefunden!', 38134),
(194832, 'deDE', 'Die Lordadmiralin ist in Sicherheit. Ich signalisiere der Schwarzen Klinge vorzurcken.', '', 38134),
(194831, 'deDE', 'Da vorn! Das ist Lady Prachtmeer!', '', 38134),
(194830, 'deDE', 'Suchen wir weiter.', '', 38134),
(194829, 'deDE', 'Tut mir leid, Solomon. Fr mich gibt nur noch ein ruhiges Leben mit Farmarbeit und Holzfllen.', '', 38134),
(194828, 'deDE', 'Wenn einer unserer Verbndeten dort drin ist, bekommen wir ihn wohl kaum wieder heraus.', '', 38134),
(194827, 'deDE', 'Und weiter.', '', 38134),
(194826, 'deDE', 'Was immer hier vorgeht, wir sollten uns fernhalten.', '', 38134),
(194825, 'deDE', 'Ah, Ben! Wir sprechen gerade darber, vielleicht die Miliz wieder einzufhren. Wrt Ihr bereit, sie wieder anzufhren?', '', 38134),
(194823, 'deDE', 'Versucht, Euch nicht fressen zu lassen!', '', 38134),
(194822, 'deDE', 'Ich hoffe, unsere Verbndeten sind nicht in seinem... Bauch gelandet.', '', 38134),
(194821, 'deDE', 'Das Ungetm greifen wir lieber nicht an. Unsere Waffen sind gegen die Panzerung wahrscheinlich nutzlos.', '', 38134),
(194814, 'deDE', '', 'Euer Volk ist so faszinierend... Es birgt so viele Geheimnisse...', 38134),
(194799, 'deDE', '', 'Lok-tar! Nun wei ganz Maldraxxus, dass Ihr nicht leichtfertig herausgefordert werden solltet.', 38134),
(194795, 'deDE', '', 'Was sind diese Monstrositten?! Archon sei Dank, dass sie noch ruhen.', 38134),
(194794, 'deDE', '', 'In die Lfte, Aufgestiegene! Fr die Bastion!', 38134),
(194785, 'deDE', '', 'Ihr kommt vom Pfad ab, $n.', 38134),
(194773, 'deDE', '', 'Dieser Plan wird funktionieren, solange Ihr mir nur alles Ntige bringt.', 38134),
(194772, 'deDE', '', 'Matyas wird meinen Sndenstein nie mehr gegen mich einsetzen.', 38134),
(194769, 'deDE', 'Lasst von Euch hren, Zayhad! Gute Gesellschaft ist selten dieser Tage.', '', 38134),
(194768, 'deDE', '', 'Fruchtbare Erde muss bestellt werden. Wachst wohl, Marasmius.', 38134),
(194767, 'deDE', 'Hmf. Aber was Ihr hier angestellt habt gefllt mir. Und der Hof scheint seine Arbeit sorgsam wie gewohnt zu verrichten.', '', 38134),
(194766, 'deDE', '', 'Solcher Einsatz nhrt uns alle. Das Herz des Waldes erblht in Eurer Gegenwart.', 38134),
(194765, 'deDE', 'Na, das werden wir noch sehen. Bis jetzt hat mir nur dieser Sterbliche echten Respekt erwiesen.', '', 38134),
(194764, 'deDE', '', 'Dann stehen uns die Faeringe vielleicht wieder offen.', 38134),
(194763, 'deDE', 'Zayhad, alter Baum! Der Wind hat nichts damit zu tun. Dieses junge Ding hat mich hergebracht.', '', 38134),
(194762, 'deDE', '', 'Marasmius, hat Euch der Wind zurckgebracht?', 38134),
(194760, 'deDE', '', 'Hallo, Winzling. Was habt Ihr denn da?', 38134),
(194759, 'deDE', '', 'Der Sprssling wird zum Stamm. Der Stamm hlt den Himmel. Der Himmel birgt die Sprsslinge.', 38134),
(194758, 'deDE', '', 'Alles zu seiner Zeit.', 38134),
(194757, 'deDE', '', 'Was Ihr hier um Euch herum seht, ist das Ergebnis zahlloser Lebzeiten.$b$bWundervoll, nicht wahr?', 38134),
(194749, 'deDE', 'Ha! Geschieht Euch recht! Und jetzt raus aus meinem Wald!', '', 38134),
(194748, 'deDE', 'Lernt etwas Respekt!', '', 38134),
(194747, 'deDE', 'So viel dazu!', '', 38134),
(194746, 'deDE', 'Ich dich auch.', '', 38134),
(194740, 'deDE', 'Bin ich! Nur einen Augenblick.', '', 38134),
(194734, 'deDE', 'Ah, noch ein loyaler Diener der Kyrianer. Seid Ihr auch hier, um niedergestreckt zu werden?', '', 38134),
(194732, 'deDE', 'Sagt Bescheid, wenn Ihr so weit seid. Das ist... beim ersten Mal immer verstrend!', '', 38134),
(194730, 'deDE', 'Hah! Die haben bekommen, was sie verdient haben! Wir drei geben ein ziemlich gutes Team ab.', '', 38134),
(194729, 'deDE', 'Gerne, mein Schatz!', '', 38134),
(194728, 'deDE', '', 'Komm schnell zurck, wenn du fertig bist! Ich habe eine Holzlieferung fr den Magistrat.', 38134),
(194727, 'deDE', '', 'Heute erfahren unsere gefallenen Brder Gerechtigkeit. Dieses abscheuliche Treiben hat ein Ende!', 38134),
(194708, 'deDE', 'Ich bring dir spter ein paar Bretter vorbei. Geht aufs Haus!', '', 38134),
(194707, 'deDE', 'Noch schlimmer, Ben. Der Fels hat das Holz komplett durchschlagen!', '', 38134),
(194706, 'deDE', 'Hey, Vernon! Ist das Boot wieder undicht?', '', 38134),
(194677, 'deDE', '', 'Aufpassen, $n! Wir sind fast bei der Insel.', 38134),
(194676, 'deDE', 'Hei servieren! Kalter Tee schmeckt schrecklich!', '', 38134),
(194672, 'deDE', 'Das war ein profitables Unterfangen, $p. Ich hoffe sehr, dass wir bei zuknftigen Unternehmungen wieder zusammenarbeiten.', '', 38134),
(194671, 'deDE', 'Nein, nein, das reicht nicht, ich muss sie alle haben! So eine Gelegenheit bietet sich nicht zweimal.', '', 38134),
(194670, 'deDE', 'Ich kann Euch gar nicht sagen, wie dankbar ich Euch fr Eure Hilfe bei der Sicherung dieser zustzlichen Bnde bin.', '', 38134),
(194669, 'deDE', 'Einerlei. Wir sind zahllos. Ich werde Euch selbst tten.', '', 38134),
(194668, 'deDE', 'Marasmius! Er hilft uns!', '', 38134),
(194666, 'deDE', 'Ttet sie.', '', 38134),
(194665, 'deDE', 'Dummkopf. Ihr habt uns direkt ans Ziel gefhrt.', '', 38134),
(194664, 'deDE', '', 'Bald versteht Ihr. Diese ruhigen Momente dienen dem Verstndnis seiner Seele.', 38134),
(194663, 'deDE', '', 'Unfassbar... aber alles schein in Ordnung. Sind das wirklich seine letzten Augenblicke?', 38134),
(194661, 'deDE', 'Ihr gebt Euch immer noch nicht geschlagen? Nun denn, die Aasfresser werden sich an Eurer Essenz laben.', '', 38134),
(194657, 'deDE', '', 'Ihr und Ysera habt sicher viel zu besprechen.', 38134),
(194637, 'deDE', '', 'Versteht Ihr jetzt, Alexandros?', 38134),
(194636, 'deDE', 'Noch ein Krieg? Das ist hart.', '', 38134),
(194632, 'deDE', 'Ah, dieses Aroma erinnert mich an meine vielen Jahre im Fluchwald...', 'Ah, dieses Aroma erinnert mich an meine vielen Jahre im Fluchwald...', 38134),
(194631, 'deDE', 'Tubbins und Theotar haben es wieder mal geschafft! Was fr delikate und unerwartete Aromen...', 'Tubbins und Theotar haben es wieder mal geschafft! Was fr delikate und unerwartete Aromen...', 38134),
(194630, 'deDE', 'Ich geniee die Mglichkeit, etwas abzuschalten und den Moment zu genieen.', 'Ich geniee die Mglichkeit, etwas abzuschalten und den Moment zu genieen.', 38134),
(194629, 'deDE', 'Ich verurteile Euch in die Flammen!', '', 38134),
(194625, 'deDE', 'Mein... letztes... Urteil...', '', 38134),
(194624, 'deDE', 'Diese Verrter mssen bestraft werden!', '', 38134),
(194618, 'deDE', '', 'Die Tore ffnen sich! Und auch wenn wir dieselben durchschreiten, fhren sie uns in unterschiedliche Welten.', 38134),
(194617, 'deDE', '', 'Und so beginnen wir.', 38134),
(194616, 'deDE', '', 'Wir dienen nicht nur unseren, sondern allen Hilfe suchenden Seelen im Universum.', 38134),
(194615, 'deDE', '', 'Unseren Wegen bleiben wir treu, lernen stets dazu und bessern uns in unserer Berufung.', 38134),
(194614, 'deDE', '', 'Was uns auch erwartet, wir bleiben stark. Kein Hindernis noch Tragdie lsst uns wanken.', 38134),
(194613, 'deDE', 'Gut gemacht, mein Seelenband. Unser Aufstieg ist jetzt nahe.', '', 38134),
(194612, 'deDE', '', 'Ich habe die Schriftrolle zum Ritual gefunden. Diese Worte ffnen die Tore.', 38134),
(194611, 'deDE', 'Wer der Archon treu ist, muss bestraft werden!', '', 38134),
(194610, 'deDE', 'Ihr kennt die Wahrheit, Aspirant.', '', 38134),
(194609, 'deDE', 'Beweist Eure Loyalitt!', '', 38134),
(194608, 'deDE', 'Geht nur, $p. Ich... ich muss einen Moment nachdenken.', '', 38134),
(194607, 'deDE', 'Jetzt verstehe ich. Maldraxxus mag seine Ehre verloren haben... aber gemeinsam bringen wir sie zurck!', '', 38134),
(194606, 'deDE', '', 'Die Richterin hat gesehen, dass Eure Erinnerungen keine Brde, sondern die Quelle Eurer Strke sind. Deshalb gehrt Ihr nach Maldraxxus.', 38134),
(194605, 'deDE', 'Ich... glaube, ich verstehe jetzt. Kehren wir zum Paragon zurck.', '', 38134),
(194604, 'deDE', 'Mein geliebter Sohn... ich bin so stolz auf dich... und das wird mir niemand nehmen.', '', 38134),
(194603, 'deDE', 'Vater...?', '', 38134),
(194602, 'deDE', 'Ich werde dir das nie vergessen... Niemals!', '', 38134),
(194601, 'deDE', 'Darion... du hast dich geopfert, um mich von der Geiel zu befreien... und verteidigst Azeroth noch immer!', '', 38134),
(194600, 'deDE', 'Bitte... lasst uns weitergehen.', '', 38134),
(194599, 'deDE', '', 'Die Schmiede mag uralt sein, aber die Klingen, die wir mit ihr herstellen, sind so scharf wie eh und je. Gut gemacht.', 38134),
(194598, 'deDE', 'Sein Verrat sucht mich auch im Tode heim. Mein Sohn... Sollte ich diesen Schmerz vergessen?', '', 38134),
(194597, 'deDE', 'Renault. Mein ltester... mein Untergang.', '', 38134),
(194596, 'deDE', 'Vergebt mir, $p. Lasst uns weitermachen.', '', 38134),
(194595, 'deDE', 'Sie strahlt immer noch so wunderschn wie damals... Dass sie vergessen wird, wenn ich...', '', 38134),
(194594, 'deDE', 'Ist das... Elena? Meine Liebe... mein Licht...', '', 38134),
(194593, 'deDE', 'Mein Mut habe ich immer aus dem Licht geschpft. Aber... ich sehe mir an, was diese Schriftrollen zeigen.', '', 38134),
(194592, 'deDE', '', 'Meditiert ber diesen Schriftrollen, Alexandros. Findet die Quelle Eures Mutes und warum wir uns nicht hneln.', 38134),
(194591, 'deDE', '', 'Euer Pfad fhrt nur in den Ruin! Ich schmiede fr unser Volk ein neues Schicksal!', 38134),
(194590, 'deDE', 'Das entzieht sich unserer Kontrolle! So lange wir dem Weg folgen, stehen wir das gemeinsam durch!', '', 38134),
(194589, 'deDE', '', 'Und was hat es gebracht? Aspiranten steigen nicht mehr auf, Zenturionen verrotten auf den Feldern!', 38134),
(194588, 'deDE', '', 'Wir sind ber unsere Vergangenheit hinausgewachsen! Seht Ihr das nicht?', 38134),
(194587, 'deDE', '', 'All die ausgelschten Erinnerungen. Sie werden Gerechtigkeit bekommen!', 38134),
(194586, 'deDE', '', 'Wir werden noch ber Euer Schicksal sprechen. Bis dahin drft Ihr unter Bewachung hierbleiben.', 38134),
(194585, 'deDE', '', 'Sie sei vorerst gewhrt.', 38134),
(194584, 'deDE', 'Meine Archon, ich bitte um Gnade. Ich... wusste nicht...', '', 38134),
(194583, 'deDE', '', 'Was Euch angeht, Uther...', 38134),
(194581, 'deDE', 'Lysonias Champion kmpft gegen sie!', '', 38134),
(194580, 'deDE', 'Habt Ihr den Verstand verloren, Lysonia?', '', 38134),
(194579, 'deDE', 'Es wird Gerechtigkeit geschehen.', '', 38134),
(194578, 'deDE', 'Eins mit der Dunkelheit zu werden, ist Betrug allem, was einen Kyrianer ausmacht!', '', 38134),
(194577, 'deDE', '', 'Uthers zerschmetterte Seele hat mir die Macht des Schlunds erffnet. Erlebt sie jetzt aus erster Hand!', 38134),
(194576, 'deDE', '', 'VOR DEM SCHLUND SEID IHR ALLE NICHTS!', 38134),
(194575, 'deDE', '', 'Euer Dienst ist beendet.', 38134),
(194574, 'deDE', '', 'Unmglich... das war...', 38134),
(194573, 'deDE', '', 'Jetzt!', 38134),
(194572, 'deDE', '', 'So viel Macht und doch so blind. Ich werde Euch die Augen ffnen, meine Archon.', 38134),
(194571, 'deDE', '', 'Euer Meister blendet Euch mit Lgen.', 38134),
(194570, 'deDE', '', 'Leere Drohungen, Aufgestiegene.', 38134),
(194569, 'deDE', '', 'Kyrestia, Eure Herrschaft endet heute! Die Kyrianer werden einem neuen Meister folgen!', 38134),
(194566, 'deDE', '', 'Kommt und erlebt das Ende der Archon!', 38134),
(194565, 'deDE', 'Ihr msst Lysonia um jeden Preis besiegen.', '', 38134),
(194564, 'deDE', 'Wir werden verhindern, dass ihre Armee weiterhin auf die Archon vorrckt. Ich schwre es.', '', 38134),
(194563, 'deDE', 'Wrde die Bastion ihr gehren.', '', 38134),
(194562, 'deDE', 'Ich hoffe, wir kommen nicht zu spt. Wenn die Archon fllt...', '', 38134),
(194561, 'deDE', 'Der Rest unserer Truppen wird sich auch zur Festung zurckziehen.', '', 38134),
(194560, 'deDE', 'Die Verschmhten sind unterwegs zur Elysischen Feste! Wir mssen sofort zurck!', '', 38134),
(194559, 'deDE', '', 'Ihr werdet mein Schicksal nicht aufhalten. Nicht, wenn ich so nah dran bin.', 38134),
(194558, 'deDE', '', 'Sobald die Archon fllt, werden selbst Eure teuren Paragons die Wahrheit erkennen.', 38134),
(194557, 'deDE', '', 'Ich habe schon gewonnen. Dank Eures kleinen Angriffs, ist die Archon zum ersten Mal ungeschtzt.', 38134),
(194556, 'deDE', '', 'Ich werde mein Volk nie im Stich lassen, so wie Ihr!', 38134),
(194554, 'deDE', '', 'Ich zeige Euch Eure Vergangenheit, gebe diesem Reich eine Zukunft. Schliet Euch mir an!', 38134),
(194553, 'deDE', '', 'Mehr habt Ihr nicht zu bieten? Zeigt mir die Strke der Archon! Erringt den Sieg, den Ihr so verzweifelt wollt!', 38134),
(194552, 'deDE', 'Das ist Eure letzte Chance. Eure Streitkrfte haben verloren... Ihr habt bereits verloren!', '', 38134),
(194551, 'deDE', '', 'Trichter Aspirant. Wenn Ihr alles seid, das sich mir noch stellen kann, habt Ihr bereits verloren.', 38134),
(194550, 'deDE', '', 'Licht, Leere... Solch banale Mchte haben hier keine Bedeutung. Die Richterin hat Euch nicht ohne Grund nach Maldraxxus geschickt. Finden wir ihn heraus.', 38134),
(194549, 'deDE', 'Warum wurde ich nicht erwhlt? War mein Glaube an das Licht nicht stark genug? Wurde meine Seele befleckt, als der Untod mich nahm?', '', 38134),
(194547, 'deDE', '', 'Die Erinnerungen an Euer Leben qulen Euch im Tod. Wrt Ihr fr die Bastion erwhlt worden, wren sie eine Brde.', 38134),
(194545, 'deDE', 'Als ich die Maldraxxi das erste Mal erblickte, sah ich nur die Geiel, die meine Heimat zerstrt hatte. Die Monster, die ich immer vernichten wollte.', '', 38134),
(194544, 'deDE', '', 'Unsere Aspiranten legen ihre Erinnerungen im Tempel der Reinheit ab, doch wir folgen einem anderen Pfad. Sagt Maldraxxus Euch nicht zu?', 38134),
(194543, 'deDE', '', 'Geht ein Stck mit mir, Ihr beide.', 38134),
(194542, 'deDE', 'Zielstatus: le-le-le-le-lebendig. Tte...', '', 38134),
(194541, 'deDE', 'Er beginnt, herunterzufahren! Kmpft weiter, Freunde!', '', 38134),
(194540, 'deDE', '', 'Ich sagte, vernichtet sie. Warum leben sie immer noch, Goliath?', 38134),
(194539, 'deDE', 'Ich erinnere mich an... Demtigung. Ihr seid... an der Reihe.', '', 38134),
(194538, 'deDE', '', 'Ihr habt mir mir so viele trainiert. Erinnert Ihr Euch nicht?', 38134),
(194537, 'deDE', 'Ton inakzeptabel. Entferne Beschrnkungen fr tdliche Wirkung.', '', 38134),
(194536, 'deDE', '', 'Alles, nur ihn nicht...', 38134),
(194535, 'deDE', 'Wartet, Athanos...?', '', 38134),
(194534, 'deDE', '', 'Der Weg nach Sden ist frei. Wir mssen uns beeilen!', 38134),
(194533, 'deDE', '', 'Das ist die groe Verlogenheit der Archon.', 38134),
(194532, 'deDE', '', 'Ihr glaubt Euch im Recht. Und doch ttet Ihr ohne Unterlass Euresgleichen.', 38134),
(194531, 'deDE', '', 'Ich werde ihre Arbeit fortsetzen. Unser Schicksal hngt davon ab.', 38134),
(194530, 'deDE', '', 'Ihr wagt es, ihren Namen auszusprechen? Der Plan meines Paragons hat gerade erst begonnen!', 38134),
(194529, 'deDE', '', 'Ihr wisst, wie das endet. Ihr werdet enden wie Devos!', 38134),
(194528, 'deDE', '', 'Schweigt, Aspirant. Ihr habt keine Ahnung von Wahnsinn.', 38134),
(194527, 'deDE', '', 'Ich musste meine Erinnerungen aufgeben. Meine ganze Existenz!', 38134),
(194526, 'deDE', 'Lysonia, es ist noch Zeit, diesen Wahnsinn aufzugeben! Wir sind alle Kyrianer!', '', 38134),
(194524, 'deDE', '', 'Wir stehen bis zum Ende an Eurer Seite!', 38134),
(194523, 'deDE', 'Ein hchst ruhmreicher Anlass! Wenn Ihr mich jetzt entschuldigt, ich bin mit einer groartigen Aufwertung von Bron fast fertig!', '', 38134),
(194521, 'deDE', 'Seid Ihr bereit?', '', 38134),
(194517, 'deDE', 'Lysonia war nicht anders. Mir war nicht klar, dass dieselbe Dunkelheit hinter ihnen steht...', '', 38134),
(194516, 'deDE', 'Devos hat mich berzeugt, dass mein Tod falsch war. Sie sprach von Gerechtigkeit.', '', 38134),
(194515, 'deDE', 'Meine Aspirantenausbildung war schwierig. Ohne Erinnerung an mein Leben.', '', 38134),
(194514, 'deDE', 'Danke fr Eure Gnade. Ich werde Euch nicht enttuschen.', '', 38134),
(194513, 'deDE', 'Ich wrde es auch gern verstehen. Bitte, geht voran.', '', 38134),
(194512, 'deDE', '', 'Muss ich denn alles selbst machen?', 38134),
(194511, 'deDE', '', 'Eure Zeit ist noch nicht gekommen. Erhebt Euch und kmpft weiter!', 38134),
(194510, 'deDE', '', 'Sie waren schwach. Ich sollte Euch danken, dass Ihr ihren Makel getilgt habt.', 38134),
(194509, 'deDE', '', 'Bedeutungslos.', 38134),
(194508, 'deDE', '', 'Wenn Ihr nicht anstndig kmpft, dann tue ich es auch nicht. $uSchlundgnger:Schlundgngerin;, zu mir!', 38134),
(194506, 'deDE', 'Tricht wie eh und je, Nadjia. Meine Wachen werden mit Euch kurzen Prozess machen.', '', 38134),
(194504, 'deDE', '', 'Tretet vor und enttuscht mich, so wie Uther es tat.', 38134),
(194502, 'deDE', '', 'Endlich! Im Kfig hatte ich viel Zeit, dieses Duell genau zu planen.', 38134),
(194500, 'deDE', '', 'Kyrestia hat recht, die Schattenlande sind zerstrt. Sie zu reparieren, bentigt Willenskraft... die ihr fehlt.', 38134),
(194498, 'deDE', '', 'Ich werde beenden, was Devos begonnen hat. Wir sind nicht lnger durch \'den Pfad\' geblendete Diener der Archon.', 38134),
(194495, 'deDE', '', 'Die Anima gehrt uns.', 38134),
(194494, 'deDE', '', 'Wir mgen die Spitzen verloren haben, doch der Tag war nicht ohne Sieg.', 38134),
(194493, 'deDE', 'Der Sieg ist beinahe unser. Erringt ihn... fr uns alle.', '', 38134),
(194491, 'deDE', 'Ich kann sie nicht viel lnger zurckhalten! Begebt Euch zum Tor!', '', 38134),
(194490, 'deDE', '', 'Mge der Blick der Archon auf uns allen ruhen.', 38134),
(194489, 'deDE', '', 'Ich werde die Arena rumen. Das Tor zum Tempel ist direkt nrdlich von hier.', 38134),
(194487, 'deDE', '', 'Hierher!', 38134),
(194485, 'deDE', '', 'KOSTET MEINEN STAHL!', 38134),
(194483, 'deDE', '', 'Ihr erinnert Euch sicher an Eure Ausbildung im Tiegel. Er war leicht zu konvertieren.', 38134),
(194482, 'deDE', 'J-ja... mein Paragon...', '', 38134),
(194481, 'deDE', '', 'Haltet sie auf! Athanos, gebt jeden letzten Animatropfen, wenn es sein muss!', 38134),
(194480, 'deDE', '', 'Verschafft uns Zeit! Der Wahrheitsbringer ist fast aufgeladen.', 38134),
(194479, 'deDE', '', 'Ihr seid beharrlich, nicht wahr?', 38134),
(194478, 'deDE', 'Wir werden ihre Verstrkung in Schach halten. Vorwrts!', '', 38134),
(194477, 'deDE', '', 'Sie stehen immer noch. Noch mal!', 38134),
(194476, 'deDE', '', 'Dann ladet ihn auf! Entzieht notfalls unseren Zenturionen Anima!', 38134),
(194475, 'deDE', 'Anima des Kolosses zu niedrig. Mssen nachladen.', '', 38134),
(194474, 'deDE', '', 'Lasst Tod auf sie regnen!', 38134),
(194473, 'deDE', 'Ich freue mich ausgesprochen, Euch kennenzulernen!', '', 38134),
(194469, 'deDE', '', 'So leicht fallen meine Champions nicht.', 38134),
(194468, 'deDE', '', 'Ihr habt nur Rekruten gettet. Andere werden ihren Platz einnehmen.', 38134),
(194465, 'deDE', '', 'Erlebt die ganze Pracht der Verschmhten!', 38134),
(194464, 'deDE', '', 'Raffinesse war noch nie Kyrestias Strke.', 38134),
(194463, 'deDE', '', 'Ihr strzt Euch also kopfber in die Schlacht?', 38134),
(194462, 'deDE', 'Ruhm der Archon! Ruhm der Bastion!', '', 38134),
(194460, 'deDE', 'Wir folgen Euch.', '', 38134),
(194459, 'deDE', 'Schlundgnger, Ihr werdet mit unserer grten Streitmacht vorstoen.', '', 38134),
(194458, 'deDE', 'Unsere Paragons werden von den Flanken her zuschlagen.', '', 38134),
(194456, 'deDE', 'Der Moment ist gekommen, Aufgestiegene. Heute endet die Bedrohung durch die Verschmhten.', '', 38134),
(194437, 'deDE', '', 'Die Gerechtigkeit wird obsiegen.', 38134),
(194428, 'deDE', '', 'Gut. Ich habe Hilfe fr Euch.', 38134),
(194427, 'deDE', '', 'Hab ich! Ja!', 38134),
(194426, 'deDE', '', 'Habt Ihr eine Liste gemacht, Sika?', 38134),
(194425, 'deDE', '', 'Oh! Hooh! Hallo!', 38134),
(194423, 'deDE', 'Die Tiere der Bastion haben nicht die Disziplin der Kyrianer. Vielleicht knnt Ihr ein paar bndigen.', '', 38134),
(194421, 'deDE', 'Huu! Kann helfen? Kann viele Aufgaben erledigen oder einfach Gesellschaft leisten!', 'Huu! Kann helfen? Kann viele Aufgaben erledigen oder einfach Gesellschaft leisten!', 38134),
(194414, 'deDE', 'Fabelhafte Arbeit. Dieses Gert wird uns von Nutzen sein.', '', 38134),
(194413, 'deDE', 'Die Mittler haben seltsame Waffen aus anderen Reichen sichergestellt. Vielleicht sind sie unserer Sache zutrglich.', '', 38134),
(194402, 'deDE', '', 'Das wird sich zeigen. Lasst ihn erstmal sehen.', 38134),
(194401, 'deDE', 'Glaubt Ihr, unser Gast kann uns wirklich helfen?', '', 38134);
INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(194391, 'deDE', '', 'Hab ich es nicht gesagt?... Jemand mit Potenzial.', 38134),
(194388, 'deDE', 'ffnet mir Euren Geist, sterbliche Seele. Wir werden sehen, welche Erinnerungen sich offenbaren.', 'ffnet mir Euren Geist, sterbliche Seele. Wir werden sehen, welche Erinnerungen sich offenbaren.', 38134),
(194378, 'deDE', '<Pfeift>', '<Pfeift>', 38134),
(194377, 'deDE', 'Denn nimmer, nimmer wieder geht die Sonne auf...', 'Denn nimmer, nimmer wieder geht die Sonne auf...', 38134),
(194375, 'deDE', 'Wer htte das gedacht! Ein einfacher, bescheidener Mittler wie ich arbeitet mit $udem sehr besonderen:der sehr besonderen; Fremden.', '', 38134),
(194363, 'deDE', 'Sie werden die Wahrheit erfahren... und dann werden sie fallen...', '', 38134),
(194358, 'deDE', '', 'Am Ende... werden sie verstehen...', 38134),
(194336, 'deDE', '', 'Seht es Teil der Ausbildung, mein Freund.', 38134),
(194335, 'deDE', 'Mssen wir laufen? Meine Prfung hat mich erschpft.', '', 38134),
(194334, 'deDE', '', 'Geht schon vor. Pelagos und ich kommen zu Fu nach.', 38134),
(194328, 'deDE', '', 'Die Verschmhten wollen unseren Aufstieg verhindern. Sucht die anderen!', 38134),
(194293, 'deDE', '', 'Mograine lobt Eure Fhrungsqualitten und Euren Mut in hchsten Tnen, Draka. Es ist mir eine Ehre.', 38134),
(194292, 'deDE', '', 'Eure Anwesenheit freut mich, Paragon. Wir haben einen gemeinsamen Feind.', 38134),
(194291, 'deDE', 'Vielleicht eine Chance, einige unserer Verluste wettzumachen.', '', 38134),
(194290, 'deDE', 'Da kommt jemand, der uns Profit bringen knnte.', '', 38134),
(194284, 'deDE', 'Ihr habt all diese Bluthauer selbst gettet?', 'Ihr habt all diese Bluthauer selbst gettet?', 38134),
(194281, 'deDE', 'Erster Schritt: Wir sorgen dafr, dass man Euch bemerkt!', '', 38134),
(194279, 'deDE', 'Es geht nicht nur darum, gut im Kmpfen zu sein. Ihr msst auch Verbindungen knpfen!', '', 38134),
(194278, 'deDE', 'Weg, weg, GEHT WEG! Das gehrt MIR! Ich habe es gefunden! Ich erhebe Anspruch auf diesen Schatz. Er gehrt mir.\n\nGeht! Geht sofort zurck in das Reich, aus dem Ihr herausgekrochen seid. Werft Euch von den Festungsmauern, ist mir egal. Lasst mich einfach mit diesem Schatz allein, den ich gefunden habe.\n\nIch brauche das. Ich brauche das mehr, als jemand mit Eurer schicken Rstung und teuren Kleidung je verstehen knnte. DAS GEHRT MIR!', 'Weg, weg, GEHT WEG! Das gehrt MIR! Ich habe es gefunden! Ich erhebe Anspruch auf diesen Schatz. Er gehrt mir.\n\nGeht! Geht sofort zurck in das Reich, aus dem Ihr herausgekrochen seid. Werft Euch von den Festungsmauern, ist mir egal. Lasst mich einfach mit diesem Schatz allein, den ich gefunden habe.\n\nIch brauche das. Ich brauche das mehr, als jemand mit Eurer schicken Rstung und teuren Kleidung je verstehen knnte. DAS GEHRT MIR!', 38134),
(194271, 'deDE', 'Nein, nein, nein! Das ist MEIN Schatz!', '', 38134),
(194266, 'deDE', 'Bereit fr das nchste Duell, $R?', '', 38134),
(194265, 'deDE', 'Ich freue mich auf zuknftige, gewinntrchtige Abmachungen.', '', 38134),
(194250, 'deDE', 'Bevor Ihr in der Arena antreten knnt, muss ich erst Euer Potenzial ausloten, Profit zu generieren. Ich werde ber Euren genauen Wert im Laufe unserer Geschftsbeziehung entscheiden.', '', 38134),
(194249, 'deDE', '', 'Stellt Euch, Totenbeschwrer! Thanikos wird gercht!', 38134),
(194229, 'deDE', 'Ein unerwartetes Ergebnis ist die grte Belohnung fr ein Experiment! Danke fr Eure Hilfe!', '', 38134),
(194228, 'deDE', 'Nchstes Kostm!', '', 38134),
(194227, 'deDE', 'Gut, und denkt an Euren Einsatz!', '', 38134),
(194224, 'deDE', 'Der Zuchtmeister hat hier und da ein paar Stapel, auf die Steingeborenenwachen aufpassen. Wir alle hassen den Kerl, und zwar so was von. Holt die Sndensteine und helft Nadjia!', '', 38134),
(194223, 'deDE', 'Tja, die heien Sndensteine. Der Zuchtmeister benutzt sie, damit die andren Blutsauger machen, was er will, wenn er ihre Steine hat. Ihr helft doch Nadjia, oder?', '', 38134),
(194219, 'deDE', 'Wenn Ihr $g\'n Freund:\'ne Freundin; von Nadjia seid, dann mag ich Euch. Wisst Ihr, wir mssen die bekritzelten Steine finden. Die liegen berall im Dickicht rum.', '', 38134),
(194210, 'deDE', '', 'Seid Ihr bereit, von meiner Weisheit zu lernen?', 38134),
(194179, 'deDE', 'Meine Knochen... werden... wieder leben...', 'Meine Knochen... werden... wieder leben...', 38134),
(194174, 'deDE', '', 'Der Hain singt vor Freude. Alte Pracht erneuert. Ihr, ein Held von Knigin und Hof. Freund von Wildsamen und Wchterin.', 38134),
(194173, 'deDE', 'Ich bin erwacht. Lasst den Test beginnen.', '', 38134),
(194170, 'deDE', '', 'Dank Euch knnen die Wurzeln sich laben und entwickeln und Myriaden von Geister ihre Wiedergeburt erleben.', 38134),
(194164, 'deDE', '', 'Der Garten fllt sich mit Anima. Auch als Setzling bieten Eure Zweige Schutz und Schatten.', 38134),
(194161, 'deDE', '', 'Dank Euch kann dieser Hain genau wie die Wildsamen gedeihen. Viel Pflege liegt noch vor uns.', 38134),
(194160, 'deDE', 'Ihr knnt Euch noch verbessern. Kehrt zurck, wenn Ihr strker seid.', '', 38134),
(194159, 'deDE', 'Zufriedenstellendes Resultat. Ich ergebe mich.', '', 38134),
(194158, 'deDE', 'Ihr werdet erwogen. Ihr werdet gemessen.', '', 38134),
(194137, 'deDE', 'Juhuuuuuu!', '', 38134),
(194135, 'deDE', 'Hab\'s geschafft! Wow!', '', 38134),
(194127, 'deDE', '', 'Mein ganzer Trupp ist in einen Hinterhalt der Verschlinger geraten. Ihr Riss im Turm hat uns berrascht... Wir waren in Sekundenschnelle umzingelt. Ich konnte mich nur knapp behaupten.', 38134),
(194124, 'deDE', '', 'Die Zeit sorgt fr das Wachstum, und bald knnt Ihr ihn wecken.', 38134),
(194122, 'deDE', '', 'Gebt diesen Geist in einen leeren Wildsamen, um zu beginnen.', 38134),
(194120, 'deDE', 'Helft, die Szene vorzubereiten. Beeilung!', '', 38134),
(194117, 'deDE', 'Zeit fr den nchsten Akt!', '', 38134),
(194106, 'deDE', '', 'Aber die Katalysatoren mssen vor den Wildsamen eingepflanzt werden, sonst nutzen sie den Geistern nichts.', 38134),
(194105, 'deDE', '', 'Segen unterscheiden sich je nach Katalysator. Experimentiert und entwickelt.', 38134),
(194104, 'deDE', '', 'Wildsamen beziehen Strke aus den neben ihnen gepflanzten Katalysatoren.', 38134),
(194096, 'deDE', 'Alle auf die Pltze!', '', 38134),
(194094, 'deDE', 'Ah, da seid Ihr! Wir knnen anfangen!', '', 38134),
(194093, 'deDE', '', 'Ihr knnt sie annehmen. So ist es doch unter Sterblichen.', 38134),
(194091, 'deDE', '', 'Nach ihrer Neugeburt danken Euch die Geister mit Gaben.', 38134),
(194088, 'deDE', '', 'Ich werde dafr sorgen, dass Euer Talent zugleich mit den hiesigen einzigartigen Wildsamen gedeiht.', 38134),
(194087, 'deDE', '', 'Sanfte Berhrung und Pflege fhrt sie ihrem Schicksal zu. Wir mssen uns um ihr Wohlergehen sorgen.', 38134),
(194085, 'deDE', 'Nein, nein! Ich darf nicht aufgeben!', '', 38134),
(194082, 'deDE', 'Man entdeckt immer etwas Neues!', '', 38134),
(194081, 'deDE', 'Hmm, Rezan is\' schlauer, als ich dachte! Vielleicht gibt\'s mal \'nen neuen Loa der mit Bwonsamdi Abmachungen trifft, hm?', '', 38134),
(194080, 'deDE', 'Ich bin schwach... Ich werde... versagen...', '', 38134),
(194078, 'deDE', 'Ich fhl mich geehrt, Majestt. $p, danke fr alles, was Ihr fr mich getan habt... im Leben un\' im Tod.', '', 38134),
(194077, 'deDE', '', 'Ich kenne Euer Schicksal nicht, Vol\'jin von den Dunkelspeeren. Aber ich biete Euch die Chance, eines Tages wiedergeboren zu werden.', 38134),
(194074, 'deDE', '', 'Zusammen mit dieser. Erstaunlich... eine sterbliche Seele, verwoben mit der Essenz eines Loa.', 38134),
(194073, 'deDE', '', 'Ich nehme diese drei Geister in unsere Obhut, Bwonsamdi.', 38134),
(194070, 'deDE', 'Hallchen, Majestt! Der alte Bwonsamdi hat geholf\'n, die vermisst\'n Loa zu find\'n.', '', 38134),
(194067, 'deDE', 'Bitte! Bitte!!', '', 38134),
(194066, 'deDE', 'Es ist endlich... vorbei...', '', 38134),
(194065, 'deDE', 'Euer Versagen... befleckt... Eure Seele...', '', 38134),
(194064, 'deDE', 'Ihr seid schwach. Ihr werdet nie aufsteigen.', '', 38134),
(194063, 'deDE', 'Helft... mir...', '', 38134),
(194062, 'deDE', 'Tut mir so leid...', '', 38134),
(194061, 'deDE', 'Allein... ganz allein...', '', 38134),
(194060, 'deDE', 'So viele... Prfungen...', '', 38134),
(194059, 'deDE', 'Ich werde nie aufsteigen.', '', 38134),
(194058, 'deDE', 'Kleia... helft mir!', '', 38134),
(194057, 'deDE', 'Das ist unmglich!', '', 38134),
(194056, 'deDE', 'Ahh...', '', 38134),
(194054, 'deDE', 'Alles ist... vergeben...', '', 38134),
(194053, 'deDE', 'Endlich... Ruhe...', '', 38134),
(194052, 'deDE', 'Nein!', '', 38134),
(194051, 'deDE', 'Warum? Warum?!', '', 38134),
(194050, 'deDE', 'Falsch! Alles falsch!', '', 38134),
(194049, 'deDE', 'Habe ich... versagt?', '', 38134),
(194048, 'deDE', 'Macht, dass es... aufhrt...', '', 38134),
(194047, 'deDE', 'Ich war nie gut genug!', '', 38134),
(194046, 'deDE', 'Schmerz!', '', 38134),
(194042, 'deDE', 'Nein, bin nur \'n alter Schattenjger. Unser Volk braucht Euch!', '', 38134),
(194040, 'deDE', 'Mge alles, das ich war... in Euch... wiedergeboren werden.', '', 38134),
(194039, 'deDE', 'Der letzte Funke... meiner Essenz... gehrt Euch. Tragt sie... in den Ardenwald.', '', 38134),
(194035, 'deDE', 'Oh! Oh, so sollte das nicht funktionieren... Aber versucht es weiter!', '', 38134),
(194031, 'deDE', '', 'Der Fluss der Zeit rauscht wild und unbndig voran. Allein ertrinke ich, zusammen schwimmen wir.', 38134),
(194030, 'deDE', '', 'Die schlummernden Seelen sind geschwcht, ihre Wiedergeburt unvorhersehbar wie der Regen.', 38134),
(194029, 'deDE', '', 'Es ist ein besonderer Ort der Erneuerung, wo verlorene Seelen einen neue Chance erhalten.', 38134),
(194028, 'deDE', '', 'Der Hain flstert sein Willkommen durch die Zweige seiner Wchterin.', 38134),
(194026, 'deDE', '', 'Folgt dem Pfad! Als ob wir... Wir sollten uns aufmachen.', 38134),
(194025, 'deDE', '', 'Wir werden Euch viel lehren, aber sorgt Euch nicht! Es ist Eure Reise und Ihr whlt selbst, wie schnell Ihr vorankommen mchtet.', 38134),
(194024, 'deDE', '', 'Ihr werdet Eure sterblichen Makel ablegen, zu einem hheren Wesen werden und unsere heiligste und schwierigste Pflicht erfllen knnen.', 38134),
(194023, 'deDE', '', 'Bei Eurem Aufenthalt hier lernt Ihr die Tugenden der Reinheit, des Mutes, der Demut, der Weisheit und der Loyalitt.', 38134),
(194022, 'deDE', 'Vol\'jin... von den Dunkelspeeren... Eure Seele ist entschlossen. Ihr sucht... Gerechtigkeit.', '', 38134),
(194019, 'deDE', 'Nein! Rezan, wir hol\'n Euch hier raus. Ihr werdet wieder.', '', 38134),
(194018, 'deDE', 'Ihr seid... zu spt...', '', 38134),
(194017, 'deDE', 'Noch... nicht...', '', 38134),
(194016, 'deDE', 'Diese Seele ist verloren! Es gibt nichts mehr zu retten!', '', 38134),
(194015, 'deDE', 'Lauft... weg...', '', 38134),
(194014, 'deDE', 'Ihr werdet das Leid mit diesem Loa teilen!', '', 38134),
(194011, 'deDE', 'Ich zhl auf Euch... Nehmt meine Anima un\' rettet Rezan!', '', 38134),
(194010, 'deDE', 'Und doch habe ich mein Leben dem Licht gewidmet. Verdient man sich so keinen Platz in der Bastion?', '', 38134),
(194007, 'deDE', '', 'Aschenbringer. Das Urteil der Richterin ist immer richtig. Sie muss etwas in Euch erkannt haben, das nach Maldraxxus gehrt hat.', 38134),
(194006, 'deDE', 'Wir mss\'n Rezan vor dem Monster rett\'n!', '', 38134),
(194005, 'deDE', 'Verpesteter und mischbarer Schlamm sind manchmal schwer zu finden.', '', 38134),
(194003, 'deDE', 'Kommt spter wieder. Wir werden gemeinsam einen weiteren Trank herstellen.', '', 38134),
(193997, 'deDE', 'Diese Masken... Meine Freunde... Es ist, als wre nichts mehr von ihnen brig.', '', 38134),
(193996, 'deDE', 'Was in Eurem Dorf passiert ist, war grauenhaft... Wir mssen sicherstellen, dass das nie wieder passiert!', '', 38134),
(193995, 'deDE', 'Ich bin dankbar, dass mich die Hter des Hains hierbleiben lassen. Ich verspreche, dass ich ihre Gastlichkeit nicht auf die leichte Schulter nehme.', '', 38134),
(193994, 'deDE', 'Ihr habt es geschafft! Das Netzwerk wurde wiederhergestellt.', '', 38134),
(193993, 'deDE', 'Jemand oder etwas hat unser Spiegelnetzwerk destabilisiert. Ihr msst die Strungsquelle finden, damit alles wieder ordnungsgem funktioniert.', '', 38134),
(193991, 'deDE', 'Die Grber hier werfen alles und jedes ins Schlammbecken. Dies ist fr den Grabungsprozess unerlsslich.\n\nLeider fhrt ein falsches Gleichgewicht oft dazu, dass diese schmutzigen Kreaturen erscheinen. Einmal hat sich diese riesige Vermischung gebildet... das war eine ziemliche Sauerei.', '', 38134),
(193985, 'deDE', '', 'Niemand ist Euch gefolgt, oder? Knochenschmiedin Heirmir will, dass dies unter absoluter Geheimhaltung geschieht.', 38134),
(193981, 'deDE', '', 'Gut festhalten! Kein bisschen Anima darf fallen!', 38134),
(193980, 'deDE', '', 'Sika, haben wir etwas Anima fr den Begrer?', 38134),
(193978, 'deDE', 'Schreit, tretet, beit, nutzt Eure Waffen... tut alles, was fr einen Sieg ntig ist!', '', 38134),
(193970, 'deDE', 'Ihr habt alles richtig gemacht, $uSchlundgnger:Schlundgngerin;.', '', 38134),
(193960, 'deDE', 'Ihr habt Potenzial, $uSchlundgnger:Schlundgngerin;. Sehen wir mal, ob Ihr in der Arena aushelfen knnt.', '', 38134),
(193940, 'deDE', 'Ich kann es kaum erwarten, diese Proben zu analysieren! Gute Arbeit, Lehrling!', '', 38134),
(193938, 'deDE', 'Das Gebiet um unsere Nekropole herum scheint von Substanzen vertzt worden zu sein. Zeit, ein paar Proben zu nehmen!', '', 38134),
(193933, 'deDE', 'Geht Ihr schon vor. Ich... brauche einen Moment.', '', 38134),
(193930, 'deDE', 'Niederknien? Ich soll vor Euch niederknien? Absurd wie immer, Nadjia. Bei unserem nchsten Treffen werde ich Euch einen echten Tod zukommen lassen.', '', 38134),
(193929, 'deDE', '', 'Matyas. Ich verlange Wiedergutmachung fr Eure Verste. Kniet nieder und entschuldigt Euch, oder wir duellieren uns. $gDer Schlundgnger wird mein Sekundant sein:Die Schlundgngerin wird meine Sekundantin sein;.', 38134),
(193928, 'deDE', 'Uff. Was ist das? Verrat!', '', 38134),
(193919, 'deDE', 'Sie wei es?', 'Sie wei es?', 38134),
(193899, 'deDE', 'Ich werde mein Bestes geben. Fr das Kartell, versteht sich.', 'Ich werde mein Bestes geben. Fr das Kartell, versteht sich.', 38134),
(193894, 'deDE', 'Ja, die Verwalter sind auch scharfsinnig, aber ich vertraue Euch, Ta\'mari. Sorgt dafr, dass Eure Partner ihre Arbeit machen.', 'Ja, die Verwalter sind auch scharfsinnig, aber ich vertraue Euch, Ta\'mari. Sorgt dafr, dass Eure Partner ihre Arbeit machen.', 38134),
(193893, 'deDE', 'Unterschtzt sie nicht. Ich wei, dass Ihr ein scharfsinniger Mittler seid, aber bleibt auf der Hut.', 'Unterschtzt sie nicht. Ich wei, dass Ihr ein scharfsinniger Mittler seid, aber bleibt auf der Hut.', 38134),
(193892, 'deDE', 'Hier ist mein Bericht, Opulenz. Die Bediensteten stellen Fragen ber das Portalnetzwerk, aber nichts, mit dem ich nicht fertigwerden wrde.', 'Hier ist mein Bericht, Opulenz. Die Bediensteten stellen Fragen ber das Portalnetzwerk, aber nichts, mit dem ich nicht fertigwerden wrde.', 38134),
(193891, 'deDE', 'Ausgezeichnet. Nur weiter so.', 'Ausgezeichnet. Nur weiter so.', 38134),
(193890, 'deDE', 'Ich hatte die Ehre, eine kleine Rolle zu bernehmen.', 'Ich hatte die Ehre, eine kleine Rolle zu bernehmen.', 38134),
(193889, 'deDE', 'Obschon ich drauf wetten wrde, dass Ihr eine Rolle bei dieser Verbesserung gespielt habt, oder, Ta\'mari?', 'Obschon ich drauf wetten wrde, dass Ihr eine Rolle bei dieser Verbesserung gespielt habt, oder, Ta\'mari?', 38134),
(193888, 'deDE', 'Das sind ausgezeichnete Neuigkeiten. Richtet Euren Partnern meine Gre aus.', 'Das sind ausgezeichnete Neuigkeiten. Richtet Euren Partnern meine Gre aus.', 38134),
(193887, 'deDE', 'Hier ist mein Bericht, Opulenz. Wir haben es erneut geschafft, die Kapazitt des Portalnetzwerkes zu erhhen.', 'Hier ist mein Bericht, Opulenz. Wir haben es erneut geschafft, die Kapazitt des Portalnetzwerkes zu erhhen.', 38134),
(193886, 'deDE', '', 'Schwchlinge! Ihr knnt das Haus der Konstrukte nicht aufhalten!', 38134),
(193867, 'deDE', 'Ihr braucht nicht an uns zu zweifeln.', 'Ihr braucht nicht an uns zu zweifeln.', 38134),
(193866, 'deDE', 'Sorgt bitte einfach dafr, dass es aktiv bleibt. Wir werden es vielleicht kurzfristig brauchen.', 'Sorgt bitte einfach dafr, dass es aktiv bleibt. Wir werden es vielleicht kurzfristig brauchen.', 38134),
(193865, 'deDE', 'Natrlich. Es ist das verlsslichste Netzwerk aller Kartelle. Sehr diskret.', 'Natrlich. Es ist das verlsslichste Netzwerk aller Kartelle. Sehr diskret.', 38134),
(193864, 'deDE', 'Kmmert sich Euer Partner noch immer um die Wartung des Portals?', 'Kmmert sich Euer Partner noch immer um die Wartung des Portals?', 38134),
(193863, 'deDE', 'Seid gegrt. Ich hoffe doch, dass alles nach Plan verluft?', 'Seid gegrt. Ich hoffe doch, dass alles nach Plan verluft?', 38134),
(193862, 'deDE', 'Der Loa is\' irgendwo ber die Brcke. Wir beeil\'n uns besser.', '', 38134),
