INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `Text_lang`, `Text1_lang`, `VerifiedBuild`) VALUES
(191059, 'deDE', 'Legt Eure Eiskltefalle in den Ogerruinen aus. Das Tier, das unser Lager bedroht, sollte auftauchen.', '', 38134),
(191058, 'deDE', 'Das ist kein wildes Tier, sondern eins von denen, die bei uns an Bord waren! Es muss den Schiffbruch berlebt haben.', '', 38134),
(191057, 'deDE', 'Erwischt!', '', 38134),
(191056, 'deDE', 'Schaut, da ist es!', '', 38134),
(191038, 'deDE', 'Legt die Falle weiter entfernt vom Lager aus. Das Tier kommt nicht, wenn wir zu nah sind.', '', 38134),
(191037, 'deDE', 'Bringt mir die Vorrte. Ich zeige Euch, wie man eine Falle macht.', '', 38134),
(191035, 'deDE', '', 'Ein Eispartikel? Was wollt Ihr damit?', 38134),
(191031, 'deDE', '', 'Es greifen Euch jetzt zwei Gegner an. Gleicht Eure Chancen mit \'Verwandlung\' aus!', 38134),
(191030, 'deDE', '', 'Hab ich ja auch nicht! Das war $p.', 38134),
(191029, 'deDE', 'Aber Ihr wolltet mich doch nicht in Schaf verwandeln!', '', 38134),
(191028, 'deDE', '', 'Trefft ihn mit einem Angriff, um den Zauber zu brechen.', 38134),
(191027, 'deDE', '', 'Euer verwandelter Gegner wandert ein Weilchen herum. Nutzt das aus und bekmpft die brigen Widersacher.', 38134),
(191025, 'deDE', '', 'Sehr lustig, Rekrut. Ich denke, Ihr habt den Bogen raus. Gehen wir zurck.', 38134),
(191024, 'deDE', 'Bedenkt aber, dass ein Treffer beim Gegner den Zauber aufhebt. Probiert es bei Meredy aus.', '', 38134),
(191023, 'deDE', 'Oh, diese Taktik kenne ich! Whrend ein Gegner verwandelt ist, knnt Ihr Euch dem anderen widmen.', '', 38134),
(191021, 'deDE', '', 'Aber Cole! Das wrde ich doch nie tun. Ehrlich!', 38134),
(191020, 'deDE', 'Wollt Ihr... mich etwa auch in ein Schaf verwandeln?', '', 38134),
(191019, 'deDE', '', 'Cole, kommt her. Wir zeigen $p, wie man \'Verwandlung\' einsetzt.', 38134),
(191017, 'deDE', '', 'Ich habe jede Menge Ideen, wie man diese Oger beseitigen kann. Mal sehen, mit welchen wir nicht versehentlich die Insel in die Luft jagen...', 38134),
(191016, 'deDE', 'Manchmal wnschte ich, ich wre ein Gestaltwandler wie die Geister hier. Dann knnte ich in die Lfte aufsteigen wie der Gehrnte Jger! Keine Beute wrde meinem Blick entgehen!', '', 38134),
(191015, 'deDE', 'Der Gehrnte Jger ist ein beispielloser Fhrtenleser. Wenn er seine Beute nicht finden kann, kann das niemand.', '', 38134),
(191014, 'deDE', 'Sagt mir, wie verlief Eure Jagd? Habt Ihr und der Gehrnte Jger das Tier gettet, das er gesucht hat?', '', 38134),
(191013, 'deDE', 'Ich habe mich inzwischen von meinen Verletzungen erholt. Gut, dass uns der Gehrnte Jger gerettet hat, was?', '', 38134),
(191012, 'deDE', 'Htet Euch, diese Straen sind gefhrlich geworden. Etwas ist in Tirna Scithe passiert.', '', 38134),
(191011, 'deDE', 'Seid gegrt. Wie kann die Wilde Jagd behilflich sein?', '', 38134),
(191010, 'deDE', 'Die Jagd dient dem Ardenwald.', '', 38134),
(191007, 'deDE', 'Das wird die beste Feier, die Revendreth je erlebt hat!', '', 38134),
(191006, 'deDE', '', 'Euer Gegner wandert als Schaf herum, bis Ihr ihn angreift. Etwa so.', 38134),
(191005, 'deDE', '', 'Ist doch nur eine Demonstration! \'Verwandlung\' kann im Kampf enorm helfen.', 38134),
(191001, 'deDE', '', 'Meredy, jetzt ist nicht die Zeit fr Streiche!', 38134),
(190998, 'deDE', 'Ich freue mich schon auf den nchsten Gluthof. Die Leute sagen, das knnte der beste aller Zeiten werden!', '', 38134),
(190997, 'deDE', 'Vorsicht, Jger! Dieser Weg ist nicht mehr sicher.', '', 38134),
(190996, 'deDE', '', 'Eine Kostprobe \'Verwandlung\'!', 38134),
(190995, 'deDE', 'Angstschatten ist das Lieblingsreagenz von Markgrfin Stradama. Trefft mich im Labor, sobald Ihr es habt!', '', 38134),
(190994, 'deDE', 'Die Mutigen... die Mchtigen... die Verrter. Alle sind hier gefangen. Befreit sie.', '', 38134),
(190992, 'deDE', 'Ein verlorener Fae in der Nhe, in Qualen gefangen. Befreit ihn... bevor ihn die Dunkelheit verschlingt.', '', 38134),
(190990, 'deDE', 'Selbst eine Ernterin des Stolzes sollte hier nicht verkmmern. Rettet die Venthyr vor diesem Schicksal.', '', 38134),
(190989, 'deDE', 'Nun, da die Vorkmpfe vorbei sind, kann die wahre Prfung beginnen!', '', 38134),
(190987, 'deDE', 'Wie unerwartet! Es scheint, unsere Welpen knnen doch beien!', '', 38134),
(190986, 'deDE', '', 'Tief unter dem Meer bekmpfte $p die herrische Knigin Azshara.', 38134),
(190985, 'deDE', '', 'Hoffentlich hat mein Zauberbuch im See nicht zu sehr gelitten... Vielleicht htte ich mal selbst danach sehen sollen.', 38134),
(190984, 'deDE', '', 'Es ist vollbracht. Mge die Kraft Eurer Seelen Euch gut dienen.', 38134),
(190983, 'deDE', '', 'Ich werde zu Kapitnin Garrick zurckkehren. Folgt mir doch in Eurer neuen Tiergestalt.', 38134),
(190981, 'deDE', 'Wie dem auch sei... Es wird sicher unterhaltsam. Bringt die Herausforderer!', '', 38134),
(190980, 'deDE', 'Anscheinend denken diese Emporkmmlinge, sie knnten es mit den besten Kmpfern von Maldraxxus aufnehmen! Ha! Wohl kaum!', '', 38134),
(190979, 'deDE', 'Willkommen im Theater der Schmerzen!', '', 38134),
(190978, 'deDE', '', 'Nutzt Eure gestrkte Verbindung zur Natur. Schtzt die Wildnis und bewahrt das Gleichgewicht.', 38134),
(190976, 'deDE', '', 'Die Konstrukte fleddern immer noch die Leiche unseres gefallenen Hauses. Ttet sie und rettet so viel Ausrstung, wie Ihr knnt.', 38134),
(190975, 'deDE', 'So... darf es... nicht enden...', 'So... darf es... nicht enden...', 38134),
(190974, 'deDE', 'Ein Kind der Bastion... das dieses Schicksal nicht verdient. Fhrt es ins Licht.', '', 38134),
(190972, 'deDE', 'Zu... frh...', 'Zu... frh...', 38134),
(190970, 'deDE', 'Ihr habt... betrogen...', 'Ihr habt... betrogen...', 38134),
(190968, 'deDE', 'So... knapp...', 'So... knapp...', 38134),
(190967, 'deDE', 'Nutzlose... Knochen...', 'Nutzlose... Knochen...', 38134),
(190966, 'deDE', 'Noch... nicht...', 'Noch... nicht...', 38134),
(190965, 'deDE', 'Das war... doch erst... der Anfang...', 'Das war... doch erst... der Anfang...', 38134),
(190964, 'deDE', 'Das berlebt Ihr nicht!', 'Das berlebt Ihr nicht!', 38134),
(190963, 'deDE', 'Das ist Euer Ende!', 'Das ist Euer Ende!', 38134),
(190962, 'deDE', '', 'Gemeinsam vernichten wir die Verrter, die Maldraxxus beschmutzen.', 38134),
(190960, 'deDE', 'Zeit, meinen Ruhm zu mehren!', 'Zeit, meinen Ruhm zu mehren!', 38134),
(190959, 'deDE', 'Meine Legende beginnt!', 'Meine Legende beginnt!', 38134),
(190958, 'deDE', 'Das hier wird mir Ruhm einbringen.', 'Das hier wird mir Ruhm einbringen.', 38134),
(190957, 'deDE', 'Ihr helft mir die Leiter hinauf!', 'Ihr helft mir die Leiter hinauf!', 38134),
(190956, 'deDE', 'Brechen wir ein paar Knochen!', 'Brechen wir ein paar Knochen!', 38134),
(190910, 'deDE', 'Sucht Ihr mich?', '', 38134),
(190907, 'deDE', '', 'Lauscht ihren Stimmen... Lernt ihre wertvollen Lektionen.', 38134),
