(194760, 'ptBR', '', 'Ol, criaturinha. O que voc tem a?', 38134),
(194759, 'ptBR', '', 'O broto se torna o tronco. O tronco sustenta o cu. O cu contm os brotos.', 38134),
(194758, 'ptBR', '', 'Tudo a seu devido tempo.', 38134),
(194757, 'ptBR', '', 'O que voc v ao redor  resultado de incontveis vida.$b$b ou no  uma beleza?', 38134),
(194749, 'ptBR', 'Ha! Foi bem feito! Agora saiam da minha floresta!', '', 38134),
(194748, 'ptBR', 'Vo aprender a respeitar!', '', 38134),
