INSERT INTO `lfg_dungeons_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2348, 'deDE', 'Der Groe Entwurf', 'Der Kerkermeister steht kurz vor dem endgltigen Sieg. Wenn er nicht aufgehalten wird, erliegt der gesamte Kosmos seiner Macht.', 45745),
(2347, 'deDE', 'Griff der Herrschaft', 'Zovaals mchtigste Anhnger haben Millionen von Zivilisationen zerstrt. Werden sie jetzt das Universum auslschen?', 45745),
(2346, 'deDE', 'Ephemere Ebenen', 'Die Essenz der Schpfung wird verschlungen. Wenn dem nicht Einhalt geboten wird, wird die Realitt sich auflsen.', 45745),
(2345, 'deDE', 'Grundstein der Schpfung', 'Lihuvim kmpft vergeblich gegen die Herrschaft an. Wenn er nicht gerettet wird, fllt die Macht des Hters dem Kerkermeister anheim.', 45745),
(2294, 'deDE', 'Der Groe Entwurf', 'Der Kerkermeister steht kurz vor dem endgltigen Sieg. Wenn er nicht aufgehalten wird, erliegt der gesamte Kosmos seiner Macht.', 45745),
(2293, 'deDE', 'Griff der Herrschaft', 'Zovaals mchtigste Anhnger haben Millionen von Zivilisationen zerstrt. Werden sie jetzt das Universum auslschen?', 45745),
(2344, 'deDE', 'Die Abrechnung', 'Auf der Turmspitze erwartet Euch Sylvanas Windlufer. Dort wird der entscheidende Kampf um das Schicksal des Kosmos ausgetragen werden.', 45745),
(2343, 'deDE', 'Fesseln des Schicksals', 'Der Seelenfluss endet an der Spitze des Turms. Vernichtet jene, die von ihm zehren, um die unzhligen Seelen zu befreien, denen das Leben nach dem Tode verwehrt wird.', 45745),
(2342, 'deDE', 'Die Dunkle Bastille', 'Die Schreie der Gequlten treiben die Armee des Kerkermeisters an. Bereitet ihrem Leiden ein Ende und beraubt den Schlund seiner wertvollsten Ressource.', 45745),
(2341, 'deDE', 'Frontkmpfer des Kerkermeisters', 'Die Frontkmpfer des Kerkermeisters stehen bereit, um jeden Angriff abzuwehren. Um in das Sanktum des Kerkermeisters zu gelangen, mssen ihre Linien durchbrochen werden.', 45745),
(2340, 'deDE', 'Eine Audienz mit der Arroganz', 'Denathrius erwartet die Helden ungeduldig in seinem Turm, um in einem letzten Aufeinandertreffen endgltig ber das Schicksal von Revendreth zu entscheiden.', 45745),
(2339, 'deDE', 'Blut aus Stein', 'Denathrius\' Generle der Steinlegion bereiten im Hinterhalt lauernd eine Falle vor, die die Rebellion ein fr alle Mal vernichten wird.', 45745),
(2338, 'deDE', 'Reliquiarium der Opulenz', 'Whrend die Aristokratie im Siegestaumel schwelgt, mssen Helden ihrer Feier ein Ende setzen und ihre Machtbasis vernichten.', 45745),
(2337, 'deDE', 'Die hungrigen Gewlbe', 'Anima fliet aus den Gewlben von Schloss Nathria wie Blut aus einer offenen Wunde. Dieser Aderlass muss gestoppt werden, bevor Revendreth noch vllig ausblutet.', 45745);
