INSERT INTO `journal_encounter_locale` (`ID`, `locale`, `Name_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2405, 'ruRU', 'Тред\'ова', 'Гормы гложут окраины Арденвельда, пожирая иссушенные останки мертвых существ, но тварь по имени Тред\'ова обнаружила нечто куда более ценное: дикое семя Лакали, лоа знаний. Попробовав на вкус силу и мудрость лоа, Тред\'ова возжелала большего, чем просто выживание.', 54673),
(596, 'ruRU', 'Гал\'дара', 'Драккари обратились к лоа, когда силы Плети напали на Зул\'Драк. Ледяные тролли пришли к ним не с мольбами, а с мечами. Когда кровь убитого бога, Акали, обагрила землю, верховный пророк Гал\'дара и его сподвижники-предатели впитали силу лоа.', 54673),
(594, 'ruRU', 'Мураби', 'Драккари хотели принести в жертву Мам\'тота, чтобы получить его силу, но лоа уничтожил себя сам. Несмотря на это, Мураби, несомненно, обладает силой богов; верховный пророк выпил крови лоа, и теперь он готов пойти на все, чтобы Зул\'Драк оставался в руках троллей.', 54673);
