INSERT INTO `mount_locale` (`ID`, `locale`, `Name_lang`, `SourceText_lang`, `Description_lang`, `VerifiedBuild`) VALUES
(2201, 'ruRU', 'Боевой волк часового', 'Торговая лавка', 'Трофей, добытый в ходе налета часовых. Волки покоряются лишь сильнейшим.', 54673),
(2080, 'ruRU', 'Красный ездовой козленок', '|cFFFFD200Событие:|r Mists of Pandaria: Remix', 'Этот козленок компенсирует свой небольшой размер на редкость упрямым нравом.', 54673),
(2078, 'ruRU', 'Снежный ездовой козел', '|cFFFFD200Событие:|r WoW Remix: Mists of Pandaria', 'Горные козы с вершины Кунь-Лай славятся тем, что поднимаются даже на самые высокие скалы в поисках бобов кафа и отложений соли.', 54673),
(1468, 'ruRU', 'Янтарная стрекотуха', '|cFFFFD200Торговая лавка|r|n', 'Этот вид стрекотух, подаренный торговой лавке дедушкой Баки, отличается невероятной покладистостью.', 54673),
(2145, 'ruRU', '[PH] Goblin Surfboard - Blue', 'Торговая лавка', '', 54673),
(2039, 'ruRU', 'Свирепая синяя боевая черепаха', '|cFFFFD200Торговая лавка|r', 'Не нужно гоняться за противниками до посинения, когда есть пушки. От ядер еще никто не убегал.', 54673),
(1841, 'ruRU', 'Багровый мерцающий лисохвост', '|cFFFFD200Торговая лавка|r', 'Согласно гилнеасской легенде, эта красно-белая лиса проходит через Зимний Покров только в определенное время года.', 54673),
(1785, 'ruRU', 'Древний копытень', '|cFFFFD200Торговая лавка|r|n', 'Этот копытень повидал все за свою долгую жизнь. Век за веком он терпеливо обучал своих всадников верховой езде.', 54673),
(1742, 'ruRU', 'Кристаллический скорпион Скверны', '|cFFFFD200Торговая лавка|r', 'Один из детенышей Скорпирона, над которым проводили эксперименты по скрещиванию со Скверной.', 54673),
(1582, 'ruRU', 'Свирепая зеленая боевая черепаха', '|cFFFFD200Торговая лавка|r', 'Черепаха слишком медленная, чтобы догнать вашего врага? Поставьте на нее пушку. От ядра еще никто не убегал!', 54673);
