UPDATE `locales_quest` SET `OfferRewardText_loc8`='Очень впечатляет, $N... коготь Острокогтя непросто добыть! Тебе везет в охоте!\n\nОстрый Удар давно донимал батраков с лесозаготовок, когда они пытались добраться сюда, на заставу Расщепленного Дерева. Несомненно, скоро разойдется весть, что именно ты прикончил чудовище, и множество песен в твою честь будет распеваться у костров и на лесопильнях по всему Ясеневому лесу!', `RequestItemsText_loc8`='Да, могучий |3-6($C), я почувствовала твое приближение. Уверена, тебе есть что еще рассказать мне о твоей охоте.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=2;
