UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ха! Ты его $Gприкончил:прикончила;! Ты $Gоказал:оказала; большую услугу Элвину и заслуживаешь хорошей награды!', `RequestItemsText_loc8`='Ты $Gнашел:нашла; шайку Гаррика? Неужто мы, наконец, избавились от этого бандита?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=6;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Славная работа. Эти кобольды – воры и трусы, но когда их много, они становятся опасны. А людям Штормграда лишняя угроза не нужна.\n\nПримите мою благодарность за хорошую работу.', `RequestItemsText_loc8`='Ну, $gдобыл:добыла; мне банданы?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=7;
