UPDATE `locales_quest` SET `OfferRewardText_loc8`='Знаю, $N, Сталван мертв. Я ощутила волну надежды, пробежавшую по оскверненному лесу. Сумеречный лес раз и навсегда освободился от кровожадной твари. Мою радость омрачает лишь мысль о тех, кто пал жертвой этого чудовища. А ты отважен и хитер, $C.', `RequestItemsText_loc8`='Да, $N?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=98;
