UPDATE `locales_quest` SET `OfferRewardText_loc8`='У меня просто сердце разрывается, как подумаю об этих бедных детках, Мейбелл и Томми Джо. Ах, молодость... Весна... Любовь...\n\nДумаю, я смогу им чем-нибудь помочь. Погоди-ка, дай подумать...', `RequestItemsText_loc8`='', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=107;
