UPDATE `locales_quest` SET `OfferRewardText_loc8`='Спасибо, ты здорово мне $Gпомог:помогла;.$B$BЧто это? Аргус приложил к ящику записку... Что?! Аргус хочет ПЛАТЫ? Пф!$B$BЛадно, $N, спасибо за помощь.', `RequestItemsText_loc8`='О, да ты $Gвернулся:вернулась;! Удалось достать подковы?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=119;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вольно, |3-6($c).$b$bЧиновник Соломон – благородный человек. Его слова много для меня значат. Я поговорю с королем и объясню ему ситуацию. Заверьте доброго судью, что он получит подержку армии Штормграда. Я вышлю подкрепления, как только получу приказ Его Величества.', `RequestItemsText_loc8`='С чем $Gпришел:пришла;?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=120;
