UPDATE `locales_quest` SET `OfferRewardText_loc8`='Хм, жалкой засаде Черного Железа не помешать мне доставить товар!$b$bНо Сайан... Никогда бы не подумал, что он сторонник этих предателей. Я с ним почти год работаю... должно быть, что-то упустил.$b$bЛадно, подумаю об этом позже.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=273;
