UPDATE `locales_quest` SET `OfferRewardText_loc8`='Теперь наши земли спасены! Признаться, раньше я с недоверием относился к вашему брату... но твой подвиг, |3-6($r), заставил меня задуматься. Спасибо тебе!', `RequestItemsText_loc8`='Ты еще не $Gвыполнил:выполнила; мое задание, |3-6($c).', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=276;
