UPDATE `locales_quest` SET `OfferRewardText_loc8`='Отлично, $N!$B$BВот твои деньги, ты честно их $gзаработал:заработала;, выполнив все обязательства.', `RequestItemsText_loc8`='Наши купцы смогут почувствовать себя в безопасности, только когда мы избавимся от Жруна и его сородичей.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=279;
