UPDATE `locales_quest` SET `OfferRewardText_loc8`='Чего тебе надо? Ты, может, не замечаешь, но мы здесь немного заняты – и укрепления строим, и Приозерье латаем после нападения орков. Если тебе сдалась ретбанская руда, добудь ее $Gсам:сама;!', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=345;
