UPDATE `locales_quest` SET `OfferRewardText_loc8`='Опять Шоу?! Совести у него нет!..$B$BБратство Справедливости, говорите? Да вы просто мешок с хорошими новостями! Скажите еще, что Смертокрыл воскрес и напал на город...$B$BНу, ладно, выкладывайте все по порядку. И не пропускайте ни единой детали. Я хочу знать все.', `RequestItemsText_loc8`=NULL, `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=350;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Вы помещаете яйцо... аварийный маяк... внутрь цыпленка... робота.$B$BИзнутри робота доносится жужжание и скрип – он пытается встреть. Когда робот оживает, изнутри доносится голос Карлса:$B$B\"По крайней мере, КПХ-17/TN работает, но нуждается в серьезном ремонте! Он слишком тяжелый, чтобы унести отсюда... впрочем, у меня есть идея. Не хотите присмотреть за ним?\"', `RequestItemsText_loc8`='Похоже, вы нашли обломки... механического цыпленка? Из яйца снова доносится голос:$B$B\"Да, отличная работа! Это в самом деле мой автономный робот, хотя судя по сенсорам, он нуждается в хорошей починке перед перелетом в Пиратскую бухту. Для начала поместите маяк внутрь аппарата. А остальное он сделает сам!\"', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=351;
