UPDATE `locales_quest` SET `OfferRewardText_loc8`='Моя книга?! Так это ты украл ее!\n\nНо... что это за новые чары на ней... Бетор? Он в Лордероне?\n\nЕсли он все еще \"жив\", то, может быть, ему тоже удалось вырваться из холодных объятий Короля-лича...', `RequestItemsText_loc8`='Твои хозяева глупцы! Незачем было подсылать тебя. Я никогда не стану рабом Плети!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=366;
