UPDATE `locales_quest` SET `OfferRewardText_loc8`='Да, этот яд великолепно подойдет, $N! Я уже добавил все остальное в мой состав и вскипятил его. Наконец-то я испытаю эту смертельно опасную смесь!', `RequestItemsText_loc8`='Вы уже нашли яд злобного ночного кругопряда, $N? Это последний компонент, необходимый мне для испытания моего нового состава.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=369;
