UPDATE `locales_quest` SET `OfferRewardText_loc8`='Сварено пиво, разлито по кружкам,$bЭй, не деритесь, друзья и подружки!$bКаждому выделим по половине$bВкуснейшей порции ребрышек в пиве!', `RequestItemsText_loc8`='Мне нужно шесть ребер скального вепря и кружка \"Болтливого пива\", $N.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=384;
