UPDATE `locales_quest` SET `OfferRewardText_loc8`='Благодарю вас, $n! Совершенно бесполезная вещь, не считая воспоминаний... Нет слов, как я благодарен за то, что принесли мне этот предмет; вам, конечно, трудно понять цену памяти о Западном Крае. Возьмите это в знак моей признательности...', `RequestItemsText_loc8`='$n! Вы нашли компас?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=399;
