UPDATE `locales_quest` SET `OfferRewardText_loc8`='А, так тебя прислал мировой судья Севрен. Тогда да, дело у нас с тобой очень важное.\n\nУверен, Севрен выбрал того, кто справится с моим заданием, но… ладно, посмотрим.', `RequestItemsText_loc8`='Здравствуй. Либо ты не знаешь, кто я, либо у тебя ко мне очень срочное дело.\n\nПотому что иначе ты не посмел бы меня беспокоить…', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=405;
