UPDATE `locales_quest` SET `OfferRewardText_loc8`='Благословенный Свет! Наконец-то еда! Сладенькая тыквочка...', `RequestItemsText_loc8`='Прочь, нечестивое созданье! Свет, защити меня! Алый орден избавит Азерот от таких...\n\nОй... Это что, еда для меня? Я умираю с голода...', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=407;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Твои действия нанесли Плети ощутимый удар! И твоя победа не будет забыта ни Отрекшимися, ни врагами.\n\nПродолжай бороться вместе с нами, $N, и когда-нибудь мы изгоним Короля-лича из Азерота!', `RequestItemsText_loc8`='Мы не можем позволить себе прохлаждаться, $N. С каждым часом кольцо Плети вокруг Тирисфальских лесов сжимается все сильнее.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=408;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Невероятно! Ты убил Лилит Нефару и тем самым доказал, что являешься врагом Плети!\n\nЯ думал, что я – единственный из нежити, обладающий свободой воли, но теперь вижу, что есть и другие, кто не поддался влиянию Короля-лича.', `RequestItemsText_loc8`='Старый, побитый, окровавленный стол.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=409;
