UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ты убедил его присоединиться к нам! Это огромная радость для всех Отрекшихся, $N. Я доложу об этом Темной Госпоже.', `RequestItemsText_loc8`='Ты вернул книгу Гюнтеру? Что он сказал?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=411;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Эти гиромеханические шестерни и зубцы рестабилизации прекрасно подходят к чертежу Оззи для устройства Перенаправления. Как только я залью гидросмазку во внутренние клапаны сгорания, подкручу кривохраповик и повышу вязкость электрогуммигеля, раса гномов будет возрождена.', `RequestItemsText_loc8`='Перенаправлятор будет закончен и введен в эксплуатацию, как только у нас будет достаточно зубцов рестабилизации и гиромеханических шестерней.', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=412;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Горный пехотинец Ячменовар пробует \"Мерцающий портер\"... $B$BОх-х. Аж искры из глаз! Кажется, я даже в темноте видеть могу!', `RequestItemsText_loc8`='Привет, |3-6($c)! Что за дело привело тебя сюда?$B$BНадеюсь, что-нибудь стоящее. Я уже много дней не дрался хорошенько, да и этот бочонок эля уже почти пуст...', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=413;
UPDATE `locales_quest` SET `OfferRewardText_loc8`='Ну-с, попробуем...$B$BКлянусь бородой Магни! Аж голова кругом пошла! И в теле легкость необыкновенная.$B$BСейчас полечу, как птичка!', `RequestItemsText_loc8`='Привет, |3-6($c), добро пожаловать в Телcамар!', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=414;
