UPDATE `locales_quest` SET `OfferRewardText_loc8`='Карты и эта... эта подвеска? Интересно. \n\nОчевидно, что волшебники Даларана почему-то интересуются Янтарной мельницей, но их цель по-прежнему остается неизвестной. \n\nМожет быть, Далар сможет выяснить, что они там ищут.', `RequestItemsText_loc8`='Узнали что-нибудь полезное, $N?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=478;
