UPDATE `locales_quest` SET `OfferRewardText_loc8`='У меня даже голова закружилась от возбуждения! Какой эксперимент! Ты мне очень помог, $N. Мы будем сеять болезнь и смерть по всем этим землям!', `RequestItemsText_loc8`='Я рад, что ты вернулся, $N. Я уж совсем заскучал. Ни единого развлечения, сижу, травлю и заражаю Ампи, а потом воскрешаю ее. И так целый день.\n\nКак прошло путешествие в Подгород? Тебе удалось зарубить по дороге человечка-другого?', `ObjectiveText1_loc8`=NULL, `ObjectiveText2_loc8`=NULL, `ObjectiveText3_loc8`=NULL, `ObjectiveText4_loc8`=NULL WHERE `Id`=515;
